insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(949289787,'吃粥啦','各位同学大家好，我是一位刚刚毕业的学生，我没有选择一份安逸的工作，而是折腾创业！当初在校期间，与交大学长戴文博（之前宅米的联合创始人）一起做快来学车，交大也有很多朋友在我这里报名学车都是我负责安排的……大家如果有想要学车的我可以帮你们介绍给驾校老板，有问题可以来我店里问我，因为之前和驾校老板交情很好！我现在创业资金百分之八十是出自于我父母之手，所以我要把这个店做起来，毕竟爸妈的工作都是很辛苦的，我现在拿着他们的血汗钱出来创业，实属于心不忍！现在网站只上了“饿了么”，因为上平台需要满减和给平台费用，自己差不多只能保本有时候还亏钱！想让更多的人知道我的店，需要充钱买排名，所以我也是无奈之举才会来这里请大家需要吃粥的时候可以选择“兰亭粥铺”下单，我保证味道不会比那些品牌的差，而且卫生方面我敢保证我是最好的，因为我也是刚毕业出来，自身有洁癖，深知一次的服务不好，可能会导致永久的失去这位顾客！大家以后若有什么问题可以直接打我电话：13120626133（微信同号）故此在贵校思源门旁边开粥铺！还望大家能够多多支持！','2018-04-05 13:29','2018-08-08 13:29','东川路859号兰亭粥铺','https://tongqu.me/upload/acts/20180324/small_574943d7ccb33fe3b1641e101980a0a1.png','同去网','2018-04-05 13:29:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-75005801,'“慧谷”创新创业训练营','一大波梦想正在砸来！方位：“慧谷”创新创业训练营时间：4月15日起创新与创业，需要的不仅是憧憬与激情，还需要迈出坚定的第一步；也需要同行者为你鼓劲加油；更需要不断完善的商业逻辑与技能！“慧谷”创新创业训练营，为交大创新创业者而生！探索创新基因，开启创业思维；做追逐梦想的实干派！我们是谁？上海交通大学创业训练营始办于2015年，已培训了138支团队440多名交大创新创业者。2017年创业学院携手国家大学科技园打造了全新的“慧谷”创新创业训练营，以“创新驱动创业”为主题，形成了以“讲座论坛+实践与孵化、集中授课+精准辅导”为特色的全新训练体系。2018年在广泛汲取学员反馈的基础上锐意改进，将打造更具内涵的课程内容及更加精细的辅导体系，还将引入丰富有趣的创业游戏、团队内建、外出考察交流等学习内容。现火热开启报名，欢迎勇于开创的你！2017年“慧谷”创新创业训练营开营仪式你能收获什么？一、内容丰富充实的课程培训在创新创业训练营里，不仅可以听到各路大咖分享的创业经验，还可以通过初阶课程学习到商业模式、商业计划书、项目路演、大学生创业扶持政策等方面的基本知识，通过进阶课程进行营销策略、法务财务、设计思维等方面专题研讨。除此之外，我们还开展投资人茶会，为大家介绍投资、融资方面的实战经验！2017年训练营主题讲座2017年训练营专题研讨二、导师一对一精准辅导我们有强大的创业导师及创投导师团队，给各个创业团队提供一对一精准辅导。在这里你可以接触到各个领域的大佬们，一起交流和沟通。导师也会针对具体的创业项目进行个性化指导，提供建设性建议和帮助！2017年训练营一对一导师辅导三、有趣丰富的考察活动和团队游戏本期创新创业训练营将组织团队到嘉兴参加户外活动，让大家在团队内拓的同时学习创业知识。同时我们还特别引入了团队游戏环节，旨在增强团队协作能力及商业能力，加强队员之间的友谊！2017年训练营赴常熟考察交流四、项目路演机会我们为各个团队提供模拟商业路演机会，团队成员可展示自己的项目并得到导师们宝贵的建议，从而为项目的发展方向有更清晰的认识。同时，还为部分团队推荐商业路演，对接投资人。这是展示和提升自己的宝贵机会哦！五、项目展示和后期支持','2018-04-15 00:00','2018-05-23 00:00','上海','https://tongqu.me/upload/acts/20180324/small_39858f2c9a75231e4f0698eaebc64efe.jpg','同去网','2018-04-15 00:00:00.0');
