insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(519686804,'本科学术系列讲座（十三）','本科学术系列讲座是我院本科生的必修课程“专业学术报告”的组成部分之一，它是将经济管理领域的理论知识和工具与社会实践特别是经济管理领域的热点问题相结合的学术导向课程。通过本课程的学习，促使学生熟悉经济管理领域的相关问题或关注要点，了解问题和热点背后的知识基础和作用机制，提升学生观察问题、思考问题、提出应对问题的基本学术素养，为未来从事科研和实际经济管理领域相关的工作打下扎实的基础。讲座嘉宾将结合当前的经济管理热点或学术前沿，邀请学院教师安排系列讲座，每次2小时；讲座将分经济和管理两个专业组织的专场学术讲座。学生参加不少于16次报告或讲座，并每次撰写报告听后感，每篇听后感不低于1000字。本科学术系列讲座（十三）邀请嘉宾：陈德智 老师讲座题目：不战而胜：孙子兵法战略讲座时间：2018年3月28日（周三），下午13:30-15:30（含提问时间）讲座地点：闵行校区，陈瑞球楼100号备    注：2016级、2017级安泰经管学院本科生可作为必修课程《专业学术报告》1次。嘉宾简介：陈德智，上海交通大学安泰经济与管理学院创新与战略系副教授、博士生导师，主要研究方向：孙子兵法战略，战略管理，技术战略与研发管理。迄今在国际期刊International Journal of Operations and Production Management，Journal of Engineering and Technology Management，International Journal of Production Economics和Sustainability及国内重要期刊《管理工程学报》、《中国管理科学》等发表学术论文90余篇，出版《创业管理》、《技术跨越》、《战略管理精品案例》、《管理案例编写与教学》等专著；在毅伟案例中心（IVEY Publishing）发表教学案例3篇，编写中文教学案例100多篇；主持国家自然科学基金3项，上海市软科学重点项目等5项。','2018-03-28 13:30','2018-03-28 15:30','闵行校区，陈瑞球楼100号','https://tongqu.me/upload/acts/20180321/small_466028c9b8397380afb7ad68f1559444.JPG','同去网','2018-03-28 13:30:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1436539636,'上海交通大学通识教育“名家讲堂”第70讲----审美有无用处：德国思想家的理解','上海交通大学通识教育“名家讲堂”第70讲审美有无用处：德国思想家的理解主讲嘉宾：Matthias Freise主办单位：上海交通大学文科与通识教育委员会  教务处  人文艺术研究院讲座时间：2018年3月21日（周三）14：00—16：00讲座地点：闵行校区  东中院1-204主讲嘉宾简介：Matthias Freise，哥廷根大学斯拉夫语言文学及比较文学教授，俄国19世纪经典文学的权威专家，世界文学与比较文学领域的知名学者，致力于推动不同文学与文化间的对话与理解。出版著作11部，发表学术论文200余篇，另主编过10部学术文集。教学及研究方向覆盖东欧各国文化、文学及文艺理论。在文艺理论领域，他是首位挑战时下流行的“数字人文”潮流并大力主张“对话人文”的学者。报告内容简介：在工具理性主导的现代社会里，对美学功能的传统认知面临着巨大挑战，然而如果没有了艺术，人类心灵的成熟、社会机能的运作将受到威胁。本讲座从康德、荷尔德林、陀斯陀耶夫斯基、海德格尔等人的美学理论出发，并评讲其局限性。将以叙事作品中的视角为例，阐释艺术的功能，并以电影《巴尔扎克和小裁缝》为例，论述非审美体验视角所造成的后果。','2018-03-21 14:00','2018-03-21 16:00','闵行校区  东中院1-204','https://tongqu.me/upload/acts/20180321/small_8ce0d857d27dc78df54556f1110c5e3e.png','同去网','2018-03-21 14:00:00.0');
