insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1178827386,'励志讲坛第222期 | 挥笔纵横 着墨四海','主题：挥笔纵横 着墨四海嘉宾：周斌（上海交通大学文创学院教授）时间：2018.3.28（星期三）  13:45-15:30地点：上海交通大学（闵行校区）图书信息楼九楼报告厅嘉宾介绍：周斌，上海交通大学文创学院教授，中国书法文化国际传播研究所所长。他曾担任联合国前秘书长潘基文的书法老师，致力于推动书法文化的国际传播，以“笔尖上的文化”在国际舞台展示中国书法艺术，搭建起文化沟通的桥梁。他恪守师道、不忘本心，同时又有着开阔的眼界与胸襟，承担起书法文化传承之重任。他笔下的书法，非诗却具玲珑诗心，非画然有灵动画意。草书行云流水，峻拔纵逸；楷书刚强浑厚，端正挺拔。看似点横撇捺，实则精深广大，它包罗万象、博取百家。一人，一杆笔，一砚台，一众学子，无论他身处何方，展纸挥毫间所传授的都不仅仅是笔墨技法，更是蕴含其中的中华文化。三尺讲台前，他匠心独运，润物无声。一直以来，他不仅教授一撇一捺中的“气与韵”“形与神”、章法中的疏与密，更将汉字中所蕴含的文化娓娓道来，让所有学生深感中国文化的博大精深。国际交流中，他突破隔阂，着墨四海。在纽约大学等多所国际高校开设的书法课程中，他不断挖掘汉字的艺术特征与节奏美感，让语言的隔阂不再成为文化沟通的障碍，为书法的国际交流提供了崭新的视角，推动中国文化以更加开放的方式走向世界。文化传承上，他革故鼎新，与时俱进。作为书法心理学领域重要学者之一，他不懈探索书法对于现代社会及现代人思维方式的价值，从“心境平和”、“社会和谐”和“世界和平”三个维度来诠释中国书法文化的“和”的理念。展纸挥毫，笔走蛟龙。纵横捭阖辟新径，酣畅淋漓写人生。他所呈现的书法艺术，是点与线的巧妙组合，更代表着国际舞台上的中国新形象。3月28日，上海交通大学（闵行校区）图书信息楼九楼报告厅，与你一起挥笔纵横！用心灵感动心灵，以生命影响生命！','2018-03-28 13:45','2018-03-28 15:30','上海交通大学（闵行校区）  图书信息楼九楼报告厅','https://tongqu.me/upload/acts/20180320/small_3dacd8b6d1a6faad1cd672a67c5e2231.jpg','同去网','2018-03-28 13:45:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1163317839,'电院学业交流分享会','电院学业交流分享会学在交大17级电院萌新们~如何做好高中到大学的转换？怎样找到一套适合自己的学习方法？高数大物太“迷”人？课余时间不知道干啥？那就快来电院学业交流分享会吧！这里有国奖同学分享学习经验，介绍高数、大雾学习方法；还有惊喜学习资料以及1161项满分素拓一次哦！你还在等什么！ 我们周三不见不散！   #本次分享会只面向电院17级本科生#活动简介       本次活动旨在落实“学在交大”的理念，构建一个电院同学与国奖同学交流的平台，解答同学们在学习中遇到的各种问题。通过国奖同学的分享，帮助同学规避学习中误区，找到各学科最有效的学习方法和考试技巧，帮助同学们共同进步。本期嘉宾    方荣耀       2016级信息工程专业。连续两学期综合测评排名位列全专业第一，其中第二学期综合测评97.6。       曾获“行万里路，知中国情”寒假社会实践先进个人、校级优秀团员，校三好学生等荣誉。       日常爱运动，最近沉迷吉他和钢琴。             多读书、多看报，做一个不只会学习的学霸。   王鑫伟       2016级软件工程专业。本科一年级国家奖学金获得者，连续两学期综合测评位列全专业第一。       曾带领团队获2017暑期社会实践校级三等奖，曾获社会实践校级优秀个人、三好学生、校级优秀团员等荣誉。       爱本格推理，爱日韩动漫，       更爱戴上耳机去听那世界万千。时间地点 时间：第四周周三（3月21日）13:30 地点：微电子报告厅 【请参加分享会的同学提前15分钟进场】','2018-03-21 13:24','2018-03-21 15:30','上海交通大学闵行校区 微电子报告厅','https://tongqu.me/upload/acts/20180319/small_b32b55b367e3e225337ad762fb3ac115.jpg','同去网','2018-03-21 13:24:00.0');
