insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1603905211,'03月30日晚-04月01日春季赏花之旅-徒步婺源五岭古道，观篁岭梯田','活动须知目的地简介：婺源：为江西上饶市下属县城，位于江西省东北部、上饶市北部，是古徽州六县之一，也是徽州文化的发祥地之一。婺源篁（huáng）岭景区，地处石耳山脉，面积15平方公里，由索道空中揽胜、村落晒秋访古、梯田花海寻芳及乡风民俗拾趣等游览区域组合而成。 篁岭属典型山居村落，民居围绕水口呈扇形梯状错落排布。篁岭因”晒秋”闻名遐迩，村落“天街”似玉带将经典古建串接，徽式商铺林立，前店后坊，一幅流动的缩写版“清明上河图”。 周边千棵古树环抱、万亩梯田簇拥，四季花海展示惊艳的“大地艺术”。 独特的旅游体验带您走过索桥、滑过溜索、乘上高空索道，俯瞰独特的晒秋奇观。活动详情','2018-03-30 19:10','2018-04-01 19:10','上海博物馆集合出发','https://tongqu.me/upload/acts/20180313/small_6dbf65b795e9462e257ea0e6bd869c14.jpg','同去网','2018-03-30 19:10:00.0');
