insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1929268605,'03.23晚-03.25, 石潭观花海, 船游新安江, 尝千岛湖大鱼头','活动须知新安江发源于安徽黄山，经淳安县，流至建德市；江水再往东流，经桐庐，流入富阳市境，曰富春江；再往东，到了萧山区的闻家堰，称钱塘江。富春江-新安江-千岛湖风景区就位于上起淳安，下至富阳的一段区域内，是浙江省的一个重要风景区千岛湖即新安江水库。这座大型水库，不但气魄雄伟，而且景色壮观秀丽。水库内岛屿星罗棋布，常见的岛屿有398个，还有数百个小岛随着库水的涨落而时浮时沉，因此人们称它为“千岛湖”。从石潭村越往里走，风景越好，也越有野趣。村与村之间仅靠一条羊肠小道相连。当春季来临时，漫山遍野的油菜花、梨花，盛开地如火如荼，那才是真正的世外桃源啊！而且由于交通不便，游人罕至，是摄影、写生的好地方。活动详情','2018-03-23 19:00','2018-03-25 19:00','上海博物馆','https://tongqu.me/upload/acts/20180308/small_9079a282899abc0524b59c1d5f469a47.jpg','同去网','2018-03-23 19:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2095172952,'【大师讲坛第096期】A Career in Thermodynamics—Past, Present and Future','报告主题：A Career in Thermodynamics—Past, Present and Future报告嘉宾：Alexandra NavrotskyMember of the National Academy of Sciences,USAMember of World Academy of CeramicsMember of American Philosophical Society报告时间：2018年3月12日（周一）13:30—15:00报告地点：上海交通大学闵行校区图书信息楼八楼报告厅嘉宾介绍：世界陶瓷科学院院士，美国科学院及哲学院双院士Alexandra Navrotsky教授，是一位国际著名的热化学科学家,同时也是加州大学长滩理学院的院长。她的研究聚焦于将原子水平的结构和成键特性与矿物、陶瓷和其他复合材料的宏观热力学行为联系起来，通过将高低温反应量热法作为一项基础性研究工具，她为从矿物热力学到陶瓷加工到沸石的广泛应用领域做出了巨大贡献。Alexandra Navrotsky教授孜孜不倦的研究在学术上收获颇丰，在Nature, Science等著名学术期刊上发表的超过880篇的论文使她声名斐然。在潜心学术的同时，她也为政府和学术界的众多部门机构提供咨询服务，促进学科和机构之间的合作研究。','2018-03-12 13:30','2018-03-12 15:00','图书信息楼八楼报告厅','https://tongqu.me/upload/acts/20180307/small_f809c25425d54622a6928f5e597d83be.jpg','同去网','2018-03-12 13:30:00.0');
