insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1298176238,'人工智能时代，精算师将何去何从','人工智能发展至今，已不再是一个概念，它正在改写我们的生活和工作。有研究显示，人工智能到2060年前后有50%的概率完全超过人类。一方面，人工智能正在催生新的商业机会；另一方面人们也在担心人工智能到底会如何颠覆其所在的行业。精算是综合运用数学，统计，风险和概率等知识的学科，主要应用于金融，证券，保险等领域。面对人工智能的迅速发展，精算师们也在积极探讨行业未来的发展。2018年3月28日，上海交大安泰经管学院、英国精算师协会和精算界的各位朋友将集聚一堂，共同探讨人工智能时代下的未来精算行业的变革和发展。此次峰会邀请了英国CASS商学院、全国重点金融精算院校负责人，金融、保险、证券和咨询企业等精算部负责人、政府金融机构参与。峰会将从学院洞见、行业视角、第三方观察等角度展开，旨在为金融企业和商学院搭建一个面对面合作交流的平台。时间：3月28日（周三）14:00 — 19:00地点：上海交大安泰经管学院A407室主办单位：   上海交通大学安泰经济与管理学院协办单位：   英国精算师协会；陆家嘴金融城人力资源管理联合会演讲主题1：人工智能——从现象到本质演讲人：沈惠璋   上海交大安泰经管学院信息管理与信息系统系主任演讲主题2：人工智能在医学辅助诊断和健康管理中应用演讲人：陈宏   复星集团总部——新技术与新经济产业集团投资总监演讲主题3：机器学习助力保险业智慧化变革演讲人：姚佶   安永(中国)企业咨询有限公司精算与保险咨询服务总监圆桌论坛：未来精算师的培养-Vali，英国CASS商学院精算专业教授-陈宏，复星集团总部——新技术与新经济产业集团投资总监-姚佶，安永（中国）企业咨询有限公司精算与保险咨询服务总监-顾颖，上海交大安泰经管学院国际项目总监、中英精算硕士项目负责人英国CASS商学院宣讲会主讲人： Vali Asimit，英国CASS商学院精算专业教授欢迎扫描下方二维码，进群了解更多详情！','2018-03-28 14:00','2018-03-28 19:00','上海交大安泰经管学院A407室','https://tongqu.me/upload/acts/20180327/small_1c84e3443cc47b4663261509c54a5868.jpg','同去网','2018-03-28 14:00:00.0');
