insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1562801977,'物联网前沿技术讲座','时间：2018年5月16日13:30 地点：上海交通大学闵行校区软件大楼演播厅 演讲主题：“TI物联网解决方案”，“不能错过的下一个浪潮——物联网”，“用AI打开的世界”，“未来,万物互联”，“AliOS Things物联网操作系统,开源、开放、赋能万物”，“物联网,开启万物互联的智能世界”，“一种技术无尽选择——Zigbee技术进展及在物联网中的应用”，“从后台到前端的通用物联网框架”，“二维码在物联网行业入口的应用”。 主讲人：TI，中国移动，百度，阿里，华为，Zigbee，Tridium和北京新大陆各路大咖齐聚一堂，与你一起分享“物联网”的故事。 说明：电院研究生记讲座素拓一次，电院本科生记1161项95分素拓 报名链接：https://tongqu.me/act/17310 访问数量：','2018-05-14 00:00','2018-05-14 00:00','','','学生办','2018-05-14 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1328780222,'浪潮，改变因你而来——“AI FUTURE”人工智能企业沙龙第四期','时间：5月15日 14:00-16：00 地点：电院3-306 讲座主题：高效能和深度定制的浪潮人工智能 演讲人：李锐  浪潮集团AI研究院首席专家 曾就职于德国西门子、阿里巴巴，现任浪潮集团AI研究院首席专家。 毕业于慕尼黑工业大学计算机学院，获博士学位。主要研究方向为机器学习、数据挖掘、文本挖掘，精通英语及德语。现任人工智能顶级会议AAAI2018审稿委员会委员 浪潮，作为中国领先的云计算大数据服务商中国服务器销量市场第一，全球前三浪潮云占区域政府70％，行业部委80％云专利与云标准数量均位于国内首位。现场可了解人工智能2019届实习生招聘情况。 讲座记本科生1161项目95分讲座素拓一次，研究生记讲座素拓一次 访问数量：','2018-05-12 00:00','2018-05-12 00:00','','','学生办','2018-05-12 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1046786049,'丝路多语言信息智能处理研究及展望','报告人：吾守尔·斯拉木，新疆大学信息科学与工程学院教授、博士生导师，中国工程院院士 报告：丝路多语言信息智能处理研究及展望 时间：2018年5月14日（周一）上午10:00-11:30 地点：电信学院3号楼208会议室 主持：谷大武，上海交通大学特聘教授，教育部长江学者 摘要： 一、新时代新需求、新机遇 二、智能信息处理发展趋势 三、丝路多语言信息智能处理拟解决的重大科技问题 四、国家反恐维稳迫切需要丝路智能交流平台 五、周边国家语言分类及智能交换 六、近期开展的工作 七、新疆多语种信息技术实验室简介   吾守尔·斯拉木教授简介： 吾守尔·斯拉木，新疆大学信息科学与工程学院教授、博士生导师，中国工程院院士。现任新疆多语种信息技术重点实验室、新疆民文信息技术研发中心、新疆多语种信息技术研究中心主任，中国中文信息学会原副理事长、新疆IT联盟名义主席，享受国务院特殊津贴专家、国家级突出贡献中青年专家、全国先进工作者、全国侨界十佳、新疆十佳科技人物等。 长期从事多语种信息处理研究与开发应用。带领多民族研发团队，主持承担国家863计划项目7项、973项目1项、国家基金重点项目2项、国家级和省部级项目40多项；主持研发应用30多种支持多语种的新系统新软件；主持制定国际标准5项、国家标准22项、编译著作（教材）9本、发表论文170余篇。荣获国家科技进步奖3项、自治区科技进步奖7项、何梁何利科技奖、光华科技二等奖等省部级以上奖励13项。新疆大学计算机科学与工程国家一流学科、211工程新疆地区信息化及关键技术学科带头人，牵头组建了计算机应用博士点、博士后流动站、4个硕士点、新疆多语种信息技术重点实验室、新疆民文信息技术研发中心、新疆多语种信息技术研究中心。 多次被江泽民、胡锦涛、李鹏、李岚清以及习近平、李克强、俞振声、刘延东等党和国家领导人接见和鼓励。2011年当选为中国工程院院士。 本次活动针对电院同学开放报名，电院研究生记一次讲座素拓。请报名的同学提前15分钟到场，并刷学生卡签到。 报名链接：https://tongqu.me/act/17273  访问数量：','2018-05-11 00:00','2018-05-11 00:00','','','学生办','2018-05-11 00:00:00.0');
