insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-298192003,'电院2018心理健康月开幕式暨《积极心理学与大学生幸福观》主题报告','各位同学，心理健康月首场活动将为大家带来《积极心理学与大学生幸福观》的主题报告。 积极心理学是研究人的优势与幸福的一门学科，可以帮助自我学会面对并改善诸如抑郁、焦虑、压力、拖延等常见不适情况，关于积极心理学的哈佛大学《幸福课》一经推出就受到学生和各界人士的热烈欢迎。报告具体信息如下： 时间：5月9日，13:30-15:30 地点：电信群楼3-100报告厅 本次活动计研究生讲座素拓一次，本科生记1161项满分素拓。欢迎同学积极报名。 报名链接：https://tongqu.me/act/17209 访问数量：','2018-05-07 00:00','2018-05-07 00:00','','','学生办','2018-05-07 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1885909705,'直击区块链，赋能新金融','本次讲座特邀深圳微众银行区块链首席架构师张开翔给大家分享区块链的核心原理和价值前景。 深圳前海微众银行股份有限公司（以下简称“微众银行”）是国内首家开业的民营银行和互联网银行，由腾讯牵头发起设立，现已推出微粒贷、微众银行APP、微车贷、微众有折、微动力等金融产品，并将人工智能技术、区块链技术、大数据技术、云计算技术等新兴金融科技运用在相关的金融产品及金融服务中。 微众银行秉承对等合作的分布式商业价值观，牵头成立了金融区块链合作联盟（深圳），并深度打造了完全开源的区块链平台BCOS和FISCO BCOS，实现了金融级的安全性、高性能、高可用性和监管支持等优点，目前已在真实生产环境中上线了区块链对账和区块链仲裁两个应用场景，持续以金融实践检验技术，并据此输出经验和制订标准。 微众银行作为互联网银行的开拓者，亦是金融科技的忠实践行者，在区块链领域有深入的探索和丰富的实践经验，并与金链盟开源工作组其他成员一起搭建了区块链底层开源平台FISCO BCOS。同时，还参与了多项区块链相关的国际、国家、行业与团体标准的制定。 嘉宾介绍 张开翔 微众银行区块链首席架构师，FISCO BCOS平台架构师。曾在腾讯工作多年，在分布式系统，网络安全，海量服务等技术领域有丰富的经验。目前致力于区块链平台系统建设、以及推动基于区块链的业务落地。 讲座内容 讲述区块链的基本原理及其发展历史，深入介绍公链与联盟链的区别，探讨区块链的价值、前景及发展趋势。同时，本期嘉宾作为多个业内极具影响力与开创意义的区块链应用案例打造者，将为您亲自解剖区块链在多场景下的应用，包括区块链与对账，区块链与仲裁存证等。 时间： 2018年05月10日 19:00 地点： 上海交通大学学生创新中心（原工程训练中心）思源楼200号 讲座福利 为了证明我们邀请你来的诚意，先看福利！ 先到会场的前50名同学每人获得精美礼品一份~ 参与现场提问，精美礼品拿到手软~ 如果你是电院的小伙伴，重点来了， 本次报告电院研究生记讲座素拓一次，本科生记1161项95分素拓 千万别错过！ 访问数量：','2018-05-04 00:00','2018-05-04 00:00','','','学生办','2018-05-04 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1534119331,'【研究生】关于收集2017年度研究生助学金个人年度总结的通知','各位受助同学： 2017年度研究生助学金个人年度总结的收集工作现在开始，现将相关事宜通知如下： 一、材料要求 1、电子版个人年度总结。模板和称谓表详见附件，请同学严格按照要求撰写； 2、电子版个人生活照一张。要求有清晰正脸，不小于1M（请勿提交证件照）； 3、个人年度总结和照片均以“项目名称+姓名”命名； 注：若极个别同学获得多项助学金，请分别撰写个人年度总结，并分别填写、提交材料。 二、材料提交流程 1. 从本页面或材料提交入口（https://ssc.sjtu.edu.cn/f/f24750d6）下载个人年度总结模板及助学金设奖方称谓列表 2. 撰写个人年度总结、访问并通过jAccount登陆材料提交入口 3. 上传个人年度总结与生活照 三、材料提交截止时间 2018年5月10日16:30 附件： 个人年度总结模板及称谓 访问数量：','2018-05-03 00:00','2018-05-03 00:00','','','学生办','2018-05-03 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2062604617,'【研究生】关于评定2018年凯原励志奖学金的通知','各位同学： 现将2018年凯原励志奖学金评审情况通知如下： 一、奖励对象和名额分配 每年授予100名品学兼优的家庭经济困难学生“凯原励志奖学金”，其中凯原法学院20名，致远学院20名，由凯原法学院和致远学院自行评审，其他60名用于奖励全校其他学院的学生。其他60名获奖名额的分配规则为：本科15名，硕士20名，博士25名。 全校共推荐90名候选人进入学校评审阶段，学校评审委员会以现场面试的方式从90名候选人中选出60名获奖人和5名候补获奖人，凯原励志奖学金终评委员会审核确定60名获奖学生。 电院推荐6名硕士研究生、7名博士研究生进入学校评审。 二、奖励金额和申请人条件 奖励金额每人1.25万元。 申请人资格： 凡在“上海交通大学家庭经济困难学生数据库”中登记的上海交通大学在册全日制本科生、硕士研究生和博士研究生，均具有“凯原励志奖学金”的申请资格，但在申请时前一年的2月1日至申请年的1月31日期间（以下简称“评选年度”）具有下列情况之一的，不具备申请资格 ： （一）考试不及格的； （二）受纪律处分或仍处于处分考察期内； （三）因公派出国、中止学业或者休学等原因离校的； （四）延期毕业的，但下列两种情除外： 1、优秀博士生学位论文基金获得者； 2、因公派出国等其他特殊情况延期毕业，经由申请学生所在学院党政联席会议同意的。 三、材料提交 电子版材料 （一）《凯原励志奖学金申请表》的电子版，命名为“学院+学历+姓名+凯原励志奖学金申请表”，请根据样表填写； （二）《凯原励志奖学金申请汇总表》电子版，命名为“姓名+凯原励志奖学金申请汇总表”，请根据样表填写，注意选择硕士/博士表格; （三）《上海交通大学2017-2018学年奖助学金汇总表》电子版，命名为“姓名+上海交通大学2017-2018学年奖助学金汇总表”，博士可不写成绩，专业排名可不写； （四）自我展示PPT（具体情况后文说明，有模板）。 纸质版材料 （一）《凯原励志奖学金申请表》和个人申请书，纸质版，请根据样表填写，手写签名日期； （二）成绩单截图 ； （三）科研成果清单及其证明材料（评选年度内）； （四）在评选年度获得的奖项材料； （五）足以证明申请人品学兼优的其他书面材料； 所有材料截止时间为5月8日下午5点。电子版材料请打包发送到seiee_yjsjxj@163.com，纸质版材料请提交到电群3-109助管处。 四、其他 （一）根据学校要求，学生请准备4页','2018-05-03 00:00','2018-05-03 00:00','','','学生办','2018-05-03 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-719567176,'【本科生】关于开展2018年度上海交通大学凯原励志奖学金评审工作的通知','为帮助家庭经济困难的学生勤奋学习、努力进取，树立高度的社会责任感和公共意识，在德、智、体、美等方面全面发展，廖凯原基金会特在我校设立“凯原励志奖学金”，现将本年度凯原励志奖学金评审情况通知如下： 一、奖励对象和名额分配 全日制本科生，凡同时满足以下两个条件的，均可申报： （1）2018年9月仍在校，并履行正常学生证注册报道手续的（即已直升本校研究生或博士，确定在交大读研/博的大四同学也可以申请）； （2）在交大就读目前学历期间曾经获得过助学金的，或者在上海交大贫困生数据库中的。 但在申请时前一年的2月1日至申请年的1月31日期间（以下简称“评选年度”）具有下列情况之一的，不具备申请资格 ： （一）考试不及格的； （二）受纪律处分的； （三）因公派出国、中止学业或者休学等原因离校的； （四）延期毕业的。 “凯原励志奖学金”的评选，应当坚持下列标准： （一）思想端正，诚实守信，品德优良； （二）学习勤奋，态度端正，成绩优秀，科研突出； （三）热心公益、成绩显著； （四）家庭经济困难、生活简朴，自强自立。 电院（包括原电院、信安、软件、微电子）上报本科生候选人2名，这2名候选人最终将与全校其他学院的共计90名候选人一起参加学校的最终评审。 二、奖励金额 每人1.25万元。 三、申请材料 （1）《个人申请书》纸质版，见附件5（申请书首行居中写“凯原励志奖学金申请书”，抬头顶格写“尊敬的廖凯原基金会”，内容自定，最后落款手写签名，日期）； （2）《附件2 2018年度上海交通大学凯原励志奖学金申请表》的纸质版和电子版，严格按照模板填写，电子版命名为“学院+学历+姓名+凯原励志奖学金申请表”，如张三的申请表文件名为“电院本科张三凯原励志奖学金申请表”； （3）“附件3 2018年度上海交通大学凯原励志奖学金申请学生汇总表” 电子版，严格按照模板填写，以自己的名字命名，如“张三 2018年度上海交通大学凯原励志奖学金申请学生汇总表”； （4）在评选年度获得的奖项材料，纸质版。（注：“评选年度”为申请时前一年的2月1日至申请年的1月31日期间，下同）； （5）在评选年度发表的科研成果、其他科研竞赛、奖项等证明材料，纸质版； （6）足以证明申请人品学兼优的其他书面材料，纸质版； （7）《附件4 上海交通大学2017-2018学年奖助学金汇总表》，电子版。 请申请的学生于5月8日下午5点前将以上所有纸质材料提交至电院3-102助管','2018-05-03 00:00','2018-05-03 00:00','','','学生办','2018-05-03 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-876855204,'【本科生】关于2017年度助学金感谢信收集工作的通知','各位同学： 2017年度奖助学金个人年度总结，即感谢信的收集工作现在开始，现将相关事宜通知如下： 一、学生名单 需要提交感谢信的学生名单见下方链接。 二、所需材料 1、电子版个人年度总结。模板和称谓表详见附件2、附件3，请同学严格按照要求撰写； 2、电子版个人生活照一张。要求有清晰正脸，不小于1M（请勿提交证件照）； 3、个人年度总结和照片均以“项目名称+姓名”命名； 注：若个别同学获得多项助学金，请分别撰写个人年度总结。 三、提交方式及截止时间 上述材料请打包并命名为“姓名+奖项”，于2018年5月13日（周日）24:00前发送到zhuguan104@163.com，邮件以“姓名+奖项”命名。 请同学们严格按照要求提交材料，以免影响奖学金发放以及后续奖学金评审工作 附件： 附件1.电子信息与电气工程学院名单 附件2.“个人年度总结”模板 附件3.奖助学金“个人年度总结”称谓表 访问数量：','2018-05-03 00:00','2018-05-03 00:00','','','学生办','2018-05-03 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1987102673,'电院学生党支部开展4月份主题党日活动','4月，电院学生党支部围绕纪念马克思诞辰200周年暨《共产党宣言》发表170周年开展主题党日活动，部分支部进行了主题读书分享交流活动。 各支部党员结合马克思的生平事迹进行交流，集体学习《共产党宣言》，观看纪录片《共产党宣言》。 部分支部组织集体观看《年轻的卡尔·马克思》、《建军大业》等红色经典电影，开展《共产党宣言》、《周恩来传》、《万历十五年》等红色书籍主题分享交流。 博士生电子第二党支部与本科生电子党支部以专业为桥梁，组织电子系本科生党员参观光纤国家重点实验室，介绍实验室基本情况、现有团队、研究方向、未来发展等，活动有效促进博士生支部与本科生支部的联络，拓展本科生视野，增进对科研的了解和兴趣。 博士生电气第一党支部组织新老生学术经验交流，邀请支部优秀博士生分享科研经历和想法，帮助支部成员学习了解科技达人的最新学术成果、学术思想和科研方法。 博士生电子第一党支部以4月24日中国航天日为契机，参与航天八部的“不忘初心，不负韶华，勇做航天强国的奋进者和担当者”中国航天日主题活动。航天八部的老师向支部党员介绍八部的发展历史和成就，科普航天武器知识。 通过参观学习，支部党员更加深刻体会到发展中国航天事业、建设航天强国的重要意义，进一步坚定了自己对学术科研的不懈追求。 作者：焦文宾 摄影：各支部提供 访问数量：','2018-05-02 00:00','2018-05-02 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=22301','学生办','2018-05-02 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2066433396,'人工智能宣传月-西电集团主题讲座','研究生记讲座素拓一次，本科生1161项讲座满分素拓，对西电集团和人工智能有兴趣的不要错过哦 并记电子系和计算机系学术报告会一次 地点：电院3-208 时间:5月3日19点-21点 企业简介： 西安西电电气研究院有限责任公司（简称“西电研究院”）是中国西电集团有限公司（简称“中国西电”）下设的直属研发机构。中国西电作为一家全球性的技术公司，主要提供输配电行业的电力装备和成套系统解决方案，是现代电网行业的中流砥柱。 西电研究院作为中国西电的中央研究院，是集团公司创新发展的“永动机”，主要从事电气、机械、材料、数字化系统等前沿领域的尖端技术研发，重点领域包括：智能电力设备、先进制造、先进材料、工业互联网、自动化与控制系统以及电力电子系统等。 访问数量：','2018-04-28 00:00','2018-04-28 00:00','','','学生办','2018-04-28 00:00:00.0');
