insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1612910855,'【研究生】关于研究生素拓公示网站的通知','各位同学： 为方便素拓查询和素拓公示，电院研究生素拓将采用交大一门式平台进行统一管理。自通知发布之日起至2018年8月31日为测试阶段，一门式平台与ftp将同步公示素拓；自2018年9月1日起，只使用一门式平台进行素拓管理，不再采用ftp。现对一门式服务平台的有关注意事项通知如下，请同学们关注： 1.素拓查询网站为：https://ssc.sjtu.edu.cn/s/4ec48e2c。目前，查询仅支持姓名或学号的精确查询，条目中班级的缺失不影响对素拓的搜索和认定。 2.对于已经公示完毕的活动，请同学们关注姓名或学号搜索时的条目是否相同、姓名和学号是否匹配、条目是否重复以及与ftp上的内容是否相同。若出现姓名学号搜索条目不同、姓名学号不匹配、条目重复和ftp上存在的条目未在一门式网站上显示等问题，请截图发送至huangn01@163.com，并请留下姓名和学号以便进行调试，反馈截止时间为2018年5月31日。请同学们务必进行素拓查询，保证素拓的正确性。用于评定2018年秋季奖学金的素拓将以一门式网站上公示的内容为准。 3.对于还在公示期的活动，如果出现疑问请在公示期内去相应公示部门进行咨询。 4.提醒各位同学，在未来所有的活动中，当登记素拓时，请向活动方提供准确、清晰的学号和姓名。如出现由于姓名或学号缺失导致无法查询到素拓，影响奖学金评定，后果由同学本人承担。 5.学生办网站上的公示情况列表将保持更新，请同学们关注。公示情况列表网站为： http://xsb.seiee.sjtu.edu.cn/xsb/info/12887.htm 【说明】目前仅公示党团素拓和讲座素拓。社会实践，荣誉称号等其他素拓会在9月份素拓认定时进行统一公示。届时请根据通知查看。 访问数量：','2018-04-23 00:00','2018-04-23 00:00','','','学生办','2018-04-23 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1138460956,'【NOTICE】留学生信息收集SEIEE International Students Contact Information Collection','各位留学生同学： 通过本学期的几次活动后，学院发现有部分同学的联系方式已经发生变更。 为了保证能够及时的将大家的学业事务、奖助学金事务以及活动通知及时的让大家知晓。电院学生工作办公室开始收集大家的联系方式。 请大家准确填写问卷。 如果因联系方式有误而导致的学业、奖助学金事务问题，学院将不承担任何责任。 问卷地址在本文末尾。 Dear international students of SEIEE: After several activities this semester, we found that some international students  E-mail address and mobile phone number have changed. In order to facilitate the timely and accurate notification of the scholarship, activity notice and various teaching arrangements, we began to collect the new contact information. Please fill in this questionnaire truthfully. The SEIEE will not be responsible for any academic or scholarship problems just because of the wrong contact information. 联系方式填写地址： the questionnaire link is here: https://www.wjx.top/jq/22800984.aspx 访问数量：','2018-04-20 00:00','2018-04-20 00:00','','','学生办','2018-04-20 00:00:00.0');
