insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(573558536,'【NEWS】电院召开2018年度留学生安全法制教育大会','为促进留学生对我国法律法规、我校校规校纪的了解，电子信息与电气工程学院于4月16日在电院3-200报告厅开展了电院全体留学生法规校纪教育活动。电院学生办思政教师刘淼、辅导员郑中豪出席本次会议。此外，本次大会还邀请了上海市文保分局涉外科民警赵镭，任伟建，杨晨，交大保卫处政保科科长李为强老师进行法制宣传。会议由郑中豪主持。 赵警官分别从我国高校常见案件的预防、典型诈骗案例、以及寝室安全用电三方面详细的讲解了安全法律知识。赵警官通过图片以及视频材料向学生展示了球场盗窃、公共场所到期日、如实盗窃和自行车、电动车盗窃的违法行为，并号召大家注意人身财产的安全。 随后，赵警官讲解了法制宣传。对于毒品这一内容做了着重阐述。并向学生明确了我国法律对于毒品的态度。 最后，电院学生办同时还配合文保分局的民警对我院留学生展开了对毒品认识的问卷调查。 会后留学生纷纷表示，通过此次法规校纪教育大会，使自己更好的了解了中国法律法规和校园规章制度，更加熟悉中国国情，他们坚信，今后自己在中国的留学生活必将更加顺利。 作者：郑中豪 摄影：郭晶晶 访问数量：','2018-04-18 00:00','2018-04-18 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=22161','学生办','2018-04-18 00:00:00.0');
