insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1677680182,'第十二届电院“电力九零”校友爱心奖助学金颁奖仪式举行','2018年4月8日下午，第十二届“电力九零”校友爱心奖助学金颁奖典礼于电院群楼3-208报告厅顺利举行。电院党委副书记杨一帆、电院党委副书记李劲湘、电气工程系系主任严正、电院学生办综合办副主任刘淼以及“电力九零”校友代表洪峰、张明、王海群、杨文威、刘粤海、张卫红、周骏麟、朱蕻出席了本次颁奖仪式。会议由第十一届“电力九零”奖助学金获奖学生曹俊主持。 “上海交通大学电力九零校友爱心基金”是由交通大学电力系九零届的全体同学捐建的。为了帮助选择电气专业的困难学生顺利完成学业，坚定专业思想，电力九零全体同学出资帮助经济困难的交大电气工程系的学子，鼓励他们勤奋学习，力争在德、智、体等方面全面发展，成为社会发展和经济建设的优秀人才。 严正代表学院首先致辞。他对校友们的回访母校表示了热烈的欢迎以及衷心的感谢，对这项“电力九零”奖助学金在助学树人领域作出的创新表示肯定。他提到，希望将这种助学模式进行推广。同时，他也对获奖的同学提出要求，要向学长们学习，在以后的学习生活中要有坚强的意志，要珍惜这份荣誉并努力将这份事业传承下去。 接着，校友代表王海群致辞。致辞中，他首先对严正主任对"电力九零"的高度评价表示感谢，其次，他提到了"电力九零"基金的发展历程，希望此基金可以很好的反哺母校。他怀揣感恩之心，对母校表达了深深的感激之情，希望“电力九零”这个基金会能够基业永续，代代传承下去。 而后校友代表张卫红宣读了第十二届“电力九零”校友爱心奖助学金获奖名单，并由各位校友代表为获奖学生颁发获奖证书。 获奖学生代表欧阳自强作了发言，他对"电力九零"爱心基金表达了自己衷心的感谢。尽管生活条件并不是很好，但是他在生活中努力、勤奋、全面发展。在退伍回来后，他努力学习充实自己，他以感谢、动力和反思三个词表达了自己的心情。他感谢学长学姐们的帮助，坚信只有汗水才能配得上荣誉，学习前辈们感恩责任奉献的榜样，让感恩传递，让奉献永驻，让责任长存。 在访谈环节，各位校友一一耐心地解答了同学们的各种疑问，给出自己的建议。 最后，周骏麟学长回顾了自己过去十年的生活经历，图文并茂的讲述了在自己专业领域摸爬滚打的历程，让人感受到他丰富的生活阅历和扎实的专业技能。也为学弟学妹们提供了就业的建议，并了解以后的工作环境。 仪式结束后，各位校友与获奖学生进行了合影留念。 “电力九零”校友爱心奖助学金已经走过十二个年头，至今已经帮助了七十多位品学兼优、自强不息的学子。值此交大一百二','2018-04-09 00:00','2018-04-09 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=22067','学生办','2018-04-09 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1869921903,'关于2018届本科毕业生的毕业集体照拍摄通知','各位2018届本科同学： 我们即将迎来毕业流程中非常重要的毕业集体照拍摄环节，具体安排请认真查看以下内容，特别是集合时间地点和拍摄注意事项。请各位班长注意提醒班级同学（特别是在外实习的同学和留学生）安排好时间参加毕业集体照的拍摄，不要留下永久的遗憾。 毕业集体照分两部分：系照合影（1张）和班级照（2张）。其中，系照合影要求全院所有同学统一上午8：30集合；班级照分为学位服和正装照。毕业照总计40元，请各位参与毕业照拍摄同学把钱交给班长，由班长收集齐后在暂定4月9日的班长会议上统一上交和确认（可支付宝或现金）。 ==================================================================== （1）系照合影安排： 【集合时间地点】2018年4月24日（周二）上午8:30  电信群楼3号楼东侧所有同学整队，找寻自己的系牌 系照合影拍摄顺序如下： 第一组（9:00开始） 第二组（9:00开始） 1、电子系（225人） 1、软件学院（95人） 2、计算机系（172人） 2、自动化系（80人） 3、电气系（152人） 3、仪器系（55人） 4、网络空间安全学院（100人） 4、微纳电子学系（38人） 5、IEEE试点班（69人） 全院全体同学请按时到场，集合完毕后准时开始拍摄，过时不候！ （2）班级照安排 地点：电信群楼3号楼东侧广场，时间：2018年4月24日（周二） 各班具体时间： 上午 F1403001- F1403008 10:00-11:00 F1403009- F1403016 11:00-12:00 下午 F1403017- F1403024 13:00-14:00 F1403025- F1403032 14:00-15:00 F1403601- F1403602 15:00-16:00 F1403701- F1403703 F1421101- F1421102 1. 建议班长邀请班主任和思政教师参加班级照的拍照； 2. 班级照拍摄的时间顺序以现场预约为准，先到齐的班级先拍，过号重新排队； 【特别提醒】 1、毕业集体照将存入交大档案馆，要求全体毕业生参加； 2、本班所有同学（包括可能延期或结业的同学）都需参加，不得邀请非本班毕业生参加； 3、毕业集体照共分为一张系照和两张班级照； 两张班级照：分别为正装照和学位服照，学位服现场提供，正装需自备（实在无法','2018-04-04 00:00','2018-04-04 00:00','','','学生办','2018-04-04 00:00:00.0');
