insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1387596540,'2018年春心理咨询安排——“倾青有约”心理健康工作室','亲爱的同学， 您好！“倾青有约”心理健康工作室是专为电院学生打造的心灵小家，工作室将为电院学子提供心理咨询、团体辅导、心理讲座、心理沙龙等多种形式的活动。 本学期，工作室邀请校内专业心理咨询师，为电院学生提供免费的心理咨询服务。具体时间安排如下： 周一 周二 周三 周四 周五 12：20-13：30 王雅卓 邢雯婷 王雅卓 梁玉杰 宋奕青 13：45-14：35 邹   雷 刘春颖 15：00-15：50 邹   雷 刘春颖 【咨询须知】 本工作室通过个人咨询、心理团体、沙龙分享、体验式活动等多种形式，为电院学子提供专业的心理健康服务。工作室规则如下： 1、本工作室服务对象为电子信息与电气工程学院注册的全日制本科生和研究生，不收取任何费用。 2、本工作室咨询服务范围涵盖学生的各类困惑与烦恼。各类严重的心理疾病需要根据建议去精卫中心就诊，工作室只提供辅助咨询。 3、本工作室实行预约登记制度，请用登录网址http://counsel.seiee.sjtu.edu.cn，通过jaccount预约，每次咨询均需预约。若网络故障或情况紧急，可致电34205080（宋老师），或至电信群楼3-109问询。 4、学生可自由选择咨询师。咨询后若有需要，学生可更换咨询师。由于资源有限，请勿同时在两个咨询师处咨询。 5、咨询地点：电信群楼1-201A（电信群楼1号楼东侧走廊2楼）。咨询请提前5分钟到达咨询室，并主动出示校园卡。 6、一次咨询时间不超过50分钟，建议每周不超过1次。 7、来访同学迟到20分钟以内，可以咨询，但是咨询师不相应顺延咨询时间。若迟到20分钟以上，请预约下一次咨询。 8、咨询师可能会提出行为安排建议，你若觉得不妥，可坦然拒绝。 9、请勿和咨询师发展咨询以外的个人关系，勿提与咨询无关的请求。 10、本工作室严格恪守职业伦理，为所有来访同学保密。 11、本工作室不提供任何形式的学生心理健康状况证明，不提出药物服用的建议。证明或药物服用请咨询校心理咨询中心或具有资质的心理诊断机构。 12、为了保障咨询的专业与规范，建议勿预约您的思政教师。 【预约方法】 1、登录网址http://counsel.seiee.sjtu.edu.cn，通过jaccount预约； 2、扫码关注微信公众号，进入公众号后通过菜单预约。 “用倾听陪伴，让青春飞扬”！倾青有约用真心，陪您一起探索、成长！ 访问数量：','2018-03-26 00:00','2018-03-26 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=21942','学生办','2018-03-26 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1949021006,'本科生2017-2018学年第一学期综合测评成绩及排名公示通知','各位同学： 2017-2018学年第一学期综合测评成绩及排名见附件，请同学们自行下载查看。 关于本次公示需要特别强调以下几点： 1）附件内包含所有同学的成绩和排名，请同学们及时查看，如有问题在公示期内反馈。 2）评选按照最新的奖学金评选规定，必须上一学年完整在校的在籍学生才能评选2017-2018年度奖学金，故对于因退学、入伍、休学、国外交流等有一个学期以上不在校的同学，不参加本次综合测评，若有情况错误或遗漏，欢迎同学们及时反馈。 1）每学期综合测评成绩由学积分和素拓成绩构成，学积分占70%，由学院教务办提供，素拓占30%，由综合测评网站导出。 3）本次综合测评成绩公示期为2018年3月26日-2018年3月28日下午17：00，如有问题请务必于公示期内反馈，公示期后不接受成绩更改。 4）关于素拓成绩，除系统计算错误可以进行修改外，其他原因导致的成绩错误不予更改。 5）关于学积分，学积分由教务系统导出，原则上以此为准，若因修改课程成绩等原因导致学积分已经发生变化，请在公示期内反馈。 公示期内所有问题请发送邮件至seieejiangxuejin@126.com反馈。 2015级公示 2016级公示 2017级公示 电院学生办 2018年3月26日 访问数量：','2018-03-26 00:00','2018-03-26 00:00','','','学生办','2018-03-26 00:00:00.0');
