insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(806666076,'电院“心系国防情，志守科研岗”寒假社会实践团赴雪枫旅参观学习','2018年1月19日至21日，电院“心系国防情，志守科研岗”寒假社会实践团宁夏线在电院党建指导老师梁玉杰、曹原的带领下，前往位于宁夏的“雪枫旅”展开为期三天的社会实践活动。实践期间，同学们观摩了反恐训练，参观了旅史馆，与部队首长和官兵座谈，深感震撼、收获颇丰。 走进“雪枫旅”的大门，实践团成员被广场中央题有“雪枫旅”的石碑吸引。“雪枫旅”是一支有着光荣历史的部队，其前身为新四军抗日先遣大队，由抗日名将彭雪枫、原军委副主席张震、原中顾委常委肖望东等老一辈革命家于1938年6月在河南确山县竹沟镇创建。抗战时期，“雪枫旅”在淮阳窦楼打响了抗日东进的第一枪，在板桥集战斗中开创了我军历史上首次用机枪击落敌机的光辉历史。随后，“雪枫旅”挥师南下，解放杭州，受到浙江百姓的热烈拥护。光辉的历史造就了精英的部队，如今的“雪枫旅”，是西部战区反恐、特战的中坚力量，在反恐斗争中发挥了重要的作用，在全军比武大赛中斩获佳绩，在国际舞台上展现中国铁军的光辉形象。 在部队首长的带领下，实践团来到了训练场。训练场上，地面进行跳伞训练的设备一字排开，部队首长介绍，跳伞是“雪枫旅”每位战士必备的技能，高跳低开、高原跳伞、夜间跳伞、三角翼跳伞等高难度科目对于“雪枫旅”的战士而言，是日常训练内容。实践团来到了一幢模拟居民楼前，一支反恐部队正在进行楼房攀登、降落训练。随着指挥员一声令下，两名战士发射抛绳器，纵身腾跃，迅速爬上了房顶。战士们还用沿水管攀登、沿阳台攀登等方式训练了不借助绳索爬上房顶的各种动作。随后，战士们进行了降落训练，按照指挥员的指令降落到指定楼层，并完成侦查、从阳台突入等动作。沙场上，一支特战部队正在进行格斗训练，两名战士分别手持棍棒、匕首等武器展开近身格斗。战士们爬楼时如履平地的娴熟动作、格斗时的过硬技能赢得了实践团成员热烈的掌声。 与部队官兵共进午餐后，实践团参观了旅史馆，实践团成员了解了“雪枫旅”官兵在战争时期英勇杀敌、舍身救战友的英雄历史，在和平年代参加全军比武大赛、国际反恐联合训练和抗战胜利七十周年大阅兵的光荣事迹，对“雪枫旅”的战争时期的光辉历史、新时代下的崇高使命有了深刻的感悟。 实践团参观了“雪枫旅”的王牌部队“天狼突击队”，这是一支隶属西部战区的反恐特战部队。要想成为天狼突击队的一员，战士们要接受常人难以想象的严峻考验，在丛林荒野中自主寻找食物、水源，在复杂多变的地形、气候条件下完成大量高难度的科目。只有具有钢铁般意志','2018-02-02 00:00','2018-02-02 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=21685','学生办','2018-02-02 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(358256945,'【通知】关于做好2018届毕业生求职创业补贴发放工作相关通知','关于做好2018届毕业生求职创业补贴发放工作的相关通知 各位同学： 根据《关于进一步做好本市高校毕业生求职创业补贴发放工作的通知》（沪人社就发〔2016〕91号，详见附件1）的相关要求，现就做好我校2018届毕业生求职创业补贴申请发放工作的相关事宜通知如下： 一、发放对象 本校毕业年度内有就业创业意愿的以下学生： （一）享受城乡居民最低生活保障家庭的毕业生； （二）残疾毕业生； （三）就读期间获得国家助学贷款的毕业生。 以上皆指全日制学生，不包括已确定升学、出国留学的毕业生。 二、发放原则 坚持自愿申请、公开公正、属地管理、专款专用、及时高效的原则。 三、补贴标准 补贴标准为每人1000元，每人限领一次。 四、申请材料 1、《高校毕业生求职创业补贴申请表》（见附件2。注：学校学生资助管理部门审核盖章处留空，校就业中心收齐后统一提交校学生事务中心审核盖章） 2、本人身份证复印件正反面，贴在申请表背面； 3、个人银行账户复印件正面，贴在申请表背面（学校统一办理的银行借记卡复印件，复印件要能看清卡号，或卡号在复印件空白处另写并签字）； 4、享受低保的毕业生需提供所在家庭享受城乡居民最低生活保障的证明；残疾毕业生提供残疾证复印件；获得助学贷款的毕业生提供助学贷款合同的重要页面复印件。（具体要求必须按照附件4《求职创业补贴发放工作图文操作细则》严格操作，不符合要求的证明将会导致申请失败。） 5、在线填写《上海交通大学2018届毕业生求职创业补贴申请汇总表》，请务必保证填写正确。线上汇总表链接如下： https://jinshuju.net/f/NHhvtC 线上汇总表二维码如下： 五、申请程序 1、毕业生于2018年3月1日前(1月20日-2月25日寒假期间暂不接收材料)将《毕业生求职创业补贴申请名单》和申请材料递交到至电院学生办（电院3-107马丽丹老师处）。 2、由校就业中心将通过初审的申请人员名单在校内公示5个工作日。公示无异议的申请人材料由校就业中心统一上报给闵行区人力资源和社会保障局。 校就业中心联系人：倪老师(54745994转661) 办公室：闵行校区铁生馆205室 交大上海市求职创业补贴申请咨询交流QQ群：538589074 附件： 附件1 沪人社就〔2016〕91号(关于进一步做好本市高校毕业生求职创业补贴发放工作的通知) 附件2 高校毕业生求职创业补贴申请表 附件3 毕业生求职创业补贴申请名单 附件4','2018-01-16 00:00','2018-01-16 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=21653','学生办','2018-01-16 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-441596812,'电院社会实践团赴文昌卫星发射中心参观学习','1月10号早晨，“心系国防情”海南线社会实践团搭上了去往文昌卫星发射中心的大巴。雾蒙蒙的天气给此行增添了些严肃庄重的气氛。 实践团首先来到了指控大厅，此时正在进行合练任务，通过大厅的屏幕，可以直接看到西昌发射中心的实时画面，虽然只是合练，但是大屏幕上一步步逼近的倒计时，还是让人充满着期待。之前在电视上看到更多的是“倒计时”、“点火”、“发射”这些激动人心的场景。但是通过这次参观，实践团团员们了解到每次成功发射的背后，都是这样一次次精密的安排，严格的合练，绝不放过任何一个细节。颗颗螺钉，小小按钮，在航天人的眼中，关系到航天梦能否实现，关系到民族尊严能否维系。 参观期间，毕业于上海交通大学的工程师学长悉心回答着同学们的各种问题。在十楼的落地窗前，可以看到几公里外的两座发射塔架，在绿洲中高高耸立，与远处的高楼辉映。在那里，曾经发射过长征五号以及长征七号运载火箭。在长征五号与长征七号的模型下，学长向实践团一行科普火箭是如何运输到发射基地，在哪里进行组装，组装完成之后如何运输到发射塔架。实践团的同学也在此时积极地提出自己的疑问。学长的解答逐渐让实践团团员们意识到，当下是一个争取太空空间的重要时期，我国航天事业发展至今，用短时间取得了一系列举世瞩目的成就，也意识到，我国的航天事业距离世界第一梯队还有很大差距。学长不忘鼓励同学们树立航天梦，如果有机会，将来要积极参与到航天事业之中。 之后，实践团来到了火箭的发射塔架下。由于通知未到位，值班战士拦住了实践团一行人。直到与上级通话之后，才予以放行。小小的细节，彰显的是卫兵的守职。随后，实践团乘坐电梯前往发射塔架顶端，电梯荷载控制严格且上升比较慢，了解之后才知道这是防爆电梯，为了防止电路造成安全隐患。由此可见，看似简单的发射塔架，实则不然。登上塔顶之后，可以将整个发射中心尽入眼底，发射塔的一边是烟波浩淼的南海，另一边就是美丽整洁的基地。很难想象，十年前这里还是一片荒凉的景象，而现在文昌发射中心已经是国内技术最先进，运载能力最强，基础建设最新的火箭发射中心。可以预见的是，在不远的将来，会有更多更先进的火箭从这里飞向太空，让我们的航天梦一步步变为现实。 最后，实践团参加了座谈会。出席座谈会的除了实践团全体成员，还有中国人民解放军驻上海交大选择培养办公室的耿梅娟主任、王海鹏干事、校武装部的赵建士老师、发射站的领导以及交大毕业的学长们。耿梅娟首先发表讲话，向发射站的领导们介绍了本次社会实践','2018-01-14 00:00','2018-01-14 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=21633','学生办','2018-01-14 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-263733386,'【研究生】2018年国防科技奖学金工作开展通知','各位同学： 2018年国防科技奖学金工作已经开展，此项奖学金针对附件表格中所涉及专业的所有研究生，请同学们注意以下几点说明： 1.学生申报国防科技奖学金，需要学生本人主动与用人单位取得联系，征得用人单位同意（每个单位或看简历或面试，形式不一），学生方可申报国防科技奖学金，签订三方协议书。针对电院的用人单位与具体专业要求，以及用人单位联系方式详见附件《2018年国防科技奖学金》表格。 2.协议签订后，学生毕业后必须到协议单位工作，如违反相关协议，需要退1赔1，具体详看附件三方协议。 3.请有意向的学生1月15日--1月19日联系用人单位。请同学们尽早联系用人单位，用人单位也在其他高校发布了同样的奖学金通知。如在1月24日前能够与用人单位达成一致，请申报此奖学金的同学及时联系事务中心于老师，以便完成后续材料申报。 于老师联系方式：021-54742275-803，yuaitao1985@sjtu.edu.cn 4.中国商飞上海飞机设计研究院此项奖学金学生需求2人，专业为信息与通信工程或相关专业，学历要求硕士研究生。请有意愿加入此公司的同学在1月18日前将简历发至zhaosixuan@comac.cc，或联系13651906515赵老师。 电院学生办 2018年1月12日 2018年国防科技奖学金 三方协议书（范本） 访问数量：','2018-01-12 00:00','2018-01-12 00:00','','','学生办','2018-01-12 00:00:00.0');
