insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2145776655,'电院学生基层党组织建设项目完成中期检查','12月初，电院学生党总支对今年4月立项的学生基层党组织建设项目开展情况进行中期检查，各支部成效显著。 各支部围绕思想组织、优良学风、科技创新、服务奉献等主题，采取了参观红色景点、学业分享交流、深入企业参观、志愿结对服务等灵活多样的活动形式，扎实开展支部建设。 为促进支部之间成果交流，进一步激发基层党组织活力，电院学生党总支将部分支部的建设情况制作成展板进行展示。 这次中期检查的成果，是电院基层学生党组织建设风貌的集中体现。电院学生党总支将继续开拓思路，落地实践，确保全院88个学生党支部齐头并进，充分发挥党支部的战斗堡垒作用。 作者：焦文宾 访问数量：','2017-12-22 00:00','2017-12-22 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=21263','学生办','2017-12-22 00:00:00.0');
