insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1705044650,'【研究生】蒋震海外研究生奖学金评选通知','各位同学： 蒋震海外研究生奖学金申请现已启动。该奖学金由蒋震工业慈善基金会提供，旨在支持大学生进行海外交流活动，减轻其学费压力。具体事项如下： 1.资助对象： 品学兼优的工程科大学毕业生（即：目前研三的学生），即将在国内取得硕士学位；并计划进入美国著名大学继续攻读硕士或博士学位者（备注清楚：已拿到offer或正在申请中）。 2.资助金额： 每人不超过五万美元（用以支付一学年学费及酌情给予生活费，以学季形式分期支付）。 3.评审程序： 学生自主申报，由学校进行审核，最终评选出共两名学生参加第一轮甄别（12月5日下午1:00，必须参加，具体另行通知），该甄别为笔试形式，中英文题目；通过笔试的同学将参加明年一月由蒋震基金会组织的位于深圳的面试，主要考察英语、仪表、思维等；获奖名单将于明年一月公布。 4.具体要求： 本奖学金不能与其他奖学金兼得（校abc、学业进步不计入在内）。请注意，此奖学金经过笔试、面试，确定获评将为2018年度，所以和2017年度获评奖项不冲突。但是会和2018年度唐立新奖学金续评、荣昶奖学金续评冲突，请同学们慎重考虑； 获奖学生必须保证能够入读其主修学科排名在十名以内的美国大学； 5.提交材料： 学生电子申请表、申请书、及学生希望提供的获奖等情况证明材料（电子版）。 请于12月1日（含）前提交。 电子版请以“学院+姓名+蒋震海外研究生奖学金”命名并上传至ftp"2017奖学金/蒋震海外研究生奖学金"，ftp地址是ftp://202.120.47.214/，用户名和密码均是public。 6.注意事项： 本奖学金要求、提交材料较为复杂，时间紧急。学生事务中心网站也有相关通知，进度请留意公告栏； 请学生务必注意材料真实性，虚假资料将被取消资格； 若学生未能考取符合要求的学校，或另考取其他不符合要求的学校，将取消其获评资格； 请学生务必注意两轮甄选的时间地点，保证可以参加。 电院学生办 2017年11月30日 2017-2018蔣震海外研究生獎學金申請表 上海交通大学奖学金申请书 访问数量：','2017-11-30 00:00','2017-11-30 00:00','','','学生办','2017-11-30 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-363472581,'“学习十九大精神，争当时代先锋”——电院B16硕士电气第四党支部11月主题党日活动圆满举办[图]','11月24日中午，电院B16硕士电气第四党支部在电信群楼3-306开展了11月的主题党日活动。本次的党日活动以“学习十九大精神，争当时代先锋”为主题，学指委综合办主任喜苏南、党建指导老师宋奕青以及支部全体党员参加了本次活动。 本次活动以十九大及新党章知识问答作为开场，同学们对20道与十九大或新党章有关的问题进行抢答，由于会前同学们提前对十九大以及新党章进行了学习，对大部分题目都能很快地给出正确答案。 知识问答之后，支书郭灵瑜带领支部同学对十九大报告进行了较为系统的学习，从十九大的主题出发，解读了“极不平凡的五年”，“主要社会矛盾变更”等十九大关键词，加深了同学们对十九大精神的理解。 集体学习结束后，各位同学各自谈了对十九大及新党章的看法和体会。其中，主要社会矛盾变更以及从严治党成为了同学们热议的话题。也有不少同学从十九大精神出发，探讨了应该如何做一位优秀的学生党员。 讨论结束后，喜苏南和宋奕青对同学们的发言进行了总结，高度赞扬了同学们的热烈讨论和发言，并对知识问答这一活动方式表示了肯定，也对今后党日活动开展的内容和形式提出了宝贵的建议。喜苏南还表示，作为与支部一对一结对联系的学指委机关老师，希望今后能够多多参加到支部的活动中来，和同学们多多交流，共同学习进步。 作者：李煜 摄影：李钰倩 访问数量：','2017-11-29 00:00','2017-11-29 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20932','学生办','2017-11-29 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(279875188,'电院博士生自动化第二支部举办“学十九大精神，展新时代风采”主题党日活动[图]','十九大胜利召开之后，学校迅速掀起学习宣传贯彻党的十九大精神热潮。为了促进同学们主动关心国事、学习理论知识，增强爱国主义情操，11月27日，电院博士生自动化第二党支部全体党员开展了以理论学习与知识竞答相结合的形式“学习十九大精神，展新时代风采”的主题党日活动，校团委书记方曦、电院学生党总支书记田怡萌应邀出席本次活动。会议由支部书记李肖城同志主持。 首先，支部书记李肖城同志向全体支部党员传达了学校党委书记姜斯宪书记关于传达学习党的十九大精神会议上的报告内容。报告整体分为十三块内容，支部书记对其一一进行介绍，并在关键报告细节上着重阐述，并畅谈作为博士党员、作为交大学生，我们该如何解读这份十九大报告的用词与用句。与会支部党员表现出积极认真的学习态度，仔细聆听汇报内容，学习新党章。 会议的第二部分是十九大知识竞答环节，由支部副书记王鸿源同志主持。知识竞答主要是围绕十九大报告内容，分为必答环节和抢答环节两个部分组成，支部党员共分为4组，以计分方式决定优胜者。经过近百道题目的竞答，由孙志坚同志为代表的第一组表现优秀，理论知识扎实，获得第一名。在此环节的进行中，各位党员热情高涨，表现出了积极争胜的态度，大家踊跃回答问题。对于回答错误的题目，王鸿源同志带领大家更正学习，加深印象。环节结束时，支部书记李肖城同志指出，部分同学对于一些理论知识掌握不足，强调各位党员应时常“充电”，加强对理论知识的温习。获胜小组代表孙志坚同志在会上发表了自己对十九大的感想，其感言自己从小受父亲教育，也出于个人对政治比较感兴趣，平时对党内事务、国家大事等十分关注，熟悉掌握理论知识，并举例说明政治生活对我们每个人的职业生涯及生活的重要性。 接着，支部热烈欢迎新生党员的加入，新成员分别进行了自我介绍，并对十九大精神发表了自己的感言和见解。严昱明提到自己出身红色家庭，爷爷曾参加过抗美援朝战争，对爷爷心怀崇敬，从小接受红色教育，对“只有中国共产党才能救中国”感触颇深。车琳对十九大胜利召开后，国内发生的诸多不良事件发表了自己的见解，认为网络上的部分批评声音甚至是造谣事件很可能是由国内外敌对势力、分裂势力的阴险伎俩，面对如此复杂险恶的环境，中国共产党及中国政府任重道远，作为党员的我们需要用冷静心态、明辨的眼光去看待并思考问题，切不可掉以轻心。 最后，方曦在总结中结合自身经历，给在座每位党员分享了三点内容：方法论、信念以及就业选择。在方法论上，希望大家能够用矛盾论的哲','2017-11-29 00:00','2017-11-29 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20936','学生办','2017-11-29 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(200073020,'蒋震海外研究生奖学金','各位同学： 蒋震海外研究生奖学金申请现已启动。该奖学金由蒋震工业慈善基金会提供，旨在支持大学生进行海外交流活动，减轻其学费压力。具体事项如下： 1.资助对象： 品学兼优的工程科大学毕业生（即：目前大四、研三的学生），即将在国内取得工程科学士或硕士学位；并计划进入美国著名大学继续攻读硕士或博士学位者（备注清楚：已拿到offer或正在申请中）。 2.资助金额： 每人不超过五万美元（用以支付一学年学费及酌情给予生活费，以学季形式分期支付）。 3.评审程序： 学生自主申报，由学校进行审核，最终评选出共两名学生参加第一轮甄别（12月5日下午1:00，必须参加，具体另行通知），该甄别为笔试形式，中英文题目；通过笔试的同学将参加明年一月由蒋震基金会组织的位于深圳的面试，主要考察英语、仪表、思维等；获奖名单将于明年一月公布。 4.具体要求： 本奖学金不能与其他春季奖学金兼得，会与荣昶奖学金及唐立新奖学金续评冲突 获奖学生必须保证能够入读其主修学科排名在十名以内的美国大学； 5.提交材料： 学生电子申请表、申请书（称谓：尊敬的蒋震工业慈善基金会）、及学生希望提供的获奖等情况证明材料（电子版）。 请于12月1日（含）前提交。 电子版请以“学院+姓名+蒋震海外研究生奖学金”命名并上传至ftp"2017奖学金/蒋震海外研究生奖学金"，ftp地址是ftp://202.120.47.214/，用户名和密码均是public。 6.注意事项： 本奖学金要求、提交材料较为复杂，时间紧急，请各位同学抓紧申请，学生事务中心网站也有相关通知，进度请留意公告栏； 请学生务必注意材料真实性，虚假资料将被取消资格； 若学生未能考取符合要求的学校，或另考取其他不符合要求的学校，将取消其获评资格； 请学生务必注意两轮甄选的时间地点，保证可以参加。 2017-2018蔣震海外研究生獎學金申請表 2.上海交通大学奖学金申请书模板 访问数量：','2017-11-29 00:00','2017-11-29 00:00','','','学生办','2017-11-29 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2136247658,'【研究生】2017年博世奖学金增补评审通知','各位同学： 接设奖方通知，现将2017年博世奖学金增补评审要求通知如下： 一、评审对象 本奖学金仅针对已经在网上进行过“2017年电院专项奖、优秀奖”申报的计算机系、软件学院硕士二年级、硕士三年级学生。名额分配如下： 计算机系 硕士三年级 1名 计算机系 硕士二年级 1名 软件学院 硕士三年级 1名 软件学院 硕士二年级 1名 二、评审要求 1.参评此增补奖学金的同学需要满足以下门槛条件之一： （1）在评审年度内有论文发表/录用、或专利授权/公开、竞赛获得第1-6名或一-三等奖。科研成果需符合《【20170731修订稿】上海交通大学电子信息与电气工程学院研究生专项奖学金、优秀奖学金实施细则（2017）》的要求 （2）在评审年度内有校级及以上荣誉称号。荣誉称号需在素拓认定过程中进行认定，即在ftp上的荣誉称号。 2.此奖学金由同学自主申报，最终学院推荐人选按照专项奖公示的排名确定。（参见链接：http://xsb.seiee.sjtu.edu.cn/xsb/info/12888.htm） 三、材料要求 1.2017-2018学年博世奖学金汇总表。 2.博世奖学金个人申请陈述表。 3.上海交通大学申请书。 4.各类证明材料。 材料1只用提交电子版，于12月1日中午11：30前发送至seiee_yjsjxj@163.com。 材料2-4只用提交纸质版，于12月1日中午11：30前提交至电群3-109助管处。 以上材料截止时间为12月1日中午11:30。 四、注意 1.所有材料提交完毕才视为申报成功，进入评审阶段。不提交材料的同学视为放弃此奖学金。 2.本项奖学金不能与国家奖学金及其他专项奖、优秀奖兼得。已经获评国奖、优秀奖学金和其他专项奖学金，或者正处于某些专项奖学金评选过程（常春藤特长奖学金、福建校友会奖学金、有成奖学金等）的同学不能参评本项奖学金。 3.过程中发现弄虚作假等行为取消参评资格并按照校纪校规进行相应处理。 电院学生办 2017年11月28日 博世奖学金个人申请陈述表 上海交通大学奖学金申请书模板 2017-2018学年博世奖学金汇总表 【20170731修订稿】上海交通大学电子信息与电气工程学院研究生专项奖学金、优秀奖学金实施细则（2017） 访问数量：','2017-11-28 00:00','2017-11-28 00:00','','','学生办','2017-11-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-999112225,'电院本科生计算机第一党支部举行“学习十九大”知识竞赛','11月27日晚，电院本科生计算机第一党支部在电信群楼3-306会议室举行了“学习十九大”知识竞赛，活动邀请了校团委老师许艳、电院党建指导老师李智出席。 支部组织委员强志文和纪检委员谢文涛结合十九大报告，精心准备了题库。竞赛采取分组对抗的形式，各小组之间积极抢答、对抗激烈，在紧张刺激的比赛之中，支部成员进一步加深了对十九大精神的理解。 活动结束后许艳寄语支部成员，在肯定了支部活动形式的基础上，指出学习十九大精神是一个长期的过程，不能只停留在大众化知识点的记忆背诵，作为一名大学生党员，要在深刻理解的基础上，积极宣传贯彻十九大精神。 最后，许艳、李智与参赛党员合影留念，“学习十九大”知识竞赛在温馨热烈的气氛中圆满结束。 作者：焦文宾 摄影：焦文宾 访问数量：','2017-11-28 00:00','2017-11-28 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20871','学生办','2017-11-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-930186582,'电子科技大赛志愿者招募','为更好地在我市青少年中普及电子知识，提高广大青少年的科学素质技能，促进学校电子科技活动的开展。培养学生动手进行电子作品制作与电子实验能力，使青少年学生逐步掌握电子技术知识与技能。将举办2017年“徐教院附中杯”上海市青少年电子制作活动。需招募60名志愿者担任赛事裁判。 报名链接：https://tongqu.me/act/16228 福利：1、 电院本科生：提供107项志愿者素拓 2、电院研究生：记一次党团活动素拓 访问数量：','2017-11-28 00:00','2017-11-28 00:00','','','学生办','2017-11-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(872766247,'电院学生党建研究会第二届主席团换届大会顺利举行','11月26日晚，上海交通大学电院学生党建研究会第二届主席团换届大会在电信群楼3-100举行，电院学生党总支书记田怡萌，副书记吴宏达与其他党建指导教师应邀出席，电院学生党建研究会全体成员及电院党支部书记参加大会。大会由电院学生党建研究会指导老师曹原主持。 在奏唱国歌后，电院学生党建研究会主席权令伟做了工作汇报，权令伟在报告中详细地介绍了党建研究会的组织架构以及职能，对过去一年的工作进行了详细的总结。跟着权令伟的思路，在场同学再一次回顾过往，引起了强烈的反响。 随着《上海交通大学电子信息与电气工程学院学生党建研究会主席团候选人名单》的宣读，主席团候选人竞选演说正式拉开帷幕。 期间沙金锐同学，应楚同学，蔡健飞同学，潘宇杰同学，吴飞圣同学，吕丙烁同学，李佳俊同学，李林果同学，陈嘉梁同学依次回顾了自己在电院学生党建研究会的工作经历，阐述了对竞选职位的理解与自我优势以及对未来的展望，也介绍了在工作过程迸发出的新思想：“星火+计划”、“实干主义”、“32+8+2+1计划”、家文化建设等。这些新的思想都引起了场下观众和指导老师的强烈反响。 在田怡萌，吴宏达等党建指导教师的共同投票决定下，新一届主席团成员诞生，并由田怡萌宣读《上海交通大学电子信息与电气工程学院党建研究会第二届主席团当选名单》，第二届主席团成员有：陈嘉梁，李林果，吕丙烁，沙金锐，应楚。 随后，当选主席团的成员发表竞选感言，新一届主席团成员均在发言中表示，一定会全身心投入到电院学生党建研究会的工作中来，保持优良传统，严守思想理论阵地，继续开拓创新，努力研究实践，成为院级学生党建研究会创新实践的先行者，形成示范效应，努力做好每一项工作。 接下来，田怡萌做总结致辞，在致辞中对电院党建研究会过去的工作表示充分的肯定，并对电院党建研究会的未来做出寄语，相信在全体党建指导老师和新一届主席团的带领下，大家能够“百尺竿头，更进一步”，在今后的工作中不断奋进，再创辉煌。 最后，大会在庄严的国际歌中圆满结束。 作者：孙琦 摄影：吴宏达 焦文宾 鲍捷 访问数量：','2017-11-28 00:00','2017-11-28 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20853','学生办','2017-11-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1543946480,'【本科生】2017年上海交通大学电力九零校友爱心奖助学金申请通知','各位同学： 大家好！ 现开展2017年上海交通大学电力九零校友爱心奖助学金申请工作。 “上海交通大学电力九零校友爱心基金”是由交通大学电力系九零届的全体同学捐建的,旨在帮助选择电气专业的困难学生顺利完成学业，坚定专业思想，成为社会发展和经济建设的优秀人才。每年资助6位同学，每人10000元。基金详细介绍见附件。 申请者要求： 上海交通大学电子信息与电气工程学院电气工程与自动化专业，大三年级（F15级）本科生； 热爱祖国，勤奋学习，立志为社会做贡献； 关心集体，乐于助人，积极参加公益活动； 遵纪守法，艰苦朴素，专业成绩优秀； 经济困难，没有贷款难以承担完成学业所需费用。 请有意愿申请电力九零校友爱心奖助学金的同学，需要提交以下材料完成报名： 电子材料： 1：上海交通大学电力九零校友爱心奖学金申请表。请认真填写附件中的申请表，并在申请表的右上角黏贴一寸照片。命名时，为“申请表_XX”其中XX为你的名字。涉及学积分处，统一填写核心课程学积分。 2：申请书。请参照附件中电力90申请书模板。称谓处不要修改。电子版签名处直接打字上去。 3：一张能看清正脸的个人电子照片。 将这三份材料打包压缩，压缩包命名为“电力90_姓名_学号”。发送至邮箱zhuguan104@163.com，邮件命名为“电力九零奖助学金申请+姓名+学号+班级”。如有疑问，也可邮件咨询。 纸质版材料： 将上述的1、2两项材料打印，其中： 在1申请表的右上角黏贴个人彩色一寸照片，涉及签名处需手写签名日期。 在2申请书最后要手写签名和日期。 纸质版提交至电信群楼3-102助管处。 报名截止时间为12月13号星期三17:00。确定报名人选后学院将组织面试初评，交由基金会审核后确定最终获评人选。 电院学生办 2017.11.28 【2017】电力90助学金评选章程 上海交通大学电力九零校友爱心奖学金申请表 电力90申请书模板 访问数量：','2017-11-28 00:00','2017-11-28 00:00','','','学生办','2017-11-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(742315956,'【本科生】2017年上海交通大学电子工程系78级校友爱心基金评选通知','各位同学： 大家好！ 现开展2017年上海交通大学电子工程系78级校友爱心基金申请工作。 “上海交通大学电子工程系78级校友爱心基金”是由交通大学电子工程系78级校友捐建的,旨在帮助学院的困难本科学生顺利完成学业，坚定专业思想，成为社会发展和经济建设的优秀人才。每年资助10位同学，包括一等奖3人，每人8000元；二等奖7人，每人5000元。   申请者要求： 上海交通大学电子信息与电气工程学院，电子工程系二年级（F16级）、三年级（F15级）本科生： （一）热爱祖国，勤奋学习，立志为社会做贡献； （二）关心集体，乐于助人，积极参加公益活动； （三）遵纪守法，艰苦朴素，自强自立； （四）学习刻苦，综合测评成绩在年级排名前50%； （五）经济困难，在学校贫困生数据库中的贫困等级为一般贫困或特别贫困。 请有意愿申请上海交通大学电子工程系78级校友爱心基金的同学，需要提交以下材料完成报名： 纸质版材料： 1、申请书。参照附件一中的模板格式要求打印提交，在申请书的最后需手写签名和日期； 2、申请表。打印并填写附件二中的申请表。其中“上学年综合测评与专业排名”一项，请留白，由学院统一填写。 纸质版提交至电信群楼3-102助管处。 报名截止时间为12月13号星期三17:00，报名结束后，学院将从申请同学中初评出15名候选人，再由电子工程系78级校友基金会评选出10名获奖同学。   电院学生办 2017.11.28 附件一、78级校友爱心基金申请书模板 附件二、上海交通大学电子工程系78级校友爱心奖学金申请表   访问数量：','2017-11-28 00:00','2017-11-28 00:00','','','学生办','2017-11-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1936238901,'第十八期触电论坛——AI时代下的工程师之攀登珠峰之路','第十八期触电论坛将于2017年11月29日(周三) 19时召开，地点为微电子楼报告厅。 本期嘉宾：依图科技研发总监 周健 周健，依图科技研发总监；上海交通大学计算机系学士、硕士；亚洲首个世界大学生程序设计竞赛世界冠军。他于2006年加入Google，负责中文网站搜索质量优化；后在阿里云、MediaV担任研发经理、总监，主要从事分布式系统研发。 活动简介： 依图科技从事人工智能创新性研究，致力于将先进的人工智能技术与行业应用相结合，建设更加安全、健康、便利的世界。 依图参与人工智能领域的基础性科学研究，致力于全面解决机器看、听、理解的根本问题，在计算机视觉、自然语言理解、知识推理、智能硬件、机器人等技术领域取得了突破性进展。依图的技术已经服务于安防、金融、交通、医疗等多个行业。发展近 4 年来，依图科技的产品已经应用到全国二十多个省市地区的安防领域。安防领域之外，依图也进入智慧城市领域和健康医疗领域，协助政府构建"城市大脑"，缩小医疗领域的巨大知识鸿沟，改善医患体验。 报名参加本次活动的电院学生，本科生获得1161项满分素拓，研究生记录参加讲座一次 报名链接：https://tongqu.me/act/16252 访问数量：','2017-11-27 00:00','2017-11-27 00:00','','','学生办','2017-11-27 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-80698224,'不忘初心跟党走，不断拼搏勇攀登——重走历史，嘉兴南湖之行','11月19日，电院B16硕士电子第二学生党支部党员从学校出发前往浙江嘉兴南湖，开始南湖红色学习之旅，重温往昔峥嵘岁月。 1921年7月，中国共产党第一次全国代表大会因上海法租界巡捕的袭扰，被迫于8月初转移至南湖一条游船上继续举行，代表们在游船上讨论通过了中国共产党的第一个纲领和第一个决议，选举了党的中央领导机构，宣告中国共产党成立，南湖、烟雨楼由此成为中国共产党诞生地。党支部的同学们怀着敬仰的心情参观了南湖中共一大会址红船，切身体会到中国共产党成立的艰辛，认识到早期共产党人对于民族独立、人民解放的历史担当。 随后，我们前往南湖革命纪念馆参观。在宣誓墙下，支部党员在党的诞生地重温入党誓词，嘹亮的誓言喊出了同志们对党的热爱，更表达了今后认真学习工作的目标。 纪念馆生动再现了中国共产党创建的历史背景、艰难过程以及对于实现中华民族伟大复兴中国梦的历史贡献和现实意义。通过参观学习，全体党员经历了一次党性教育的洗礼，对中国共产党诞生初期的历史有了进一步的认识和更加深入的了解。 经过这一次南湖红色学习，同学们感慨良多，通过对共产党早期艰苦斗争历史的深入学习和了解，支部党员更加深刻认识到今天美好生活的宝贵。支部党员表示要不忘初心，坚定理想信念，以革命前辈为榜样，在学习科研上严格要求自己，不断拼搏，勇攀高峰，为实现“两个一百年”奋斗目标做出自己的贡献。 作者：许兴华 摄影：陈梦仪 访问数量：','2017-11-27 00:00','2017-11-27 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20844','学生办','2017-11-27 00:00:00.0');
