insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2094061784,'关于上海交通大学电子信息与电气工程学院硕士毕业生申报优秀毕业生通知！（2018届研究生）','关于上海交通大学电子信息与电气工程学院硕士毕业生申报优秀毕业生通知！（2018届研究生） 各位2018届硕士毕业生， 目前开始评审优秀毕业生，优秀毕业生是学生就读期间最高荣誉之一，而且班级的名额与有效申报人数成正比，因此请符合条件的同学积极申报。 优秀毕业生评审采取学生申报、班级评选、学院审核的方法，由学生自行申报，提交申报材料，学院审核材料后按照班级申报的人数分配名额，班级召开班会，进行无记名投票确定获评同学。申报西部就业名额及参军退伍名额的同学，学院评选小组根据申报者材料择优评审。详情请见附件1《上海交通大学电院硕士研究生优秀毕业生评选条例及日程安排》。 评选流程的时间节点如下 1、填写表格提交材料 11月22日~11月30日下午5点，学生填写附件2《上海交通大学电院硕士优秀毕业生申报表》并请导师填写推荐意见。同时，登录同去网，填报申报信息。 同去网网址: https://tongqu.me/act/16169 11月30日下午5点前将申报材料提交至电院3-107事务部助管处，纸质材料包括： （1）申报表 （2）申请西部就业指标的学生需提交三方协议复印件 （3）申请参军退伍指标的学生需提交相应证明 不提交纸质材料视为无效申请 2、评审西部就业名额以及参军退伍名额，并分配班级名额 12月1日~5日，学院审核申报者材料，择优评审出西部就业指标以及参军退伍指标的获评学生。根据各班有效申报人数分配班级名额，申报市优失败的同学自动转入申报校优的序列。申报西部就业指标以及参军退伍指标的学生如果申报失败，符合条件的同学自动转入班级评审序列。提前毕业的学生视为一个班。 3、班级评审出候选人 12月6日~10日，学院公布西部就业指标以及参军退伍指标获评名单，公布各班级候选人名单以及名额。各班班长召开班会进行无记名投票，注意候选人不得担任监票人。申报市优失败的同学，自动进入申报校优的候选人名单。班长于12月10日下午5点前将《电院硕士优秀毕业生班级评选结果上报表》填写完毕，班长和党支书（或团支书）签字后提交至电院3-107事务部助管处。 4、名单公示，完成后续材料填报 12月11日~15日下午5点，学院在学生办网站公示所有初步获评者名单，获评同学填写《优秀毕业生登记表》，获评市优毕、西部就业和参军退伍的学生还需撰写成长成才故事等其他材料。西部就业名额的同学还需提交《承诺书》。不按照要求准备相关材料，学院有权取消获评资格。 5、','2017-11-22 00:00','2017-11-22 00:00','','','学生办','2017-11-22 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1644935097,'电院2017级新生党员启航大会暨第六期星火培训班开班典礼举行[图]','10月21日下午，电子信息与电气工程学院2017级新生党员启航大会暨第六期星火培训班开班典礼在闵行校区菁菁堂举行。电院党委书记苏跃增为全体2017级新生党员带来题为《初心承星火，砥砺再启航》的第一堂党课，电院全体思政教师及研究生新生党员、第六期星火培训班学员参加。 校党委书记姜斯宪在2017年秋季学期干部大会上强调，大力弘扬“学在交大”，要全面深入推进“立德树人、教书育人”的理念。目前，电院正在积极营造浓郁的理论学习氛围，切实有效地推进基层党组织建设。苏跃增鼓励全体党员认真思考人生并塑造积极的人生观和价值观，他要求党员同志要深入学习党的十九大报告，强调党员骨干要发挥带头作用，创新形式，组织开展形式多样的党日活动。现阶段，电院将结合自身大电类专业特色，发挥育人主体作用。十一月，院长毛军发也将为电院全体新生带来精彩讲课。 苏跃增首先以习近平总书记在中国共产党第十九次全国代表大会报告中提出的“中国共产党人的初心和使命，就是为新中国人民谋幸福，为中华民族谋复兴”的重要论断，引出党课的核心问题：为什么中国共产党可以承担起民族复兴的历史重任。 为回答这个核心问题，苏跃增从四个方面进行了阐述：马列主义为何在中国广泛传播、为何中国最终选择了共产主义、红色革命政权为什么能够存在以及中国共产党何以有持久的生命力。 马列主义为何在中国广泛传播 对于马列主义为何在中国广泛传播的问题，苏跃增阐述了从明朝灭亡到鸦片战争的历史背景，认为新文化运动的兴起、工人阶级的壮大、马列主义与传统儒家和墨家文化的契合是传播的内因，而十月革命、世界工人运动澎湃、甲午战争后日本的影响是传播的外因。 马列主义在中国的广泛传播，大大启发了人们的思想觉悟，促进了人们的思想解放和爱国运动的开展，增强了工人阶级斗争的信心，推动了马克思主义与中国革命实际的结合。极大程度上促使了中国工人阶级登上了政治舞台，推动马克思主义中国化的历史进程，加速了“毛泽东思想”的产生。 为何中国最终选择了共产主义 苏跃增指出，中国对共产主义从认识到承认的过程是漫长而曲折的，实践证明选择共产主义是正确的道路。 中国共产党成立已有96年，一部中国共产党的奋斗史,就是为实现中华民族伟大复兴而奋斗的历史，中国共产党是中华民族伟大复兴事业的推动者、领导者、组织者和实施者。在党的坚强领导下，沿着中国特色社会主义道路不断前进,“两个一百年”的奋斗目标、中华民族伟大复兴的中国梦就一定会实现。 红色革命政权为','2017-11-21 00:00','2017-11-21 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20798','学生办','2017-11-21 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1311933872,'电院第二届“星火杯”党建知识竞赛暨第六期星火培训班结业典礼顺利举行','2017年11月19日，电院第二届“星火杯”党建知识竞赛决赛暨第六期星火培训班结业典礼于微电子大楼报告厅拉开帷幕。电院学生党总支书记田怡萌与其他党建指导教师应邀出席。电院2016级本科生王志辉和朱颖担任主持人。 本期星火培训班正值党的十九大圆满召开，培训班通过多次专家授课和小组学习，以丰富的课程形式引导学员认真学习十九大精神，在新时代展现新气象与新作为，得到了学员的强烈反响。 在结业典礼上，来自第18组的2015级本科生吴飞圣作为优秀组长代表发言。他分享了自己在本次星火培训班上学习的感悟，并倡议各位学员不忘初心、牢记使命，用实际行动践行党的十九大精神，以身作则向身边人传递红色正能量。 “星火杯”党建知识竞赛将此次活动带向高潮。比赛共分为三个环节，分别对应三种题型：必答题、抢答题和“求生之岛”淘汰题。题目内容包罗万象，主要包含党史军史、时政要闻、红色经典影视剧等多方面的内容。经过前期初赛选拔，来自本科生、研究生的24名选手最终闯入决赛，分为A、B、C、D共4个战队参与角逐。参赛选手思维敏捷，积极抢答，各组之间竞争激烈，现场气氛十分热烈。 在第一轮的必答题环节中，每队轮流作答。各队都表现出扎实的党史知识，回答坚定自信，为后两轮的比赛奠定了坚实的基础。而第二轮抢答题，每队答对一题得20分，答错扣10分。实力与运气并重的抢答题使比赛逐渐达到高潮，比赛的优势方一度改变。 在最惊心动魄的第三轮，每队答错需淘汰一人，题目难度逐渐增加。淘汰赛中新增“警长指定作答”规则，即答对上一道问题的战队可在无人抢答时指定其他战队作答，这一设置极大地增添了竞赛的悬念与趣味性。各支队伍愈战愈勇，比分交替上升，比赛进入白热化阶段。最终B队凭借自己的知识储备和果敢抢答，以微弱优势赢得胜利。至此，本次知识竞赛决赛的名次全部决出。 活动最后，在场嘉宾为获奖战队颁奖。至此，本期星火培训班正式结业。 据悉，“星火培训班”作为电院在考核、选拔入党积极分子方面提出的创新举措，至今已历经三年多时间，并逐渐形成了“理论学习+小组实践”的成熟培养体系。在对课程的积极探索中，“星火杯”党建知识竞赛也已成为培训班的品牌项目，进一步增进了入党积极分子对党的非凡历程、光辉成就的认识和了解，营造了浓厚的学习氛围。通过知识竞赛的形式，学员们在进一步了解国家、读懂党章党史的同时，收获了友谊与欢乐，也增强了作为新时期中国青年的使命感。 文字：潘宇杰 摄影：沙金锐 访问数量：','2017-11-21 00:00','2017-11-21 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20794','学生办','2017-11-21 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1282864771,'庆祝党的十九大顺利召开——电院“情溯历史，志励未来”主题定向越野活动顺利举办','11月18日，电院学生党建研究会负责主办的“情溯历史，志励未来”主题定向越野活动如期举行。电院关工委老师袁廷亮、王绍平、陈洪亮和薛震民出席并作了动员讲话，活动由袁廷亮老师宣布正式开始。此次活动吸引了270名同学参加，共组成27个参赛队，角逐最终的胜利。 定向越野的路线由八个地点串联而成：全面动员、勇往直前、一带一路、精准攻坚、走进历史、党路烽火、瞬间记忆、我说你答。活动的目的是庆祝和献礼党的十九大，丰富电院新生党员和同学们的课余生活，同时，通过对党的重大事件、重要代表大会的了解和学习，培养广大党员和同学的爱党和爱国情怀，增强其党性修养。 各个队伍聚集电院3号楼前空地，关工委袁廷亮发表动员讲话，并宣布活动开始。同学们开启了定向越野的旅程，此次定向越野不仅考验着选手们党史知识，也考验着大家的智慧与体力。 全面动员活动地点为致远湖桥东草坪，每次两组队伍猜拳前行，同学们在活动过程中对1921年至2017年间党的历史上重要事件进行了回顾。勇往直前活动地点为致远湖桥西草坪，通过该游戏环节，同学们了解了世界上与中国友好合作的国家。一带一路活动地点为致远湖桥北转盘，形式为小组一起跳长绳，同学们不仅培养了团队合作精神与默契，同时也锻炼了身体。精准攻坚活动地点为软件楼南草坪，同学们对中国各个省份贫困县的分布有了进一步认识。 走进历史活动地点为东下院教室，该活动以拼图的形式呈现，同学们需要对历届党的代表大会重要事件有所了解，才能更好地完成拼图。党路烽火活动地点为东上院大厅，同学们通过在地图上辨识党的历史中重要的城市，学到了很多党的历史知识。瞬间记忆活动地点为下院教室，同学们对历届党的代表大会重要事件进行记忆及问答。我说你答活动地点为上院教室，该活动要求同学们回答给定事件发生于哪次党的代表大会。 比赛进行了一个多小时后，陆续有队伍回到起点。最终，活动积分较高的小队获得奖励，关工委的老师们为获奖小队的每一名同学颁奖。参赛同学表示，本次活动给他们留下了许多深刻的印象与值得珍藏的片段。活动通过各个趣味性游戏，增进着团队力量，也增进着大家对于党文化的了解，进一步培养了同学的爱党和爱国情怀。 作者：王岚 摄影：龚恒 访问数量：','2017-11-20 00:00','2017-11-20 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20783','学生办','2017-11-20 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-971485601,'【本科生】港澳台侨学生奖学金申请通知','各位同学           2017-2018学年港澳台侨学生奖学金项目申请现已开始，具体情况如下： 1. 参评条件： 在校港澳台、华侨学生； 热爱祖国，拥护“一国两制”方针，拥护祖国统一； 遵守国家法律、法规，遵守学校各项规章制度； 诚实守信，有良好的道德修养； 入学考试成绩优秀或在校期间勤奋刻苦、成绩优良。 2. 金额：本科学生:特等奖每生每学年8000元、一等奖6000、二等奖5000、三等奖4000； 3. 时间节点：11月20日下午15:00 4．名额分配：全院本科生台湾学生5人，港、澳、侨学生共3人，按照综合测评成绩评选 4.注意事项： 严格确认学生符合参评条件； 严格遵守时间节点； 不同类型学生填写不同申请表； 5. 材料要求： 电子版： 《奖学金申请表》、《上海交通大学港澳台侨学生奖学金通用汇总表》 以“姓名+港澳侨奖学金”命名，发送到seieejiangxuejin@126.com 纸质版： 《奖学金申请表》以及申报奖学金的支撑材料 提交到电信群楼3号楼102助管处。 附件3.上海交通大学港澳台侨学生奖学金通用汇总表 附件4：奖学金申请表 访问数量：','2017-11-17 00:00','2017-11-17 00:00','','','学生办','2017-11-17 00:00:00.0');
