insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(155024778,'学习贯彻党的十九大精神——2017年全国科学道德和学风建设宣讲教育报告会','为学习贯彻党的十九大精神、“科技三会”精神和习近平同志关于科学道德和学风建设的重要指示精神，中国科协、教育部、中科院、社科院、工程院、自然科学基金委定于11月14日下午15:00-17:30举办“学习贯彻党的十九大精神——2017年全国科学道德和学风建设宣讲教育报告会”。报告会邀请李晓红、邱勇、周琪等三位院士作报告。我校设立校级主会场和院级分会场进行直播。 讲座时间：11月14日15:00~17:30 讲座地点：新行政B楼418会议室 报名对象：电院全体全日制学生 领票地点：电信群楼3-109办公室 领票时间：11月14日9:00~11:30 讲座报名请访问同去网报名链接： https://tongqu.me/act/16101 访问数量：','2017-11-13 00:00','2017-11-13 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=20698','学生办','2017-11-13 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1249960054,'电院研会“程序员节”活动顺利展开','2017年11月13日，上海交通大学电子信息与电气工程学院研究生会在东转举行了“程序员节”活动。此次活动旨在为广大师生带来快乐，从繁重的工作学业压力中释放出来，丰富校园文化生活。 本次活动的形式是以小游戏闯关为主，在关卡设置上融入程序设计方面的内容，共开设了筷子大战、代码挑战、“礼”尚往来、二人三足、你画我猜、背夹球、盲行、扔飞镖、24点、套圈领奖等关卡。 活动规则为两人一组进行游戏，比拼总体用时。在游戏中，各组分工合作，配合默契，团结一致，互帮互助。 活动受到参与者们的一致好评，均表示在游戏中能够体会到团队合作完成任务的愉悦，还可以减轻学习科研中的压力。 访问数量：','2017-11-13 00:00','2017-11-13 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=22777','学生办','2017-11-13 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1299445422,'关于举办“E7 Show”2017电子信息与电气工程学院个人风采大赛的通知','为促进电院学子的全面发展，进一步丰富校园文化生活，营造积极向上、健康文明的校园文化氛围，为电院学子搭建展示才华的舞台，经研究决定，举办“E7 Show”2017电子信息与电气工程学院个人风采大赛。现就活动的具体内容通知如下： 一、  活动时间 报名：11月9日 ~ 11月19日 初赛：11月25日 ~ 11月26日 决赛：12月6日 二、  参与对象 上海交通大学电子信息与电气工程全体本科生、研究生。 三、  活动规则 （1） 表演形式 包括但不限于声乐、器乐、舞蹈、语言艺术，允许多人组合表演。 （2） 初赛形式 每位参赛选手准备不超过1分钟的自我介绍与不超过3分钟的节目表演，要求主题积极向上，能淋漓展示自身特色。 （3） 决赛形式 根据初赛表现，选拔10名选手入围决赛，入围决赛的选手准备不超过10分钟的节目表演，根据现场评委评分、观众投票评选出冠亚季军、最佳人气选手。 （4） 奖励机制 本科生参与初赛记一次1142项素拓，若入围决赛再记一次1142项素拓（满分）、获奖则记一次212项素拓（硬加分）。 研究生参与初赛记一次党团素拓，若入围决赛再记一次党团素拓。 所有获奖者将获得一定金额的物质奖励。 四、  报名方式 同去网：https://tongqu.me/act/16060 在报名后，请准备好初赛中使用的音频、视频等媒体文件，以“初赛材料+姓名+手机号”为主题发送至本次活动专用联系邮箱super_seiee@sina.com。 五、  其他 （1）请关注电院研会公众号（seiee_graduate）及时获取进一步通知。 （2）联系人：金悦媛（18814122767）、杨思嘉（15026683875）。 共青团上海交通大学电子信息与电气工程委员会 上海交通大学电子信息与电气工程学院研究生会 共青团上海交通大学电子信息与电气工程委员会宣传部 2017年11月9日 访问数量：','2017-11-10 00:00','2017-11-10 00:00','','','学生办','2017-11-10 00:00:00.0');
