insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-501975974,'腾讯科技·游戏开发公开课分享会','为了给学生提供更全面的游戏研发课程指导，腾讯游戏学院将联合上海交通大学工程训练中心于2017年下半学年开设游戏程序选修课程，从程序开发的角度剖析游戏制作的过程。为了方便广大同学更加深入地了解游戏研发，同时对中国的游戏行业有更深刻的认识，我们特地邀请了腾讯游戏学院副院长Jim,来为广大同学介绍游戏开发、游戏策划的相关情况。 时间：2017年9月19日 19:30 地点：上海交通大学工程训练中心思源楼200号 主讲嘉宾： Jim，刘子建，2003年5月加入腾讯。程序员，程序经理、主策划、制作人，执行制作人、自研体系管理部助理总经理、腾讯游戏学院副院。1997年进入游戏行业，程序员，专案经理。QQ幻想、QQ仙侠等游戏的主策划人。   同去网报名链接：https://tongqu.me/act/15573 报名参加本次活动的电院学生，本科生获得1161项满分素拓，研究生记录参加讲座素拓一次。 访问数量：','2017-09-15 00:00','2017-09-15 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=19760','学生办','2017-09-15 00:00:00.0');
