insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1609932575,'关于2017届毕业生求职创业补贴发放的相关通知','一、发放对象 本校毕业年度内有就业创业意愿的以下学生： （一）享受城乡居民最低生活保障家庭的毕业生； （二）残疾毕业生； （三）就读期间获得国家助学贷款的毕业生。 以上皆指全日制学生，不包括已确定升学、出国留学的毕业生。 二、发放原则 坚持自愿申请、公开公正、属地管理、专款专用、及时高效的原则。 三、补贴标准 补贴标准为每人1000元，每人限领一次。 四、申请材料 1、《高校毕业生求职创业补贴申请表》（见附件2。注：学校学生资助管理部门审核盖章处留空，校就业中心收齐后统一提交校学生事务中心审核盖章） 2、本人身份证复印件正反面，贴在申请表背面； 3、个人银行账户复印件正面，贴在申请表背面（学校统一办理的银行借记卡复印件，复印件要能看清卡号，或卡号在复印件空白处另写并签字）； 4、享受低保的毕业生需提供所在家庭享受城乡居民最低生活保障的证明；残疾毕业生提供残疾证复印件；获得助学贷款的毕业生提供助学贷款合同的重要页面复印件。（具体要求必须按照附件4《求职创业补贴发放工作图文操作细则》严格操作，不符合要求的证明将会导致申请失败。） 5、在线填写《上海交通大学2017届毕业生求职创业补贴申请汇总表》，请务必保证填写正确。线上汇总表链接如下： https://jinshuju.net/f/NHhvtC 五、申请程序 1、请在2月21日前提交纸质版申请材料至电院就业办（电信群楼3-107）。 2、由校就业中心将通过初审的申请人员名单在校内公示5个工作日。公示无异议的申请人材料由校就业中心统一上报给闵行区人力资源和社会保障局。 就业中心联系人：李 想(13524740000转686049) 倪邦辉(13524740000转62311) 电院就业办指导老师：林玲 34205252 电信群楼3-107 交大2017年上海市求职创业补贴申请QQ交流群：538589074 求职创业补贴申请通知附件 访问数量：','2017-01-14 00:00','2017-01-14 00:00','','','学生办','2017-01-14 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1757147035,'2017年寒假春运志愿者招募','2017年，上海火车站春运志愿服务项目如期而至，将在1月23日、24日两天举行，每天需求志愿者10-15人，具体情况如下：  【服务时间】 2016年1月23日8：00-12：00（暂定）   2016年1月24日8：00-12：00（暂定） 【服务地点】 上海火车站 【服务内容】 1、协助维持火车站秩序 2、为外地游客提供交通咨询  3、答疑解惑，帮助购买火车票、使用自助式设备 4、如遇火车晚点之类的问题要安抚乘客情绪 【报名需求】 1、态度端正，具有服务意识，礼貌对待每位乘客 2、听从小组长安排指挥  3、不得擅自迟到早退 4、保管好志愿者服装，及时完好归还  5、保护好自己的通讯设备，保障通讯畅通  6、注意安全，春运期间火车站人员复杂，避免发生争执冲突  7、尽量不要单独行动 8、口齿清晰，表达清楚，有耐心 【报名方式】 同去网： http://tongqu.me/act/14207 【其他】 1、志愿者需自行承担路费，自备当日午餐 2、电院本科生：提供106项志愿者素拓、1051项寒假社会实践素拓（如为活动供稿、摄影，本项将予高分，并考虑参评寒假社会实践优秀个人） 3、电院研究生：记一次党团活动素拓 访问数量：','2017-01-13 00:00','2017-01-13 00:00','','','学生办','2017-01-13 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(327117039,'电院“红色正能量企业行”系列活动顺利开展','12月底，电院学生党总支、电院职业发展中心联合举办，电院学生党总支发展部承办的“红色正能量企业行”活动暨电院第四期星火培训班“生涯规划”专题社会实践顺利开展，电院第四期星火培训班学员共300余人次参与活动。本次企业参观系列活动选取了和电院学生就业息息相关的重点行业和关键领域的六个重要企业，吸引了包括电院机关各办公室老师以及本硕博学生的积极参与，取得了热烈的反响。 本次活动一共组织了六场企业参观，包括微软、华为、腾讯等热门的就业去向企业，也包括上海电信、中船工业708所和航天八院等国家重点行业的重要企业。 活动期间，各企业都向同学们充分展示了自己的企业文化和经营理念，并组织了企业管理人员和在企业就业的交大电院校友与同学们面对面交流。其中，航天八院与同学们分享了航天人的故事和梦想，中船工业708所带同学们参观了所荣誉室，为同学们展示了中船人对国防事业和国家经济建设的卓越贡献。 活动结束后后，同学们积极反馈，表示这次企业参观活动给了他们能够真正走进企业、了解企业，特别是一些重点行业的企业的宝贵机会；同时，这样的活动也为他们在今后的人生规划和职业选择进一步提供了方向。 据悉，本次活动是电院入党积极分子星火培训班组织专题实践课程的一次重要尝试。今后，星火培训班还将继续扩宽思路，大胆尝试，为创新入党积极分子培养培训模式，进一步探索学生党建工作发展而继续努力。 作者 曹原 摄影 曹原 访问数量：','2017-01-11 00:00','2017-01-11 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=18340','学生办','2017-01-11 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-127206924,'2017上海市青少年单片机应用活动志愿者招募','   由上海市科艺中心、上海交通大学TI技术中心及沪上多所中学联合举办的上海市青少年单片机应用活动于每年寒、暑两期（1月、7月）定期举办。今年，第四期上海市青少年单片机应用活动将于1月16日如期开展，拟招募志愿者25名担任赛事裁判，具体情况如下：     【服务时间】 2016年1月16日全天（暂定7：00 - 16：00） 【服务地点】 上海市吴淞中学（早晨统一从交大包车出发）   【服务内容】 赛事裁判。有一定程序设计基础，能够准确评判单片机输出是否正确，判断单片机编曲声音是否正确等。   【人数需求】 25人   【报名需求】 认真负责，不允许中途退出 有往届参与经历者优先 【报名方式】 同去网： http://tongqu.me/act/14190   【其他】 提供午餐 志愿者补助250元/日 访问数量：','2017-01-04 00:00','2017-01-04 00:00','','','学生办','2017-01-04 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2008200495,'面向2014级本科生、2015级硕士免费计算机JAVA、嵌入式应用-ARM 、电子商务培训通知',' 为了提高同学们的就业能力，电院就业办特别邀请专业培训机构进校为同学们开展计算机技能培训。该项目在交大闵行校区已连续举办十期，先后有超过1000名应届毕业生参与培训并获得相应职业资格认证。 根据《上海市居住证积分管理试行办法》第二章第六条规定，持证人（上海市居住证）取得大学本科学历和学士学位，积90分；取得技能类国家职业资格三级，积60分。因此，凡取得国家职业资格证书（高级证书）的均可积60分，有助于本科生落户上海，国家机关事业单位和企业作为招聘及加薪的依据，申请出国留学等用途据。 基于上述情况，为了鼓励学生获计算机程序设计员（JAVA）、计算机程序设计员（嵌入式应用-ARM）和电子商务师国家职业资格证书，举办免费培训考试， 特别说明：本次共三项免费培训活动，分别是：计算机程序设计员（JAVA）、嵌入式应用-ARM培训和电子商务师培训，每位同学只能选择参加其中一项免费培训；若以前参加过电院举办的相关培训，则本次培训活动不享受免费。 以下是同学们关心的问题： （1）关于上课地点和时间安排 答：上课地点安排在电信群楼4号楼；本次安排平日班（周二、周四晚上）和周末班（周六或周日其中一天）两种班级；平日班一次课为3月14日开始；周末班为3月18日开始。 （2）计算机培训班要上多长时间的课？ 答：安排8次课（具体在上课第一天发放课表，按课表上课）。 （3）什么时候考试？ 答：考试时间集中安排在2017年9月份（有特殊情况可以安排在8月或10月份，根据个别学生对考试时间有要求的，可以安排合适的考试时间）。 （4）这个考试难吗？ 答：电院参加学员历年通过率达到95%，只要你用心参加上课，准备考试复习，考试通过不是问题。, （5）考试证书 ？ 通过考核鉴定的学员可以同时获得由国家人社部颁发的证书。     报名事项： 【参加对象】面向2014级本科生、2015级硕士研究生； 【报名地点】电信群楼3-107室助管处报名； 【费用情况】在校生共计300.00元／人的押金 退押金须知： 第一次考试合格者退还300元； 第一次考试不合格者和缺考者参加补考，补考成绩是否合格都退还300元（补考费个人无需承担）； 第一次考试不合者和缺考者不参加补考300元不予退还。     下面内容为三次免费培训的详细介绍                      计算机程序设计员（JAVA）培训项目 一、 计算机程序设计员（JAVA） 认证背景 IBM','2016-12-27 00:00','2016-12-27 00:00','','','学生办','2016-12-27 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1903374692,'“探学科前沿，立专业使命” ——电院学生党员参加2016全球室内位置服务产业峰会','      12月15日，电院组织研究生党员参加首届全球室内位置服务产业峰会学习交流，本次峰会在上海国际会议中心举行。 众所周知，人们活动的室内空间越来越庞大复杂，兼之智慧城市建设、移动互联和大数据产业的快速发展等，使室内位置服务的需求前所未有的高涨。近年来，国务院、科技部、工信部下发的多项指导政策，如《导航与位置服务发展科技发展“十二五”专项规划》、《室内外高精度定位导航白皮书》等，均提出要大力推动室内定位系统的发展。 本次峰会以“共生态，享合作”为主题，邀请到了腾讯、阿里、百度等诸多企业室内服务项目的负责人进行技术介绍交流，旨在促进室内外位置服务全产业链的企业合作，加快上海新兴产业聚集发展和智慧城市建设进程。 参与本次峰会的同学纷纷表示，听了业内专家的介绍，对于室内服务技术的原理和应用场景有了更加全面深刻的认识，今后将关注并进一步学习室内服务技术。作为交大学子，电院研究生党员，有必要紧跟技术风向，了解行业最新进展，学习前沿知识，拓宽领域视野，提升自身能力。 【作者】许丁杰 【摄影】卢宙 访问数量：','2016-12-21 00:00','2016-12-21 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=18198','学生办','2016-12-21 00:00:00.0');
