insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1280592568,'Google杯创业计划大赛决赛观众','Google杯创业计划大赛决赛将于下周三（12月14日）13:30在陈瑞球楼100号举行，欢迎同学们报名参加！ 参与的电院本科生将获得1161项满分素拓，研究生获得一次讲座素拓。 请点击同去网链接报名：http://tongqu.me/act/14108 根据《国务院办公厅关于深化高等学校创新创业教育改革的实施意见》（国办发〔2015〕36号）的要求，为深入实施大学生创业训练计划，扩大创业教育覆盖面，促进创业项目的培育、支持和发展，上海交通大学创业学院和校团委于2016年10月启动了第18届上海交通大学学生创业计划大赛，本届大赛由谷歌信息技术（中国）有限公司赞助支持，这也是我校自1999年以来，连续第18年举办学生创业计划大赛。 自大赛启动以来，共有19个院系的86个团队300多名同学报名参赛，参赛团队数量和人数均创历年之最。大赛分为“计划类”、“实践类”和“公益类”三个组别，参赛项目基于“互联网+”和硬件制造等技术基础，涉及生活服务、公益、教育、科研、医疗和社交等多领域。 大赛采用“自愿报名—参赛辅导—辅导过程遴选20支团队参加半决赛—半决赛遴选10支团队参加决赛—决赛产生金银铜奖”的赛制。 访问数量：','2016-12-10 00:00','2016-12-10 00:00','','','学生办','2016-12-10 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1664184719,'2017届硕士优毕名额分配及候选名单','各位同学 经过评审，许克路同学获评校优（参军指标），西部指标无人申请，具体申请人及名额分配见附件。 需要说明的是： （1）级点不足2.3的为无效申请 （2）无学术论文录用或发表的为无效申请 （3）申请市优的学生，如果投票落选，自动转为班级校优候选人 （4）请各班按照名额进行投票，12月16日周五下午5点前将附件“3、电院硕士优秀毕业生班级评选结果上报表”填写完毕，班长党支书签字后交至电院3-109张小丽老师处。 2017届优毕班级有效候选人及名额 (1) 3、电院硕士优秀毕业生班级评选结果上报表 电院研工办 2016年12月 访问数量：','2016-12-09 00:00','2016-12-09 00:00','','','学生办','2016-12-09 00:00:00.0');
