insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(243974029,'电院职业发展中心召开毕业研究生登记表制作专项工作会议[图]','12月2日中午，电院职业发展中心事务部在电信群楼3-107室针对毕业研究生登记表召开工作会议。职业发展中心辅导员卢宙以及事务部全体助管参加了本次会议。 本次会议的重点是部署有关2017届硕士研究生毕业研究生登记表的相关工作。卢宙从分班级发放、登记表回收与审核等方面详细介绍了关于毕业研究生登记表的工作流程。 会议结束后，事务部成员立马投入到毕业研究生登记表的相关工作中。当天下午，事务部助管同学开始了认真细致地分班级装袋工作。 本次电院研究生登记表制作面向2017届硕士毕业生，服务人数约1000人左右，继承了以往电院毕业生工作面向人数基数大的特点。研究生登记表会放入毕业研究生的档案中，对每个同学本人都有着重要意义。电院职业发展中心本着认真对每一位同学负责的工作态度，认真细化工作流程，力争为电院毕业生提供高质量，高效率，高水准的服务。 作者：解颖 摄影：解颖 访问数量：','2016-12-06 00:00','2016-12-06 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=17788','学生办','2016-12-06 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1134381872,'博士《毕业研究生登记表》填表注意事项','《毕业研究生登记表》填表注意事项（博士） 特别提醒: 1、请所有博士生正式答辩前到本人的思政教师处领取毕业研究生登记表。每人领取两份《毕业研究生登记表》及一份《填表注意事项》。 2、填写完后请到http://xsb.seiee.sjtu.edu.cn/xsb/info/8357.htm 下载电源博士毕业生人事档案寄档信息表（非常重要），根据上面要求与两份《毕业研究生登记表》一起交到电群3-107。 3、表内各项内容均需填写，没有的写“无”。 登记表封面： 【学校】上海交通大学 【系科】电子信息与电气工程学院 【专业名称】：按照研究生院学生基本信息相应项目内容填写。 【填表日期】：预答辩日期 第一页： 贴照片：本人近期一寸免冠彩色证件照。 【家庭出身】本人取得独立经济地位前的家庭阶级成分。“工人、农民、教师、干部、军人、知识分子”等。 【本人成分】填“学生”。 【籍贯】指祖籍。 【现在家庭住址】不能填写学校宿舍地址。应能具体到寄信能收到。 【何时何地入党（团）】请写清楚时间地点。 【本人身体健康状况】一栏填写有无疾病和体质强弱情况，如“良好”等。 第二页： 【本人简历】一栏自入小学起，依时间顺序填写。 【家庭成员】指直系亲属（亲兄弟姐妹以及父母）。 【主要社会关系】指的是密切往来的亲戚或其他主要社会关系。 【有何联系】可以写 “密切”等。 第三页： 【自我鉴定】四百字左右，不得太少（可以包含思想政治，学术科研，学习成绩，在校表现等几个方面）日期:预答辩日期。 第四页： 【班组（基层组织鉴定）】由班级负责人填写（班长等）并签其名字。班长已经毕业的可以由同导师实验室其他电院博士填写。自己不能写自己的！日期: 预答辩日期。 【学校（研究单位）意见】由班级负责人填写“同意班组意见”几个字，不要签名！时间：预答辩日期。交回学院后，由学院统一交学校签名盖章。 第五页： 【学校（研究单位）······的建议：】由导师填写（不得由自己填写 导师签字），日期：预答辩日期。 用黑色水笔填写（也要提醒导师用黑色笔填写），下笔请谨慎！ 负责人：刘淼 咨询电话：34205252 电院职业发展中心 电信群楼3-107 访问数量：','2016-12-06 00:00','2016-12-06 00:00','','','学生办','2016-12-06 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-287340067,'电院关工委换届大会暨第四期星火培训班开班典礼顺利举行','      11月30日下午，电子信息与电气工程学院关心下一代工作委员会换届大会暨第四期星火培训班开班典礼在闵行校区微电子学院大楼报告厅隆重举行。原校党委副书记、校关工委常务副主任蒋秀明，校关工委秘书长吴玉瑛，校党史校史研究室研究员龚诞申，学指委副秘书长、学生处副处长蒋立峰，学指委党建科科长张洋，以及电院党委副书记杨一帆，电院新老两届关工委成员，全体党建指导老师出席本次大会。大会由电院党委副书记李劲湘主持。       蒋秀明首先致辞。他结合自身经历，分享了学校关心下一代工作委员会的发展历史和优良传统，并勉励电院的关工委委员们要继续发挥余热，兢兢业业，为培养关心下一代事业继续努力工作，再创佳绩。       杨一帆宣读了电院关工委换届决定，并代表学院党委表达了对老一届关工委领导的崇高敬意和衷心的感谢。       蒋秀明向老一届电院关工委的老同志们赠送了礼物，表达对他们辛勤耕耘和丰硕成果的尊敬与感谢。       蒋立峰和杨一帆向新一届电院关工委成员颁发了聘书。       原电院党委书记袁廷亮作为连任的电院关工委委员对老一届关工委的工作进行了回顾，他赞扬了老一辈委员的认真工作与卓越贡献，并对新一届关工委的工作提出了更高的要求。       随后，电院关工委常务副主任王绍平为第四期“星火”培训班授旗，班长陈嘉梁接受班旗，第四期“星火”培训班正式开班。       接着，龚诞申为星火培训班讲授第一堂党课《讲交大故事，话党史国史》，对入党积极分子进行理想信念专题教育。他将交大的故事与党史、国史相联系，通过“一份奏折”、“一个理念”、“一张试卷”等十个有趣的小故事，将交大从建立到走向世界一流的历史故事和交大人为中华民族的伟大复兴作出的卓绝贡献娓娓道来，生动地展现了交大精神。       最后，学生党总支田怡萌介绍了第四期“星火”培训班的工作开展情况，包括培训班的举办目的，活动安排，参与要求和评优机制等，详尽介绍了培训的过程和目标，并对学员们给予了殷切的期望，学员们对培训班的总体和细节情况有了完整的了解。       大会在《歌唱祖国》嘹亮的歌声中落下帷幕。据悉，电院入党积极分子星火培训班作为学院平台承载学生入党积极分子培养教育任务的二级党校，已成功举办三期。本期星火培训班将以理想信念、责任担当、生涯规划为主线，践行社会主义核心价值观，培养适应时代要求的入党积极分子。 作者：吴幸融 陈嘉梁 摄影：吴承阳 郑棣 访问数量','2016-12-06 00:00','2016-12-06 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=17715','学生办','2016-12-06 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-392945224,'“弘扬长征精神，坚定党员理想信念”电院电气系博士生第二党支部召开专题组织生活会','      11月11日下午，上海交通大学电气系博士生第二党支部第二次党组织生活会在智能电网中心214会议室顺利召开，会议由党支部书记丁佳立同志主持。       会议第一项议程，在丁佳立同志的带领下，党支部成员认真学习中国共产党第十八届中央委员会第六次全体会议精神。十八届六中全会研究全面从严治党重大问题，制定新形势下党内政治生活若干准则，并修订《中国共产党党内监督条例（试行）》，全会高度评价全面从严治党取得的成就，以习近平同志为核心的党中央身体力行、率先垂范，坚定推进全面从严治党，为开创党和国家事业新局面提供了重要保证。       党支部各位党员同志针对十八届六中全会精神进行了热烈的讨论。陈根同志表示，这次会议与两学一做相统一，具有新的高度，并确立习总书记为核心，同时党员需要大局意识，考虑问题要全面，努力向党中央、习近平同志看齐。关于六中全会研究全面从严治党的问题，徐永鹏同志认为全面从严治党，体现了党的先进性，这也是党能带领全国各族人民实现一个又一个伟大胜利的重要保障。十八大以来，党中央‘打虎拍蝇’，人民看到了党反腐的决心和勇气，也更加坚定了党能带领人民实现中华民族伟大复兴的‘中国梦’的信心。桑顺同志从基层党员的视角出发，强调普通党员要努力做好自己的本职工作，多于基层联系，为社会多做贡献。徐军忠同志发言提出要贯彻学习习总书记的思想，做好党员的各项职责，为实现中国梦，两个一百年而努力奋斗。       会议的第二项议程，丁佳立同志带领党员同志学习习总书记纪念红军长征胜利80周年大会讲话。丁佳立同志向全体党员同志强调，长征是历史记录上的第一次，长征是宣言书，长征是宣传队，长征是播种机。长征以世界所罕见的艰难困苦，铸造了举世闻名的长征精神，成为中华民族百折不挠、自强不息的象征，我们要保持和发扬艰苦奋斗的作风，进一步坚定理想信念。共产主义理想和社会主义信念，正是共产党人崇高的追求和强大的精神支柱。       在长征精神的讨论中，支部党员各抒己见，从不同角度给予长征精神以新的含义。陈根同志向各位提出了“新长征”的时代精神，鼓励支部党员积极发扬新时代的长征精神，战胜困难，坚持道路自信和精神自信。桑顺同志结合长征题材的电视剧突出强调长征过程中付出的惨重代价，面对困难需要有革命乐观主义精神，发挥党员主观能动性。周中正同志将长征精神与科研相结合，指出博士生涯有波峰波谷，我们要迎难而上，坚持就是胜利，最终达成自己的学习目标。','2016-12-06 00:00','2016-12-06 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=17710','学生办','2016-12-06 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1257533178,'“弘扬长征精神，争做合格党员” 电院全体学生党支部召开11月专题组织生活会','      根据学校统一要求和学院“两学一做”学习教育计划，11月中下旬，电子信息与电气工程学院70余个学生党支部围绕“学习红军长征精神”、“学习十八届六中全会精神”等主题，开展了内容丰富、形式多样的专题组织生活会。既有专题讨论与交流，又有课外实践与体验，通过会议和活动相结合的形式，学生党员对长征的历程和百折不挠、自强不息的长征精神有了更深刻的领悟，对全面从严治党的战略内涵有了更透彻的理解。       博士生自动化第二党支部以“砥砺奋进，走好今天的长征路”为主题，在组织生活会上进行了集体学习和交流讨论。支部书记李肖城带领大家一同学习了“习主席纪念红军长征胜利80周年大会讲话”，习总书记就弘扬长征精神、走好今天的长征路，提出了六个方面的要求，同学们就此深入思考，重温伟大的长征精神。随后支部党员踊跃发言，就“今天的长征路”展开讨论，认为实现中华民族伟大复兴任重道远堪比长征，身为党员应该以坚韧不拔、百折不挠的长征精神磨砺自己，在新长征路上努力创造新业绩，为实现中国梦贡献自己的力量。       B16电子第一党支部、电气第三党支部等九个硕士支部积极参与了电院硕士生党建联席会举办的“忆峥嵘岁月，重走长征路”活动，通过“强渡大渡河”、“巧渡金沙江”等游戏环节，身体力行，团结合作，亲身体验长征历程的艰辛和不易，领会长征精神，收获颇丰。随后的交流环节中，支部成员纷纷表示此次活动不仅促进了支部内建，更让大家认识到，在生活、工作和学习科研中，应该铭记长征精神并发扬光大，刻苦攻坚、勇往直前，为中华民族伟大复兴付出自己的努力。       F13自动化测控联合党支部则将习主席讲话与聚焦从严治党的十八届六中全会结合起来，展开深入的讨论。与会党员积极发表自己的看法，方一维同学认为当今社会的青年人缺少一种忆苦思甜的品质，在物质并不匮乏的时代我们更应该发愤图强；谢泓材同学表示，作为学生党员，最重要的是坚守高校这个思想主阵地，努力将个人发展和祖国命运紧密联系，走好今天的长征路；何凡同学结合十八届六中全会从严治党的主题，认为反腐行动能够以儆效尤、收获民心，与此同时通过纪念长征活动发扬长征精神、树立榜样，这正是非常适时的举措。       据悉，电院70余党支部积极组织11月专题组织生活会，参与党员千余人。十八届六中全会提出了一系列全面从严治党的新观点、新要求、新部署，开启了全面从严治党的新阶段。时代变迁，但长征精神永不褪色，新的长征刚刚开始，','2016-12-06 00:00','2016-12-06 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=17705','学生办','2016-12-06 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1209839332,'首期电院学生党支书沙龙顺利举行','12月1日，为贯彻落实“两学一做”学习教育精神，加强电院基层党组织建设，电院第一期学生党支书沙龙活动在电信群楼3-100报告厅隆重举行。本次活动以“PPT制作讲演技巧培训”为主题，邀请了校教学发展中心业务室主任邢磊老师为学生党支书进行培训。全体党建指导老师也共同参与本次培训。 活动开始，邢磊首先以几个常见样例引入主题，从什么是好的PPT，如何评价PPT和怎样制作PPT三个方面深入浅出地讲解了PPT制作的关键要点。他强调，PPT应当服务于讲演者，尽量做到简洁、直观、图文贴合，避免文字堆积。随后，邢磊通过一些实例引导党支书们进一步领悟PPT制作的要领精髓。 经过邢磊老师的培训讲解，支书们感悟颇丰，就平日PPT制作过程中存在的困惑和问题与邢磊进行了交流讨论，邢磊逐一进行解答，并分享了一些实用的PPT制作素材网站。本次活动充分体现“两学一做”学习精神，贴近学生实际，有助于加强党支书工作能力。党支书们均表示受益匪浅，并会在之后的工作中学以致用，为学院的党建工作再创辉煌！     【作者】许丁杰，范晓宁 【摄影】王成鑫，欧阳自强 访问数量：','2016-12-05 00:00','2016-12-05 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=17698','学生办','2016-12-05 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-739128054,'你的论文难写吗？你的绩点想修的更高些吗？请点开','亲爱的博士、硕士研究生&各位同志们，是不是正在嗑盐呢？各位同学辛苦啦！ 为了让大家科研的道路更加充满趣味，我们博士生电气第一党支部发福利啦，一系列活动等你参加，活动内容如下： 1、和论文、科研&绩点大神面对面（科研大神发表SCI、EI多篇，绩点大神绩点3.05/3.3,真的都是大神哦，而且颜值超高,详情往下看）； 2、红色小游戏，重点是有奖品，有奖品，有奖品！！！重要的事情说三遍； 3、观红专电影《邓稼先》，懂得科研人的一颗爱国赤子之心。 是不是心动了呢？心动不如行动，赶快来参加吧！ 本周四（12/8）晚上18:30，电信群楼3-200等你来         各位，科研无国界，更没有系别，不论我们是否同一专业或者同一研究方向，你都可以来参加并聆听大神们的分享会！大家走过路过千万不要错过！ 各大神信息如下： 1、方斯顿 个人信息： 方斯顿，电力系统与自动化专业博士五年级学生，导师是程浩忠教授，研究方向为电力系统优化调度，目前以第一作者共发表、录用论文21篇，其中SCI论文6篇，EI论文15篇。 交流内容: 做科研的一些心得和体会 想说的话: 博士生涯的开始，总是伴随着很多迷茫，需要逐步从被动接受知识，变为主动学习知识，最后成为具有探索新知识能力的研究者，每一次的进步都可能伴随着阵痛和酸楚，但这每一次的进步也都伴随着蜕变与升华。一件事无论太晚或者太早，都不会阻拦你成为你想成为的那个人，这个过程没有时间的期限，只要你想，随时都可以开始。 要改变或者保留原状都无所谓，做事本不应该有所束缚，我们可以办好这件事却也可以把它搞砸，但我希望最终你能成为你想成为的人。 2、宁连营 个人信息： 宁连营，2015级电气专业硕士研究生，本科F1503008班主任，硕士B1503194党支部副支书，导师：邰能灵教授，研究方向：柔性直流输电控制与保护。现已申请公开发明专利4项，授权实用新型发明专利2项；其他什么也不说，只是我的绩点3.05（满分基点3.3）。 交流内容: 研究生课程学习与业余生活 座右铭： 孤独是一个人的狂欢，狂欢是一群人的孤独 3、刘剑 个人信息： 刘剑，2012级电气专业研究生，导师：邰能灵教授，电力系统继电保护方向。目前以第一作者发表/录用的论文13篇，其中：SCI其中3 篇（IEEE Transactions on Power Delivery 1篇，IET Generation, Transmission & D','2016-12-05 00:00','2016-12-05 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=17648','学生办','2016-12-05 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(803501726,'施耐德电气有限公司代表访问上海交大[图]','       11月24日下午，施耐德电气全球人力资源高级副总裁Sophie GRUGIER女士一行到访上海交通大学，校企双方在上海交大-巴黎高科卓越工程师学院504室举行会谈。施耐德电气的来访代表还有中国区人力资源副总裁张翼、中国区人力资源人才发展总监夏晴、全球人力资源共享中心招聘负责人张扬等。参与接待的校方代表有交大巴黎高科学院中方院长俞进、副院长李萍、院长助理陆佳亮，电子信息与电气工程学院党委副书记杨一帆、院长助理马艺馨、职业发展中心主任林玲、电气工程系副主任汪可友，电气工程系实验中心主任许少伦等。       俞进介绍了交大巴黎高科学院的基本情况，学生的国际化交流项目，国际化培养理念。交大巴黎高科学院是中法联合办学的特色学院，这一点与总部位于法国的施耐德电气公司非常契合。俞进表示，希望交大巴黎高科学院能够输出更多国际化的优秀学子到施耐德公司发展。       马艺馨介绍了电院的基本情况，并重点介绍了电院国际化办学的成绩。林玲介绍了电院毕业生就业情况，对电气工程系的同学的就业去向进行了针对性分析。林玲表示，希望能有更多优秀的电院毕业生选择在施耐德公司就业发展。        汪可友对系统介绍了上海交通大学电气工程系所辖的国家能源智能电网（上海）研发中心的基本情况，并介绍了电气工程系的重点实验室、研究概况和学生科研成就。汪可友说明了上海交通大学—施耐德电气联合实验室的情况以及目前与施耐德的合作内容，表达了希望与施耐德公司建立更深入持久的合作关系的意愿。        张翼介绍了施耐德电气179年悠久的历史，从19世纪的钢铁工业，20世纪的电力与控制到21世纪的能效管理，施耐德的发展紧随市场趋势。张翼介绍了施耐德与中国的情缘历史以及在中国的发展状况，表明今天中国已经成为施耐德电气全球第二大市场，并详细阐述了针对学生的管培生和新生训练营等项目。张扬介绍了施耐德的人才需求与人才培养计划，介绍了近年来交大毕业生进入施耐德的发展状况，对交大的毕业生给予高度肯定并提出更高期待。        会谈后，校企双方代表参观了位于智能电网大楼的上海交大—施耐德电气联合实验室。Sophie GRUGIER女士等观看了同学的现场实验，并与同学们进行亲切互动。        施耐德电气与上海交大有长期合作关系，在建立联合实验室、学生实习基地、校园创新活动等多方面都取得了很好的成绩，此次访问促进了校企双方进一步深入了解，有助于双方在更','2016-12-02 00:00','2016-12-02 00:00','','http://news.sjtu.edu.cn/_mediafile/newsnet/2016/11/27/2b4z1rq8rr.jpg','学生办','2016-12-02 00:00:00.0');
