insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1623704047,'电院召开本科生党支书座谈会 共话支部建设新篇章','11月29日晚，电院本科生党支部书记座谈会在电信群楼3-308会议室举行。自今年9月电院本科生党支部按专业纵向拆分重组以来，十四个党支部的首任支部书记第一次齐聚一堂，共话支部建设的新篇章。电院党委副书记李劲湘、特邀组织员王绍平、学生党总支全体党建老师、以及本科生党建联席会骨干成员应邀出席会议。 李劲湘首先分享了自己在大学期间担任党支部书记的经验体会，对在座的十四位本科生党支书提出了殷切期望。他强调，作为支部书记在党支部有着至关重要的作用，直接影响支部的凝聚力和支部文化氛围，要搞好支部建设，支部书记首先要率先垂范，在学习工作生活中不断培养家国情怀，这样才能做好一个党支部的带头人。 王绍平根据自己多年来的工作经验，指出了当前学生党支部中存在的问题，如组织生活重形式、轻实效，把组织生活当任务，完成了事，个别党支部书记充当老好人，开展工作过程中不够严肃。针对个别同学入党动机不纯的问题，王老师强调支部发展党员要坚持成熟一个、发展一个，决不能先发展、后考察。 随后，与会支书和老师就实际工作中遇到的问题进行了讨论。讨论议题包括如何提高党员参加组织生活的积极性，如何让党建活动与专业学习有更密切的联系，如何提高支部凝聚力和加强支部跨年级成员间的交流互助等。经过集思广益，对很多问题都提出了有效的解决方案，如可以通过开展学业辅导、高年级引导低年级同学进实验室、支部成员之间互相监督鼓励等系列活动来加强党建活动和专业学习之间的联系，增进支部跨年级的交流。 座谈会在活跃融洽的氛围中进行了近两个小时，座谈会结束之后每位党支部书记都收获了很多。电院本科生电气第一党支部书记郁琦琛表示，自己是第一次担任党支部书记，缺少工作经验，这次座谈会解决了自己之前对支部工作的很多疑惑，增强了对新支部组织建设的信心，希望以后能够定期举办类似的座谈会，支部之间互相学习，共同进步。 据悉，电子信息与电气工程学院于今年9月份进行了本科生党支部拆分重组，由按年级、跨专业成立党支部改为按专业、跨年级成立党支部，共成立十四个本科生党支部。新支部成立两个月以来，支委组建、支部破冰、组织生活、党员发展等各项工作都稳步推进，新支部建设工作取得初步成效。 撰稿：焦文宾 摄影：吴宏达 访问数量：','2016-12-02 00:00','2016-12-02 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=17350','学生办','2016-12-02 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(666337679,'2017届硕士毕业研究生登记表填写通知！','《毕业研究生登记表》填表注意事项 特别提醒: 1、参加本次《登记表》填写工作的学生：2017届全体研究生毕业生（其中也包括港澳台学生）； 2、不参加本次《登记表》填写工作的学生，他们分别是： （1）2017届海外留学生；（2）2017年退学（肄业）的学生；（3）申请延期至2018年毕业的学生； 3、《登记表》一式两份，必须用黑色的水笔填写。 4、全体学生的《登记表》，12月11日前交给班长（两份都要交）； 5、班长需要12月5日到电院就业办领取各班的登记表，收集《登记表》并填写班组鉴定，在要求的日期（详见通知最后）上交电院就业办（电群3-107）。 6、表内各项都需要填写，没有的写“无”。 登记表封面： 【学校】上海交通大学 【系科】自动化系、计算机科学与工程系、电子工程系、电气工程系、仪器科学与工程系、信息安全工程学院、软件学院、微纳电子学系 【专业名称】：按照研究生院学生基本信息相应项目内容填写。 【填表日期】：硕士：2017年2月27日 第一页： 贴照片：本人近期一寸免冠彩色证件照。 【家庭出身】本人取得独立经济地位前的家庭阶级成分。“工人、农民、教师、干部、军人、知识分子”等。 【本人成分】填“学生”。 【籍贯】指“祖籍”，按照家里户口本上籍贯一栏填写。 【现在家庭住址】一栏填写父母或监护人家庭住址。不能填写学校宿舍地址。应能具体到寄信能收到。 【何时何地入党（团）】请写清楚时间地点。 【本人身体健康状况】一栏填写有无疾病和体质强弱情况，如“良好”等。 第二页： 【本人简历】一栏自入小学起，依时间顺序填写，年月要衔接。 【家庭成员】指直系亲属（亲兄弟姐妹以及父母）。 【主要社会关系】指的是密切往来的亲戚或其他主要社会关系。 【有何联系】可以写“亲密”,“密切”等。 第三页： 【自我鉴定】四百字左右，不得太少（可以包含思想政治，学术科研，学习成绩，在校表现等几个方面）硕士：日期“2017年2月27日”。 第四页： 【班组（基层组织鉴定）】由班级负责人填写（班长等）并签其名字。自己不能写自己的！班级负责人可以找其他班干部或班主任帮忙写。硕士：日期“2017年3月6日”。 【学校（研究单位）意见】不用写。 第五页： 【学校（研究单位）······的建议：】由导师填写，硕士：日期“2017年3月13日”。 用黑色水笔填写，下笔请谨慎，尽量不要涂改，每人只有两份！ 以上仅限硕士毕业生，博士毕业生的毕业生登记表填写','2016-12-02 00:00','2016-12-02 00:00','','','学生办','2016-12-02 00:00:00.0');
