insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1185667501,'电院研究生2016年秋季专项奖学金、优秀奖学金志愿填报通知','各位同学： 因微纳电子学系三星奖学金没有合乎要求的候选人，现将三星奖学金调配电子系博士，指定博士二年级，且指定信息与通讯工程专业。《名额清单》已经更新，请大家参照。 电院学生办 2016年10月14日 --------------------------------------------------------------------------------------------------------------------------------------------------------- 各位同学： 电院研究生2016年大批量的秋季专项奖学金、优秀奖学金名额已经下来，请同学们按照步骤与要求申报。请各位同学注意以下几点： 【1】申报时间为2016年10月13日-2016年10月14日23:59分。申报方式为：使用jAccount登录同去网，报名参加“电院研究生2016年秋季专项奖、优秀奖志愿填报”活动，并在报名中填写奖学金申报信息，网址为：http://tongqu.me/act/13456 。每位同学至多可填两个奖学金志愿。 【2】学院根据各系人数将奖学金分配到各系。只有出现在《电院专项奖、优秀奖材料最终版》中的同学才有申报资格。在填写志愿时，可查看各系的名额分配，根据自己的排名填报志愿。如果一些系的一些年级申报人数不够，优先考虑将名额调配到该系其他年级（硕士博士之间不可调配），如果还有富余，则会将名额调配到其他申请人数较多的系。 【3】评审规则：每位同学两个志愿为平行志愿，评审时分数优先，硕士分数相同绩点优先，博士分数相同科研总分优先原则。每位同学最终最多获得一项本批次专项奖学金。不填报志愿视为自动放弃本批次奖学金申请。成功获评本批次奖学金后，不得申请后续的专项奖、优秀奖。 【4】评审日程安排：在2016年10月14日结束志愿申报之后，15-16日为评审时间，17-21日公示与交材料。请同学们在这一段时间内保持联系方式畅通。若日程有变，则以后续通知为准。 【5】本批次为大批量的奖学金。请同学们根据自己的情况酌情申报。后续是否还有其他奖学金未知。 附件给出了各系各年级的奖学金名额分配情况，请各位同学仔细查看，认真填报奖学金志愿。 附件给出了各系各年级同学申请专项奖、优秀奖的分数排名（只排序总分，暂未区分绩点），备注中标注了正在申请另外一批奖学金（唐立新、大学生君远奖、福建校友会等奖学金）的同学，','2016-10-13 00:00','2016-10-13 00:00','','','学生办','2016-10-13 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(283147858,'关于开展2016学年上海市红十字会高校学生助医项目的通知','  现开展2016学年高校学生助医项目，我院在校、在读的全日制学生，遭遇重大疾病且自愿接受本项目救助的可申请“上海市红十字会人道救助基金”。由多方组成的专家评审组对申报对象进行评审并按照疾病程度和困难状况排序， 分别确定5000元/人、3500元/人和2000元/人的三类标准。有意申请该基金的同学，请与10月13日17:00之前提交如下材料：   纸质版材料：   1.“上海市红十字会高校学生助医项目申报表”（word文档的附件）   2.“上海市红十字会高校学生助医项目信息汇总表”（word文档的附件）   3.身份证、学生证复印件；   4.申请助医对象提供的二级或二级以上医疗机构出具的疾病诊断报告、相关检查报告、费用账单等复印件；   电子版材料：   1.“上海市红十字会高校学生助医项目信息汇总表”（word文档的附件）   2.“上海市红十字会高校学生助医项目信息汇总表”（excel文档的附件）   请将纸质版材料打印后手写签名，送至3-102助管处；电子版材料请打包成压缩文件，统一命名为“姓名+红十字会助医项目”发送至zhuguan104@163.com。         附件一：关于开展《2016年上海市红十字会高校助医项目通知》         附件二：上海市红十字会高校学生助医项目申报表         附件三：上海市红十字会高校学生助医项目信息汇总表         附件四：电子信息与电气工程学院学生助医项目信息汇总表 访问数量：','2016-10-12 00:00','2016-10-12 00:00','','','学生办','2016-10-12 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1671040554,'电院研究生关于开展阳光计划体验式培训的通知','各位研究生新生 根据学校要求，为增强班级凝聚力，提升学生对学校的归属感和认同感，拟在全院研究生新生范围内开展阳光计划体验式培训，具体安排如下： 一、时间安排 项目 日期 系别，培训地点 地点 培训师 阳光计划体验式培训 10月15日周六 上午9点~下午5点 微纳电子学系,自动化系 电院3-200 田怡萌，邵宪一 10月16日周日 上午9点~下午5点 计算机系,软件学院，外语学院 电院3-200 巢睿祺、吴宏达 10月29日周六 上午9点~下午5点 电子系 电院3-200 宋奕清，邵宪一 10月30日周日 上午9点~下午5点 信安学院、仪器系、电气系， 电院3-200 王子二，宋玲 二、组织要求 （1）本次活动记一次“党团素拓”，请各班班长（包括博士新生班）在培训日上午和下午分别签到。 (2)只参加半天活动按照一半分数记，即0.25分 (3)请班长在活动结束后立即将带有班级姓名学号的签到名单发送给相应思政教师。 (4)请所有学生在活动结束后的三个工作日至10个工作日登陆ftp查看。地址：ftp://202.120.38.11（用户名public，密码public）。有问题及时反映，过期不再更改。 三、活动要求 建议学生穿轻便运动装，自带水 电院研工办 2016年10月 访问数量：','2016-10-12 00:00','2016-10-12 00:00','','','学生办','2016-10-12 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1849724167,'关于2013级博士、2014级硕士毕业照拍摄通知','各位2013级博士、2014级硕士同学： 我们将迎来毕业流程中非常重要的集体照拍摄，请各位班长注意提醒班级同学（特别是在外实习的同学和外籍留学生）安排好时间参加毕业集体照的拍摄，不要留下永久的遗憾。 毕业照分两部分：院系合影（1张）和班级照（2张），学院协助毕业生制作这三张纸质照片。其中院系合影全院所有同学统一上午8：30在电信群楼3号楼东侧草坪整队，班级照分为学位服和便装照。 毕业照拍摄费用为每人30元，由班长统一收齐后于10月20日前发送至就业办的支付宝账号 13122683854@163.com （杨海岩），并把班级号、姓名、总金额发送至邮箱 jyb_seiee@163.com 。 （一）各院系大合影安排： 【集合时间地点】2016年11月7日上午8:30  所有同学统一在电信群楼3号楼东侧整队，找寻自己的思政老师（举系牌）。 上午9：00准时开始拍摄院系合影，各系硕博在一起拍摄，集合完毕后准时开始拍摄，过时不候。 院系合影拍摄顺序如下： （二）班级照安排 地点：电信群楼东侧广场 各系各班具体时间如下表，请班长注意：至少提前10分钟到场，先到的班级先拍，超过时间未到齐的，需等别的专业拍完才能拍。 【特别提醒】 1、毕业班集体照将存入交大档案馆，要求全体毕业生参加； 2、不得邀请非本班毕业生参加； 3、毕业照为院系合影（1张）和班级照（学位照和便装照，共2张），学位服现场提供！ 4、如遇雨天，调整时间另行通知。 电院职业发展中心 负责老师：刘淼 严英杰 咨询电话：34205252 访问数量：','2016-10-11 00:00','2016-10-11 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=16672','学生办','2016-10-11 00:00:00.0');
