insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-140677399,'职业发展中心针对就业推荐表审核召开工作会议[图]','10月8日11点30分，电院学生办职业发展中心事务部在电信群楼3-107室针对就业推荐表的审核问题召开了会议进行讨论。职业发展中心主任林玲，辅导员卢宙以及职业发展中心事务部全体助管参加了本次会议。 会上，林玲强调了就业推荐表审核工作的重要性，对前段时间同学们在就业推荐表填写上出现的问题进行了总结，并提出了指导意见。林玲布置了下一阶段的就业推荐表审核的工作内容，并对事务部助管提出了明确时间节点，打造好与学生的互动通道等要求。 随后，事务部助管对就业推荐表审核过程中遇到的问题进行了反馈，大家针对问题展开了针对性地讨论与总结。 自9月份以来，职业发展中心就组织电院相关年级同学开展了就业推荐表的填写工作。这次会议召开在就业推荐表集中审核的时间节点上，既总结了问题，又为下一步就业推荐表的审核工作指明了方向，同时点燃了大家的工作热情。   作者：解颖 摄影：解颖 访问数量：','2016-10-08 00:00','2016-10-08 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=16655','学生办','2016-10-08 00:00:00.0');
