insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1155365445,'2016届本校读研/博延期住宿“暑期临时宿舍”申请抽签通知','各位暑期临时住宿申请人： 经过2天时间接收本校读研暑期延期住宿的申请，目前统计下来，有超过120名男生和近50名女生递交了申请。 申请人数大大超过学校下达的床位数，因此，我们决定采取抽签的方式分配临时宿舍。 请递交申请的同学6月23日（本周四）上午9点-10点持本人校园卡到电群3-103门口抽签，现场发放临时住宿申请表，过时不候。 注：请抽中签的同学，下周二下午17:00之前将填写好个人信息，并由导师签好字的申请表交到电群3-104办公室盖章。 电院学生办 2016年6月22日 访问数量：','2016-06-22 00:00','2016-06-22 00:00','','','学生办','2016-06-22 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-311210956,'统计参加7.1电院学位授予仪式和7.3毕业典礼仪式名单','1、本次统计人数包括留学生和港澳台学生 2、学位授予仪式和毕业典礼，出席对象必须是已经获得学位资格的同学（含留学生和港澳台学生）。 部分学生因课程选修重修考试原因，现无法确定是否获得学位的同学，如参加毕业典礼，需要到联络员处在告知书上办理签名手续（ 请联络员事先将附件3-“告暂未获得 学位同学的一封信“打印成书面版也可以到电信群楼3-107室第一位助管处领取书面版告知书），还需要在附件1明细表中备注栏注明“考试”。 3、不能顺利毕业的学生（包括结业学生）不能上台受礼，可以在指定席上就坐， 4、不能顺利毕业的学生不列如本次统计名单，不提供学位服 5、联络员务必于2016.6.23前反馈2份材料 1】附件1 毕业典礼出席毕业生统计表（电子版），6月23日前上传至就业办ftp://jiuyeban:34205252@202.120.38.247）毕业典礼文件夹，文件命名格式为“班号” 2】附件3 告暂未获得学位同学的一封信，每个班级一份由联络员负责组织相关同学签名（书面版），6月24日前上交 电信群楼3-107 第一位助管处 6、附件4、5为交大校歌及歌词，毕业典礼上需要集体唱校歌。 7、学院将严格按照附件1 毕业典礼统计名单租借学位服（名单必须是符合毕业条件或已办理签名毕业待定的学生） 8、学位服租借时间另行通知， 1】由联络员负责统一租借学位服，每个班级需要提供押金500元， 2】现场领取服装需要多备人手和包装袋 3】学位服遗失配件均需要照价赔偿 4】学位服回收时间为2016年7月3日下午14：00-16：00  电信群楼3-101室  逾期需要联系服装厂退还押金 附件1 毕业典礼出席毕业生统计表 附件3-告暂未获得学位同学的一封信 附件4-交大校歌 附件5-上海交通大学校歌歌词 访问数量：','2016-06-21 00:00','2016-06-21 00:00','','','学生办','2016-06-21 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-25863154,'2016级电院研究生暑假临时宿舍申请的通知','各位2016级电院研究生： 关于2016级研究生（包括直博生）暑假临时住宿申请相关安排如下： 今年学校能够提供一部分床位（25%，电院大概男50，女10）给本校直研或者本校考研录取的学生，但床位非常紧张，对于确实因科研需要的研究生，由导师提出申请。 1、申请对象：2016级电院研究生，确因导师科研任务需要申请临时寝室住宿的，可以提出申请。 2、申请方法：导师手写书面申请，申请内容包括学生基本信息、科研项目简介及学生需要承担的任务等。 3、申请要求：要求导师手写（打印或者导师只签名无效），每份申请只能为一个学生申请（一份申请里面为两个或者两个以上学生申请临时宿舍无效）。 4、递交申请：2015年6月20日（周一）、21日（周二）上午8:30-11:00将申请递交到电信楼3号楼104季自军老师处。 5、床位数：目前宿管办还没有提供准确数目，待宿管办提供后发布。 6、说明：如果申请人数少于床位数，则直接安排；如果申请人数多于床位数，则采取随机抽签的方式，抽签时间和地点将在学生办网站上另行通知。                                    电院学生办                                    2016-6-13 访问数量：','2016-06-13 00:00','2016-06-13 00:00','','','学生办','2016-06-13 00:00:00.0');
