insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-173147206,'电院525心理健康教育活动之优秀心里电影赏析','5·25研究生心理健康月活动将于下周一正式启动，活动包括心理电影赏析、心理主题讲座等活动，活动报名链接近两天会陆续挂到同去网上，欢迎同学们关注、报名。 心理电影赏析报名《遗愿清单》http://tongqu.me/index.php/act/detail/view/12362， 《沉默的羔羊》http://tongqu.me/index.php/act/detail/view/12364 优秀心理电影赏析，记一次讲座素拓，欢迎大家积极报名。 电院研工办 电院团委 2016年5月19日 访问数量：','2016-05-19 00:00','2016-05-19 00:00','','','学生办','2016-05-19 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(937558449,'电院关于2014级本科生班主任补充招聘的通知','根据学院的教学与学生工作的体制的设置，现面向电院2014级、2015级博士生中的优秀学生担任班主任工作。现通知如下： 一、招聘人数及招聘对象     招聘2014级本科生班主任20名左右，招聘对象为电院2014级、2015级博士生中的优秀学生。     聘期为2年（2016年9月-2018年7月）。 二、招聘要求     1、政治立场坚定，思想积极健康。     2、关爱学生，认真履行岗位职责。（岗位职责见附件）     3、服从学院安排，配合思政教师的工作。     4、善于交流，能够准确把握学生的动态。 三、招聘方式     简历通过邮件附件形式发送至seiee14@126.com，邮件主题和简历附件命名格式为：班级_学号_姓名。     截止时间为：2016年5月26日（周四）晚24:00 四、岗位津贴     班主任津贴由学校与学院共同承担，每人每月500元，一年按10个月计算。每学年结束后依据考核结果额外发放奖金。   电子信息与电气工程学院学生办 二〇一六年五月十九日   附：班主任岗位职责 1. 准时参加每次班主任例会，如有特殊情况会事先请假 2. 能主动走访寝室，了解学生情况 3. 能及时反馈思政布置的各项工作 4. 对于学生询问的各类事务性问题能主动咨询分管思政，并向学生解答 5. 可以完成每次主题班会的规定内容 6. 熟悉班级同学的学业成绩，掌握班级贫困生情况，能协助思政教师做好学生谈话和家长沟通 7. 能顺利完成监考任务 8. 能积极参加班级同学组织的集体活动，熟悉班级同学，并能给予思想引领 9．认真撰写《班主任工作月报表》 访问数量：','2016-05-19 00:00','2016-05-19 00:00','','','学生办','2016-05-19 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(837166783,'电子信息与电气工程学院第十六期党员论坛顺利举行','2016年5月15日19:00，电子信息与电气工程学院（以下简称电院）学生党总支、学生党建联席会在电信群楼3-100举办了第十六期党员论坛。密西根学院团委书记王冲作为本次论坛的嘉宾，与电院的学生党员们分享了自己成长过程和志愿奉献的点滴记忆。电院学生党总支副书记田怡萌、电院F15年级党建指导老师吴宏达出席本次论坛；主持人是来自电院本科生党建联席会发展部的蔡健飞。 在演讲中，王冲介绍了他在上海世博会做志愿者的经历，也细数了自己在云南、内蒙古支教时候的心路历程。他以一颗长白山天池的水滴自比，对自己作为一名中共党员的人生目标、成长过程进行了类比和阐释。 随后的访谈环节里，王冲与主持人畅谈了支教的环境适应、工作内容等同学们比较关心的诸多问题。他从自身的经历和思考出发，着重强调了党员应把个人与国家结合起来，在人生价值的追求上应具有更高、更长远的考量。 最后，电院的学生党员们也就自身关注的问题与王冲进行了互动。王冲为本次论坛签名并留下寄语：“为国储才，务本清心”，激励学生党员们勉力奋斗，以自身的努力抒发更高远的家国情怀。 访问数量：','2016-05-16 00:00','2016-05-16 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=15713','学生办','2016-05-16 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1759370517,'百廿华诞话国防，心系战友保边疆','2016年5月，B1503292党支部组织得《百廿华诞话国防，心系战友保边疆》国防教育系列活动正式落下帷幕。该活动包括为服役交大学子寄送明信片、参观国防教育基地、国防课程、国防征兵座谈等活动，在支部内部营造关注军旅文化的良好氛围，为支部同学进行了一场生动的爱国主义教育。 在母校120周年校庆之际，为了给在人迹罕至的戈壁滩、或在万里冰封的北疆、或在波涛汹涌的大洋服役的交大学子带去校庆的喜悦与感动，支部成员购买百余张明信片，为坚守在军队的交大学子送上母校的问候与祝福。随后，党支部开展了国防教育园参观活动，东区大转盘祝福征集活动，真人CS体验活动，并邀请党支部现役军官程弓同志为大家讲述了一堂生动形象的国防课程，更有交大武装部陆建忠主任以国防与征兵为主题的座谈会。 活动期间，党支部成员积极参与，非党员同学热情支持，整个集体团结一致，呈现出一片欣欣向荣之景。同学们在这些活动中了解了国防知识，也深深体会到祖国的繁荣与富强离不开强大的国防与军队。征集祝福的活动之后，党支部收到了正在部队服役的交大学子们的回信，他们表示，一定牢记“饮水思源、爱国荣校”的校训，在部队听从指挥、学好本领，为国防事业贡献自己的力量。活动也在校园内引起了不小的反响，同学们表示一定会以他们为榜样，树立远大理想，努力学习，坚持锻炼，不忘交大人的责任与使命，让母校以明天的自己为荣！   访问数量：','2016-05-16 00:00','2016-05-16 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=15707','学生办','2016-05-16 00:00:00.0');
