insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-398591770,'电院关于开展消防安全教育活动的通知','各位老师，同学们      消防安全一直是学校安全工作的重中之重，尤其是冬季，属于消防安全事故高发时段，为提升电院师生的消防安全素养，有效预防消防安全事故，学院将在接下来的一个月内，以系和课题组为单位，组织电院教师和研究生进行消防安全教育培训，我们邀请了闵行消防中队武警以及校保卫处消防科教师为大家进行知识讲解以及灭火器操作演示，并准备了足够量的灭火器供师生当场学习使用。时间安排如下： 第一场，自动化系师生 时间：12月17日 周四下午1：30~3：00 地点：软件楼报告厅 签到方式：请所有参与师生以课题组为单位签到 第二场，电气系师生 时间：12月17日 周四下午3：15~5：00 地点：软件楼报告厅 签到方式：请所有参与师生以课题组为单位签到   届时，请没有特殊安排的硕士博士研究生准时参加并做好签到。   上海交通大学电院学生办 2015-12-10 访问数量：','2015-12-15 00:00','2015-12-15 00:00','','','学生办','2015-12-15 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(72698312,'2016届硕士研究生优秀毕业生班级投票评选通知','各位班长： 电院2016届优秀毕业生评选工作正在开展，接下来是班级投票环节，首先说明一些情况： 【1】各班级的名额分配按照各班有效申请人数所占总有效申请人数的比例分配 【2】附件一中“（已调整）”表示该同学的申请类型经学院审核作出了调整，为有效申请 接下来需要各位班长组织完成以下工作： 【1】在12月14日~17日，组织班会，以无记名投票方式，根据附件一中给出的各班有效申请人，评选出本班级中的市优（上海市优秀毕业生）和校优（上海交通大学优秀毕业生）人选 【2】评选流程是先进行市优评选，落选市优的自动转入校优评选 【3】于12月17日17：00点前将附件二《电院硕士优秀毕业生班级评选结果上报表》填写完毕，班长和党支书签字后提交至电院3-102李智老师处 【4】督促获评同学填写后续材料 请各位班长注意时间节点，按时完成相关事项。有问题联系李智老师，lzhi@sjtu.edu.cn，34204944，电院3-102. 审核结果&名额分配 电院硕士优秀毕业生班级评选结果上报表 电院研工办 2015年12月14日 访问数量：','2015-12-14 00:00','2015-12-14 00:00','','','学生办','2015-12-14 00:00:00.0');
