insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1651355204,'【本科生】奖学金评审细则和综合测评征求意见通知','各位同学：    结合2014-2015学年本科生综合测评和奖学金评审情况，学院对综合测评手册进行了修订，并制订了综合测评操作指南及本科生奖学金评审细则，请同学们务必仔细阅读相关细则，如果同学们有意见或建议，请在 12月20日之前邮件向赵维娜老师反映，seieejiangxuejin@126.com，赵老师会统一将学生的意见交由电院本科生奖学金评审委员会讨论。 本次综合测评手册的主要修订之处在于： 1）删除部分项目； 2）技能培训类明确认可的项目及打分标准； 本次综合测评操作指南中需要提醒同学们重点注意的是： 1）综合测评成绩中学积分为所有修读的课程学积分； 2）综合测评过程中，本应由学生主动提交的材料必须于第一阶段的工作日内提交，此部分材料第二阶段不接受提交； 3）原则上学生本人无需提交证明材料由学院统一上传的项目（如寝室卫生、校内献血等）请同学们务必在第一阶段结束后立即查看个人成绩，如果相关部分打分上传时遗漏导致成绩缺失，可与相关部门或老师联系，提交证明，核实后可于第二阶段增补成绩，第二阶段后不接受任何项目成绩增补。 4）公示期公示的成绩包括学积分、综合测评网站导出的素拓成绩以及最终的综合测评成绩，其中学积分由电院本科生教务办提供，如课程成绩有误且在公示期内教务系统已更新则学生本人反映并核实后可以更改，否则不予更改；素拓成绩部分除网站计算错误导致的分数有误外，不接受其他任何原因的成绩更改请求和咨询。 本次奖学金评审细则详细列出了本科生奖学金（包括国奖、市奖、专项奖、优秀奖学金）评审对象、评审时间、评审流程以及哪些同学可以参与奖学金的评审，尤其是对游学、休学复学、入伍复学的同学参评奖学金资格进行了详细说明，请同学们仔细阅读。 请同学们仔细阅读综合测评手册修改说明、综合测评操作指南及本科生奖学金评审细则，征求意见后交由电院本科生奖学金评审委员会讨论、修改并最终通过，自2016年起综合测评和奖学金评审工作将按照定稿后的细则进行操作。   【20151207】电院本科生专项奖学金、ABC优秀奖学金实施细则（2016年）（征求意见稿） 【20151207】综合测评操作指南-征求意见稿 【20151207】综合测评手册2015年修订版修改说明 电院学生办 2015年12月7日   访问数量：','2015-12-07 00:00','2015-12-07 00:00','','','学生办','2015-12-07 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-168313820,'【附上讲解PPT】电院研究生2016年国奖、专项奖、优秀奖、学业奖评审细则说明会','各位同学： 下面是本次讲座的PPT，大家可以下载之后自行查看。 2016年电院研究生奖学金评审细则说明会 ---------------------------------------------------------------------------------------------- 各位同学： 本周三（2015年12月2日）中午12：10-13：30将在电院3-100举行“电院研究生2016年国奖、专项奖、优秀奖、学业奖评审细则说明会”，届时将为大家详细说明2016年电院各项奖学金评审规则，申请流程等等问题，有任何问题都可以现场交流。希望想要了解的同学准时参加。 电院学生办 2015年11月30日 访问数量：','2015-12-03 00:00','2015-12-03 00:00','','','学生办','2015-12-03 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(592737923,'关于2015年家庭困难学生寒假路费补贴发放工作的通知','各位同学 寒假即将来临，目前学生正在火热订购春运回家车票。为帮助我校家庭经济困难学生顺利回家过年，现将2015年寒假路费补贴有关事项通知如下： 发放对象：上海交通大学全日制在校的经济困难学生（本科生和研究生，包含所有年级）。 本次采用学生通过爱心屋网络自主用爱心币置换路费补贴额度，不同地区不同金额（一般为该省省会到上海的动车二等座或硬卧学生票票面价格），不给各学院分配指标名额，经济困难且有相关需求的同学在规定时间内积极登录爱心屋网站置换，补贴总额约15万元，置换采取先到先得方式，直到换完为止。 发放办法：       1、路费补贴信息将作为绿色爱心屋爱心物品发布在爱心屋网站上（网址为：aixinwu.sjtu.edu.cn）。时间是12月6日中午12：00开始置换。 2、同学用个人的jaccount帐号登录爱心屋网站，通过帐号内的爱心币置换路费补贴（每位贫困学生第一次登录爱心屋有预先充值的800爱心币）。1爱心币可置换1元回家路费补贴，在爱心屋网站下单对应补贴额度的爱心币数量，填写姓名（括号备注家庭所在省份）。 3、家庭经济困难同学在爱心屋网站上提交置换订单后，请尽快(三天以内)前往子衿街南侧爱心屋（每周的周一到周日上午10：00-晚上20：00）进行贫困生验证与区间审核。  4、返校后，2016年3月1日前到爱心屋上交本人对应区间的车票，补贴一般在2016年5月左右发放进银行卡。 详细置换规则、补贴标准、特殊情况处理、疑问和解答请参考附件中的路费补贴通知文件。 2015寒假火车票路费补贴活动方案（20151203）   有问题联系2015年路费补贴工作联系人：陈恩桃 联系电话：54742275-805 电子邮件：chenentao2003@sjtu.edu.cn 校学生事务中心 电院研工办 2015年12月3日   访问数量：','2015-12-03 00:00','2015-12-03 00:00','','','学生办','2015-12-03 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1324431024,'上海交通大学电子信息与电气工程学院2016年度中共党员拟发展考察对象公示','       根据上海交通大学关于加强党员发展和党员管理工作的相关规定，结合电子信息与电气工程学院学生党总支发展党员和预备党员转正公示制的有关规定，经各申请人自主申报，各行政班民主推优，各党支部严格考察，由各党支部提名，通过笔试、面试，综合前期各方面表现，经电院学生党总支审核，拟将下列同志列为上海交通大学电子信息与电气工程学院2016年中共党员拟发展考察计划。         现公示如下：                                             电院2016年党员发展对象（含备选）名单公示        本公示自即日起7日内，凡对上述同志列入入党发展计划有异议者，请及时以口头或书面的形式向各自的党建指导老师反映。        联系电话：021-34204520、021-34204801     电子信息与电气工程学院学生党总支                                     2015年12月2日 访问数量：','2015-12-02 00:00','2015-12-02 00:00','','','学生办','2015-12-02 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1220055830,'《毕业研究生登记表》填表说明（硕士版）','毕业研究生登记表填表说明： 注1：本表是存入学生档案的，请同学们务必认真填写； 注2：本表每人填写两份，请务必按要求填写。 表格用黑色钢笔（水笔）填写，字迹清楚，请不要用纯蓝水笔或者钢笔填写，不要用圆珠笔填写。（有其他颜色的字迹将会打回本人重新描成黑色） 请贴上近期一寸照片 需要注意的项目 封面： 学校：上海交通大学 系科：电气工程系/自动化系/计算机科学与工程系/电子工程系/仪器科学与工程系/信息安全工程学院/软件学院/微纳电子学系 专业：请按研究生院学生基本信息相应项目内容填写 填表日期：毕业当年3月1日； 表内所有签名日期：填表日期后面几天的日期（eg.**年3月5日） 第一页： “家庭出身”：指“干部、工人、农民、教师、军人……”； “本人成分”：一律填“学生”；“籍贯”：指祖籍； “何时何地入党（团）”：请将时间地点都填写清楚。 表内各项必须填写，没有的填写“无”，不能空白。 第二页： “本人简历”：从小学开始填写； “有何联系”：填写“密切”。 第三页： “自我鉴定”：400字左右，不得太少，（可以包含思想政治、学术科研、学习成绩、在校表现等等几个方面），不要忘记签名和填写时间。 第四页： “班组鉴定”：由班级负责人填写，并签名，200字左右，不得太少，本人不得填写自己的，注意笔迹不得相同； “学校意见”：不填写 第五页： 请导师填写并签名，写时间，并提醒导师务必使用黑色钢笔（水笔）填写。千万勿忘！！！ 电院就业办_电信群楼3-107室 咨询电话：34205252 负责老师：马丽丹，林玲，王锦程 访问数量：','2015-12-02 00:00','2015-12-02 00:00','','','学生办','2015-12-02 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(212258538,'电院举行“青春的选择”暑期社会实践成果交流会','11月26日下午，电院在电信群楼3-100号举办“青春的选择”暑期社会实践成果交流会。上海交通大学学指委副秘书长、就业服务与职业发展中心主任侯士兵，电院党委副书记杨一帆、李劲湘，社会实践领队老师、思政老师，以及170余名学生参与了本次活动。 会议伊始，侯士兵致开场词，他充分肯定了“青春的选择”暑期社会实践项目所取得的成果，并表示继续为该项目提供更加有力的支持。侯士兵鼓励学生响应学校号召到国家重点行业去建功立业，实现自己的人生价值。侯士兵指出，学生在择业时要注意三点：第一，把握机遇、不忘初心，充分利用身边资源；第二，要学会选择，在人生的十字路口要把握住机遇；第三、要善于发现自己的优点。 接下来电院职业发展中心马丽丹对“青春的选择”暑期社会实践活动进行了介绍。“青春的选择”暑期社会实践活动是为了响应学校鼓励毕业生面向基层、面向中西部，到国家重要行业和关键领域就业的号召，组织学生实地参观企业，亲身体验和了解关于国计民生的行业。 随后，由2015年“青春的选择”11支团队的队员代表介绍实践见闻与实践体验，11支团队参与学生人数125人，领队老师17人，分别参观了全国各地17家单位：新疆特变电工集团有限公司、新疆金风科技股份有限公司、中国工程物理研究院、东方电气集团、中国西电集团公司、中国电子科技集团公司第十四研究所、第二十八研究所和第二十九研究所、中车株洲电力机车研究所有限公司、普联技术有限公司、大疆创新科技有限公司、华为技术有限公司深圳总部、中国航天科工集团第二、三研究院、中国航天科技集团第五、九研究院及中国航天科技集团第五研究院西安分院。各位同学的介绍各有特色，不仅介绍了各个企业的文化、从事的研究领域、产品情况、招聘需求、福利待遇等，更为在场人员介绍了每一座城市的文化特色，美食，美景。北京队的代表叶祥深博士通过美国电影的各种英雄人物的技能联想到航天四个院所的产品和技术强项，通过生动、有趣的ppt讲解，给同学们展示了中国的航天梦、英雄梦。经过同学的介绍，相信每一位与会人员都对国家重要行业有了进一步的认识。 参加本次社会实践的很多队员与实践单位达成录用意向：博士生黎想与中国航天科技集团第五研究院西安分院签订了提前录取协议、博士生石煜博士与中车株洲电力机车研究所有限公司签约、硕士生郎少波与中国电子科技集团第二十九研究所签约等，成果颇丰。 最后，李劲湘对大会作总结，他认为，祖国终将选择那些选择了祖国的人。他鼓励广大学生心怀','2015-12-01 00:00','2015-12-01 00:00','','http://www.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=14548','学生办','2015-12-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1198054993,'电院学生基层公共部门发展研究会成立及招新通知','电院学生基层公共部门发展研究会 成立及招新通知 一、情况简介 为响应祖国号召，更好的引导优秀学生到基层公共部门发展并建功立业，进一步为有志于到基层公共部门就业的学生提供成长成才的舞台，特成立电子信息与电气工程学院学生基层公共部门发展研究会（以下简称电院基层研究会），基层研究生属学生社团。 电院基层研究会挂靠于电院研究生团总支，由电院职业发展中心、电院团委，以及电院研究生工作办公室共同指导。成员由电院有强烈意愿有志于从事公务员、选调生的本科、硕士、博士研究生组成。基层研究会设会长一名，副会长若干名，负责基层研究会的日常运作和管理。 二、社团目标 基层研究会成立的主要目标有五个： 一是将电院所有有意愿到基层公共部门就业（从事公务员、选调生）的学生集中起来，建立电院有志于从事公务员、选调生的学生数据库，学院所有有关公务员、选调生、政府机关挂职锻炼的信息和机会，将重点针对基层研究会发布与推荐； 二是为有志于到基层公共部门就业的学生提供交流和学习的平台，开展有针对性的理论知识学习和实务技能培训等相关工作； 三是重点针对基层研究会成员选拔优秀学生推荐到公务员和选调生岗位上去； 四是长期关注我院已赴基层公共部门工作的毕业生校友，开展持续性跟踪联络； 五是服务学校基层公共部门就业引导工作，鼓励我院有志于赴基层公共部门工作的在校学生到最艰苦的地方锻炼自己，到祖国最需要的地方建功立业，坚定扎根基层、服务人民的理想信念。 三、引导单位 目前与交大和电院合作的基层公共部门包括：中直机关选调生、中央办公厅一局、外交部、各省市国安局、各省市选调生。 四、成员特权： 1 机关部门选调生、公务员——学校学院推荐！ 2 机关部门每年通过职业发展中心老师推荐同学进行相关公务员岗位面试，通过面试的同学，才会在考试通过后获得录取资格。部分选调生或有机会不通过公务员考试，直接获得公务员编制! 3 基层工作生态——前辈言传身教！ 4 机关工作基本技能——我们提供辅导！ 5 传说中的机关工作环境——带你眼见为实！ 五、社团活动安排： 基层论坛 通过举办讲座、沙龙、访谈等各类活动，邀请具有丰富基层公共部门工作经验的地方党政领导来到电院，与有兴趣了解基层、研究基层、有志于赴基层公共部门工作的电院学子面对面交流。利用基层校友返校机会，让基层研究会的同学能够与基层校友面对面交流，了解基层工作环境，基层生态，以及电院基层校友发展情况。 集体学习 邀请公文写作、行测指','2015-12-01 00:00','2015-12-01 00:00','','','学生办','2015-12-01 00:00:00.0');
