insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1470745814,'关于研究生素拓中讲座和党团活动名单备份工作的通知','各位同学 从2015年9月起，研究生素拓中的讲座名单和党团活动名单，都会在活动结束后的五个工作日内，上传到ftp进行公示，公示期间有问题及时联系组织方更改，素拓修改截止时间为活动后十个工作日内（包含第十个工作日），逾期将统一不予受理，公示完毕后会移到“公示完毕”文件夹留存。 提醒学生有素拓分的讲座和党团活动结束后的十个工作日内要及时登录ftp查看名单是否有误。每年9月份研究生素拓工作开展时，我们会从该ftp直接调取讲座和党团活动名单。ftp地址：ftp://202.120.38.11（用户名public，密码public）。届时名单和活动清单都以ftp公示完毕的文件为准。 所有记入素拓“讲座”以及“党团活动”的活动通知必须在电院学生办网站“学生事务”栏目中发布，学生可通过该栏目中的信息来核实某项活动是否记入素拓。 素拓评分是研究生专项奖评审的重要组成部分，同学们可以查看电院研究生专项奖学金评审细则。 电院学生办 2015年9月 访问数量：','2015-09-28 00:00','2015-09-28 00:00','','','学生办','2015-09-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1626127736,'电院2015年研究生Intel、潘文渊、唐尤淑圻奖学金的申请通知','各位同学： 第一批奖学金的通知已经下来了，报名的时间即时起，截止到9月28日周一中午11点。报名时需要按照要求提交电子版材料和纸质版材料。请各位同学抓紧中秋佳节的时间，赶紧报名。 intel奖学金差额评审，学院报送10名候选人到公司参加面试，最终选择5名获奖者。10月30日面试，同天出面试结果。潘文渊奖学金，唐尤淑祈奖学金结果也在30日学生办网站公布结果。 第一批落选者可以继续参加下一批奖学金申报，成功获得者不得再申报下一批。下一批会发布大批量的奖学金，下一批评审完毕后是否还有第三批未知，即使有第三批也是较少名额，仍然按照我们公示的排名评审。 第一批奖学金申报通知 Intel奖学金 潘文渊 唐尤淑圻 电院学生办 9月26日 访问数量：','2015-09-26 00:00','2015-09-26 00:00','','','学生办','2015-09-26 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1651084414,'电院2015年研究生国家奖学金答辩通知','各位同学： 根据申请材料和排名，以及国奖的名额分配，已经确定国奖答辩入围名单。国奖将在9月28日~9月30日（周一~周三）之间答辩，国奖答辩的时间地点见附件，请各位同学提前做好各项准备，及时上传答辩PPT等材料，准时参加答辩。 2015.9.25国奖答辩安排及注意事项 国奖答辩入围名单 副本国奖答辩顺序与地点 2015年9月26日   访问数量：','2015-09-26 00:00','2015-09-26 00:00','','','学生办','2015-09-26 00:00:00.0');
