insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(489227665,'电院研究生压力管理健康讲座','报名通道：http://tongqu.me/index.php/act/detail/view/9131 电院研究生压力管理健康讲座     电院研究生压力管理健康讲座   还在被各种压力困扰吗？ 还在纠结如何平衡科研和实习吗？ 还在担心和导师关系变糟糕吗？ 还在苦于维护身边那份来之不易的感情吗？ 本次活动计入研究生综合测评讲座栏目，活动参与统计名单在活动结束后一周内在ftp公布，ftp://public.sjtu.edu.cn，用户名fashte，密码public     周三下午15：00--16：30 电院3-200   我们邀请了校心理咨询中心 薛璟老师同我们一起 /分析压力来源/ /寻找合适自身管理压力的方法/ /探求我们生活中最佳的压力平衡点/   薛璟老师介绍：心理学硕士，2004年毕业于华东师范大学，次年赴德国以及美国学习工作数年，有丰富的心理咨询和海外生活经验。擅长成长问题、压力管理等。 访问数量：','2015-05-11 00:00','2015-05-11 00:00','','http://tongqu.me/upload/acts/20150510/origin_00534865699c56c593c795988bdb9360.jpg','学生办','2015-05-11 00:00:00.0');
