insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1184804922,'电院开展12级本科生简历撰写培训讲座','  5月6日晚，由电院职业发展中心主办、电院12级本科生党建联席会承办的“职场敲门砖”——简历撰写培训讲座在电信群楼3-100顺利进行，拉开了电院12级本科生“生涯导航”系列活动的帷幕。电院职业发展中心马丽丹老师为同学们带来了精彩的讲解。      在学生求职期待值和企业职位含金量不相对称的社会大背景下，即将面临生涯抉择的大三同学有不少迷惘。针对这些问题，电院职业发展中心和电院党建联席会以服务同学为宗旨开展了一系列“生涯导航”系列活动。由电院职业发展中心，中级职业咨询师马丽丹老师作为嘉宾，为12级本科生详细介绍了如何准备简历、如何写简历。讲座一开始，马老师抛给大家一个问题，企业由备“”人情承办的面向本科生为什么需要简历，并让同学们浏览了15份简历，在互动中迅速进入今天的话题。     马老师在讲座中表示，招聘的公司、出国申请要求学生投简历就是为了从招聘者中挑选出符合公司或导师需求的学生。简历的最大特色就是“简”，而每篇简历一般只有20秒时间被HR抉择。如何让简历脱颖而出，老师教给同学们几个重要的细节，如将自己的优势用关键词体现，将简历名称命为学校+专业+姓名+应聘岗位， 用行为词优化语言等等。马丽丹老师同时强调，一份优秀的简历并不是为了展现优秀的你，而是展现出自己如何匹配应聘的岗位。   讲座中，马丽丹老师还着重强调了外企和国企投递简历的异同，分析了大家在投递简历是应该怎样有的放矢。她还从HR的角度出发，告诉大家什么样的简历才能被一眼看中，脱颖而出。   现场同学对讲座表现了热烈的反应。在讲座结束后，同学们也充分和马丽丹老师进行交流互动。部分同学表示，讲座为自己今后投递简历起到了很好的启发作用。   这次活动的开展也是为了5月13日学校的“双选会”的实习、工作大型岗位招聘活动以及即将开始的国外游学申请的顺利举行做好充分的准备。据悉，“生涯导航”系列活动还将进行“学长交流会”出国专场等活动。                                                               撰稿人：杨思渊 访问数量：','2015-05-08 00:00','2015-05-08 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=12792','学生办','2015-05-08 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(430031985,'面向2016届本科生及研究生免费嵌入式应用-ARM培训正式开班','为了提高同学们的就业能力，电院就业办特别邀请专业培训机构进校为同学们开展计算机技能培训。该项目在交大闵行校区已连续举办七期，先后有超过700名应届毕业生参与培训并获得相应职业资格认证。 根据《上海市居住证积分管理试行办法》第二章第六条规定，持证人（上海市居住证）取得大学本科学历和学士学位，积90分；取得技能类国家职业资格三级，积60分。因此，凡取得国家职业资格证书（高级证书）的均可积60分，有助于本科生落户上海，国家机关事业单位和企业作为招聘及加薪的依据，申请出国留学等用途据。 基于上述情况，为了鼓励学生获计算机程序设计员（嵌入式应用-ARM）（高级/三级）现决定举办计算机程序设计员（嵌入式应用-ARM）（高级/三级）国家职业资格证书的培训考证工作。具体事项如下： 一、培训目标 通过本级别专业理论知识学习和操作技能训练，培训对象能够掌握如下职业技能： 1.掌握C语言编程的能力； 2.具备理解ARM体系架构和熟悉Cortex－M3处理器应用开发的基本知识能力； 3.熟练使用ARM开发工具 RealView MDK(Microcontroler Devlopment Kit)集成开发环境的能力； 4.开发基于MDK的STM32F107处理器的能力。 二、主要工作内容 1.基于ARM处理器的嵌入式接口应用； 2.使用MDK开发工具进行BSP开发包和协议栈开发、移植； 3.设计基于ARM处理器的嵌入式产品； 三、教材资料 (将免费赠送电子版相关教材) 课程名称 认证类型 教材及资料 《计算机程序设计员（嵌入式应用-ARM）》 高级/三级   国家职业资格认证 《ARM开发工具RealView MDK使用入门》 《基于MDK的STM32处理器开发应用》   四、证书： 通过考核鉴定的学员可以同时获得由国家人保部颁发的证书。                             国家职业资格证书（三级/高级） 五、上课时间  7月11日周六9:30-16:30（1班），7月12日周日9:30-16:30（2班）上课，参加的同学直接到学院群楼4-408上课  六、报名事项： 【参加对象】仅面向本科大三和研二学生（2016届毕业生）。 【报名时间和地点】有意向的同学可继续到电院3号楼107室助管处报名(咨询电话：34205252) 【费用情况】在校生共计400.00元／人的押金参加考试后全额退还； 访问数量：','2015-05-08 00:00','2015-05-08 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=11210','学生办','2015-05-08 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-901642943,'电院开展12级本科生简历撰写培训讲座','   5月6日晚，由电院职业发展中心主办、电院12级本科生党建联席会承办的“职场敲门砖”——简历撰写培训讲座在电信群楼3-100顺利开展，拉开了电院12级本科生“生涯导航”系列活动的帷幕。电院职业发展中心马丽丹莅临现场，为同学们带来了精彩的讲解。    在学生求职期待值和企业职位含金量不相对称的社会大背景下，即将面临生涯抉择的大三同学有不少迷惘。针对这些问题，电院职业发展中心和电院党建联席会以服务同学为宗旨开展了一系列“生涯导航”系列活动。由电院职业发展中心，中级职业咨询师马丽丹作为嘉宾，为12级本科生详细介绍了如何准备简历、如何写简历。讲座一开始，马丽丹抛让同学们浏览了15份简历，在互动中迅速进入今天的话题。    马丽丹在讲座中表示，招聘的公司、申请的国外大学要求学生投简历就是为了从招聘者中挑选出符合公司或导师需求的学生。简历的最大特色就是“简”，而每篇简历一般只有20秒时间被HR抉择。如何让简历脱颖而出，她教给同学们几个重要的细节，如将自己的优势用关键词体现，将简历名称命为学校+专业+姓名+应聘岗位， 用行为词优化语言等等。马丽丹同时强调，一份优秀的简历并不是为了展现优秀的你，而是展现出自己如何匹配应聘的岗位。    讲座中，马丽丹还着重强调了外企和国企投递简历的异同，分析了大家在投递简历是应该怎样有的放矢。她还从HR的角度出发，告诉大家什么样的简历才能被一眼看中，脱颖而出。    现场同学对讲座表现了热烈的反应。在讲座结束后，同学们也充分和马丽丹进行交流互动。部分同学表示，讲座为自己今后投递简历起到了很好的启发作用。    这次活动的开展也是为了5月13日学校的“双选会”的实习、工作大型岗位招聘活动以及即将开始的国外游学申请的顺利举行做好充分的准备。据悉，“生涯导航”系列活动还将进行“学长交流会”出国专场等活动。 访问数量：','2015-05-08 00:00','2015-05-08 00:00','','http://202.120.2.114/_mediafile/contribute/2015/05/07/2pifjnifk4.jpg','学生办','2015-05-08 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(190180503,'电院博士生微纳电子学系党支部举办“树先锋，立模范”优秀党员访谈活动','    4月24日，电子信息与电气工程学院博士生微纳电子学系党支部在微纳科学技术研究院100号报告厅举办以“树先锋，立模范”为主题的优秀党员访谈活动，活动旨在充分挖掘身边的优秀党员同志，用他们切身的科研、学习、生活经历和感悟来激发带动全系同学更好地科研、学习和生活，争创积极向上的风气。活动邀请了微纳电子学系四位优秀的博士生党员，就科研、生活、成长经历与家庭教育以及亲情与爱情四个主题进行了深入的访谈，针对不同主题，四位优秀党员同志都分享了自己的很多经历和从中得出的感悟与理念。访谈由支部书记马德福担任主持。     针对科研问题，田鸿昌表示对于科研所碰到的所有问题都要亲自经历并且掌握，这样才算真正过关。徐明翰表示前期相关领域文献的大量阅读和跟踪本领域最新动态非常重要。陈文国表示，科研还得保持高效率，不能简单地打时间战，但要取得成果还必须有一段时间吃饭睡觉都在思考和投入到这件事情上的状态才行。成宇翔强调一定处理好科研中的人际关系，如何满足各方需求，才把心聚在一起，共同完成一件大事，这是一个团队需要考虑的重要前提。他们都共同提到博士研究生要特别注重解决问题能力的培养，要深刻懂得难与复杂的区别，难是质变的过程，需要投入巨大的精力和耐心，需要大量思考和不断实践，才有可能解决，而复杂仅仅是量的问题，是繁琐细致的问题，只要耐心细致是一定可以解决的。因此，博士研究生更要培养解决难问题的能力。     对于生活问题，田鸿昌表示生活中需要辩证地看待问题，乐观地面对问题，凡事有坏的一面，就一定可以找出它好的一面。徐明翰表示，要乐于分享，善于广泛阅读，不拖延，高效的执行力让他受益颇多。陈文国表示，生活中不要有太大的压力，只要每天完成了既定合理的目标，就应该开开心心的。成宇翔表示，要时刻清楚自己的目标，这样才能在追求目标的过程中，更好地平衡好他人的利益和自己的目标，不易被表象所迷惑和烦恼，提供极大的内需力，来达成自己的目标。     在成长经历与家庭教育模块，他们都表示，很多理念都启发于他们曾经的经历，这些经历让他们懂得了很多，因此他们都鼓励大家多去经历一些事物，不断思考，才能不断提升自己。对于家庭教育，他们觉得父母生活中的言行举止对他们的影响很大，他们的父母都非常民主，非常支持他们的抉择，但当他们快要走偏的时候，又会及时地拉他们一把。     对亲情与爱情，他们都觉得跟父母亲人的默默支持给予了他们更大的动力；对于爱情，他们做得最多的就是','2015-05-05 00:00','2015-05-05 00:00','','http://202.120.2.114/_mediafile/contribute/2015/05/05/2tiftzpkk1.jpg','学生办','2015-05-05 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(207478269,'电院研究生担任助管岗位推荐','各位同学 由于电院学生人数较多，学生素质较高，经常有兄弟院系和机关部处联系学生办，招募研究生助管。为给电院学生提供更多的勤工助学岗位，缓解其求学间经济压力，学生办友情为学生推荐。感兴趣的同学填写附件的表格，按照命名要求上传至ftp://public.sjtu.edu.cn中upload相应文件夹中，用户名fashte，密码public。 学生办会将申请学生的简历提供给招募单位，由招募单位直接与学生联系，学生直接与招募单位咨询值班时间和报酬等信息。 系别-姓名-学历-助管申请表-2015.X.X（申请日期） 学生办 2015年4月30日 访问数量：','2015-05-04 00:00','2015-05-04 00:00','','','学生办','2015-05-04 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1927904537,'电院2015级本科生班主任招聘','上海交通大学电子信息与电气工程学院 15级本科生班主任招聘通知      根据学院的教学与学生工作体制需要，招聘电院11级本科生中已经直升或者已考取本校研究生的优秀学生担任班主任工作。 现通知如下：   一、招聘人数及招聘对象    15级本科生班主任38名，招聘对象为电院11级本科生中已经直升或者考取本校研究生（包括直博生）的优秀学生。   二、招聘要求 要求政治面貌为中共党员（正式党员和预备党员）。 政治立场坚定，思想积极健康。 关爱学生，认真履行岗位职责。（岗位职责见附件） 服从学院安排，配合思政教师的工作。 善于交流，能够及时、准确把握学生的动态。   三、招聘方式    请将个人简历格式发送至邮箱：seiee15@126.com    请尽量使用doc文件，内容务必包含个人信息及联系方式。文件命名格式为 班级_学号_姓名。如：F1103701_5110379000_张三    截止时间为：2015年5月15日（周五）上午10：00。   四、岗位津贴    班主任津贴由学校与学院共同承担，每人每月400元，一年按10个月计算，对于工作表现优秀的班主任年终奖励一个月津贴。   电子信息与电气工程学院学生办 二〇一五年五月四日   附：班主任岗位职责 贯彻执行学校的各项规章制度，落实学校和院、系党政领导有关学生工作的指示，及时向有关部门汇报学生情况，反映学生的意见和建议，帮助学生解决学习、生活中遇到的问题。 深入了解学生的思想状况，认真做好学生的思想政治工作，从理想信念、道德品质、法制观念、在综合素质等方面对学生进行引导和教育，使学生树立正确的世界观、人生观、价值观。 帮助和指导学生建立、健全党支部、班委会等学生组织，加强对学生干部和学生骨干的培养教育工作，支持他们独立自主地开展工作。 关心、重视学生的学习，帮助学生明确学习目的，端正学习态度，掌握学习方法，严格学习纪律，树立良好的学习风气。 对学生进行心理健康教育，切实帮助学生提高心理素质，指导学生正确对待和处理生活中遇到的问题和苦恼。 根据学校有关规定和学生的实际表现，提出对学生进行奖励和处分的建议。 与院，系和所在班级学生保持经常性的联系，特别是于突发事件时，能够尽快赶到现场。 认真如实填写《班主任工作月报表》。 访问数量：','2015-05-04 00:00','2015-05-04 00:00','','','学生办','2015-05-04 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-388471007,'你看不到我，却能到处感受到我——电气系硕士生党员与上海电力静安站支部交流侧记','  在上海市成都北路、北京西路交界处，有一座面积达三公顷的大型主题雕塑公园，摇曳的梅枝、碧绿的草地、清澈的池水、精巧的雕塑……可以想象，在喧嚣的上海大都市中心城区，这样一座美丽的公园将为人们的生活增添多少舒适和惬意，这就是已经建成的上海静安雕塑公园。然而，当人们漫步其中时，或许很难想到，就在这如画风景的地下，强大的电流正静静地传送到静安、黄浦两区的每条街巷和浦东的世博园区，并传送到地铁线路的各个角落。   500千伏静安（世博）变电站就建在这里，和静安雕塑公园融为一体。由于变电站是作为上海世博会保电工作的一个核心工程建造的，因此人们一直称它为“世博站”。   为了能够近距离感受“世博站”的独特魅力，4月27日，上海交大电院学生党建办公室组织电气系26名硕士生党员到静安地下变电站参观，并与电站党支部进行了深入的交流。在半天的参观交流中，党员同学们不仅为电站的精巧设计和先进的技术设备所震撼，也被该电站支部党员们勤于学习、勇于奉献、争创一流的精神所深深折服。   在参观过程中，同学们看到世博站为全地下式，地面仅留主控室、进出口和进出风口，地下总建筑面积达到5.3万平方米，最深处和11层楼的高度差不多。电站李迅站长从技术角度向我们作了更深入的介绍：“世博站不仅具有540万千伏安的变电容量，而且包括几乎所有电压等级，是当今世界上第一座变电容量最大，全电缆进出线、多电压等级的500千伏全地下变电站。”静安站应用的电力技术，开创了众多全国第一：首次在华东地区采用第3绕组为66千伏等级的500千伏变压器；首次在国内变电站中应用户内闭式冷却塔；首次采用500千伏水平电缆头；首次在全地下变电站内开发应用适应不同功能需要的双套计算机监控系统……李站长同时告诉我们，500千伏电缆送电能力是220千伏电缆的2～4倍，500千伏终端站深入市区，大大减少了接入市区220千伏、110千伏、35千伏等电压等级的输电线路通道，减少各级降压变电站建设投资，同时采用地下电站的建设方式还大大节约了上海市区宝贵的土地资源。“这样一个高技术含量的庞大变电站，对员工的知识层次和技术要求很高，大家都要不停的学习来更新知识，同时工作强度很大，整个电站的工作人员只有11名，我们的男人都是铁汉子，女人都是女汉子，因为他们大多是党员。”李站长自豪地说。   曾在电站工作过的技术能手宋蓓华，现任国网上海检修公司人资部副主任，是交大电气系2005届硕士毕业生，她在交流中充','2015-04-30 00:00','2015-04-30 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=12725','学生办','2015-04-30 00:00:00.0');
