insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1169674228,'梦飞九天，实践一线','   2015年4月25日，由电院B1303493党支部发起，依托学校就业中心的“翼计划”，组织了电子系和微纳电子学系近50名党员开展了“梦飞九天，实践一线”的生涯规划主题活动。本次实践活动旨在深入企业探究工科研究生生涯规划，通过研二同学到相关对口企业进行参观学习并与交大校友开展交流讨论来促使党员同学对自己未来的生涯发展进行整体规划。力争在不久的毕业求职季中能够沉重地选择自己的职业，规划自己的生涯。整个主题活动将以华为为开始，逐一走访中兴、腾讯等企业，为同学提前展开一扇深入了解企业的大门。    作为本次活动的第一站，党员通过参观华为展示厅产品和工作生活环境，讲座交流讨论等形式，对公司概况、员工主要工作内容及招聘流程有了更具体的了解。在华为上海研究所的开放式展示厅中，工作人员演示介绍了华为最新的无线终端产品，包括多模基站，最新发布的P8手机，无线数据终端，华为智能手环，5G通信技术。接着参观了华为mlab（移动宽带创新实验室），作为了世界领先的网络设备供应商，2012年，华为mlab伴随移动互联网发展应运而生，开始从网络的角度研究IT/互联网影响，从用户体验角度研究网络需求，工作人员先后向我们介绍了华为在智能终端，业务特征数据库，话务模型预测，用户网络体验等方面的研究。通过“零距离”接触华为实验室和产品，大家不仅对目前移动通信网络有了最直接的了解，对华为在研发上的投入和技术上的领先有了更明晰的了解，同时也对自己所学知识有了更清晰的定位。在工作人员的陪同下参观了华为员工工作生活区，大家纷纷被华为豪华的硬件设施折服。设备齐全的健身馆，同时容纳几千人就餐的食堂，宽敞明亮的办公区给大家留下了深刻的印象。    最后在工作人员的安排下，交大同学与华为上海研究所的招聘人员进行了深入交流，座谈会上HR对华为的发展情况作了全面介绍，对华为核心价值观进行了深入解读，总结为“以客户为中心，以奋斗者为本，长期坚持艰苦奋斗，自我批判”，大家纷纷表示赞同。在随后的交流讨论环节中，党员就职业选择评价标准这一主题与在华为工作的交大校友展开了深入的探讨，从市场需求、自身适应、未来发展等多个方面具体剖析了当代学生应该如何理性地面对求职选择。将求职作为自己生涯规划的一部分，而不仅是找一份能养活自己的工作。对于处于研二这一分水岭的硕士学生来说，突破了原来相对比较局限的求职观，开始思索自己未来十年的生活和工作。    通过本次活动，参与的党员们对华为','2015-04-28 00:00','2015-04-28 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=12716','学生办','2015-04-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1897080314,'电院成功整理2015届研究生档案近千份','2015年4月16日至17日，电院全体研究生思政老师在档案馆完成了近千名硕士和博士毕业生的档案整理。至此，电院的研究生毕业生档案整理工作完成阶段性目标。 4月16日上午，在电院学生档案管理负责人薛秀珍老师的组织下，电院毕业生的档案整理工作全面展开。工作伊始，老师们对软件工程，信息安全，微电子等八个系近千份研究生毕业档案自仓库取出。在随后的两天时间里，老师们核对了毕业生的原有档案资料，并在每个毕业生的档案中依次放入了毕业生登记表，学位证明，成绩单，奖惩记录等相关材料。在确保档案准确无误后，根据电子数据库导出的名单，抽取了本次需要寄送的档案，放入三联单和报到证，完成最后的封口邮寄工作。 此次毕业生档案整理工作历时一天半，相较于四到五天的正常整理周期，节省了一半多的时间，这不仅归功于电院老师的耐心细致、不辞辛劳，更归功于电院档案整理工作的批量化，专业化，电子化。 据薛秀珍老师说，档案批量处理使得档案整理工作更加有序的进行，专人负责、跟踪管理的档案管理制度以及书面化的档案工作流程确保了档案的严谨和准确，而信息化的数据库统计则保证了档案整理的高效性。 经过各位思政老师辛勤付出与不懈努力，本次工作在近千份档案的整理基础上，对材料完整的约250份的档案完成第一批次的寄送工作。参与整理的思政老师们呼吁广大毕业生及时关注相关网站上档案材料的清点目录，尽早集齐材料，以期早日完成寄档工作，给自己一份安心，也为学院工作画上圆满的句号。 访问数量：','2015-04-27 00:00','2015-04-27 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=12690','学生办','2015-04-27 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1191922131,'电院学子赴世界500强企业施耐德参观交流','2015年4月22日下午，电院学子在团委老师和研究生团总支的组织下，参加了赴施耐德企业的参观与交流活动，整个活动安排周密、内容丰富，让同学们深入了解了施耐德这样一个优秀企业的发展历史、企业业务和创新精神。同学纷纷表示此次活动受益匪浅，为同学们和优秀企业之间构建了沟通交流的桥梁，为企业招徕人才的同时，也是给参加的同学一次提前与企业深入接触和获取机遇的机会，下次有类似的活动仍将积极报名，踊跃参加。 下午一点半，同学们顺利抵达位于浦东集电港的施耐德研发中心。在看过简短的宣传片后，施耐德负责招聘的人事经理为我们介绍了公司的历史与管培生计划，同学们也纷纷提出了与自己息息相关的关于招聘和如何有效地提高自身能力等问题，HR对此给予了详尽而生动的回答，并向此次参观的同学了开放了四个实习生的岗位，欢迎有相关经验或对该岗位有兴趣的同学们报名。 随后，施耐德的研发经理向我们展示了施耐德公司丰富的业务线和产品概念，尤以智能城市，智能楼宇，智能家居等和我们生活密切相关的流行概念吸引了同学们的关注，同学们与专家就其实现的一些技术问题进行了探讨。 最后，研发经理带我们去参观了施耐德的一些实验室，并邀请实验室相关负责人为同学们做详细讲解。其中，测试实验室和创新实验室的经理均为交大校友，在为同学们介绍各类测试仪器和创新方案的同时，也与同学们回忆了校园生活，整个氛围非常温馨。在同学们仍感意犹未尽中，我们不得不结束此次的参观行程。施耐德的HR表示欢迎交大学子能够多来施耐德参观交流，也希望更多的优秀交大学子能够成为施耐德的一员。 据悉，本次活动是学校翼计划及电院领航计划的系列活动中的第二次企业参观活动，日后，此类活动将常规性开展，让学生走进企业，让企业了解学生。 访问数量：','2015-04-27 00:00','2015-04-27 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=12689','学生办','2015-04-27 00:00:00.0');
