insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(403211324,'关于上海交通大学电子信息与电气工程学院本科毕业生申报优秀毕业生通知！','上海交通大学电子信息与电气工程学院本科毕业生申报优秀毕业生条例 （2015年3月）     经电院学生办批准，本次2015届本科优秀毕业生评选采取个人自荐、学院集中评审的方式进行。毕业生自荐的依据可以参考《电院本科毕业生申报优秀毕业生条例》和《选择到基层&西部&中部地区就业的毕业生申报优秀毕业生条例》。学院在集中评审中，将优先考虑到西部、中部和东北部地区就业的毕业生和优先考虑到国家重点行业工作的毕业生申报优秀毕业生申请。注意：同学们在申报优秀毕业生时，每人只能申报一项（不能同时申报市优和校优）。   《电院本科毕业生申报优秀毕业生条例》 ---简称条例（1） 申报基本条件&申报标准     遵守国家法律法规以及学校规章制度，热爱祖国，品行端正，尊敬师长；有较强的创新能力，实践能力和创业精神。在校期间学业全部合格，能够如期毕业（指当年度可以获得毕业证书和学位证书）。 （1）自荐“上海交通大学优秀毕业生”标准：      在校期间连续三个年度获得校级及以上嘉奖，此嘉奖为奖学金或荣誉称号。 （2）自荐“上海市优秀毕业生”标准：      在校期间连续三个年度获得奖学金，外加一次校级及以上荣誉称号；或连续三年获得校级及以上荣誉称号，外加一次奖学金。 奖学金包括：校优秀奖学金（A/B/C）、企业奖学金、国家奖学金、上海市奖学金；国家励志奖学金等。特别强调，单纯的助学              金不列入本项目。 荣誉称号包括：校级“三好学生”、“优秀学生干部”、“优秀党员”、“优秀团员”、“军训优秀小班长”、“军训优秀学                员”、“优秀团干部”、“党校优秀小组长”、“党校优秀学员”、社会实践优秀个人、优秀志愿者等荣誉称号                等； 《选择到基层&西部&中部地区就业的毕业生申报优秀毕业生条例》--简称条例（2） 申报基本条件&申报标准 1、遵守国家法律法规以及学校规章制度，热爱祖国，品行端正，尊敬师长；有较强的创新能力，实践能力和创业精神。在校期间    学业全部合格，能够如期毕业。特别强调：此类学生获评“优毕”后不得违约，如果违约则取消“优毕”资格。 2、毕业生已签约基层或西部或东北部地区企业；中部及东北地区是指河北、山西、吉林、黑龙江、辽宁省、安徽、江西、河南、     湖北、湖南、海南等11个省。西部地区就业不含参加大学生志愿服务西部计划。西部地区是指西藏、内蒙古、','2015-03-24 00:00','2015-03-24 00:00','','','学生办','2015-03-24 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(748455403,'本科生春季助学金自主申请通知（竹风思源、任闻玉、北京校友会）','各位同学：        春季第一批自主申请已开始。申请时间截至4月3号。应助学部名额分配，符合我们学院申请的奖项有竹风思源、北京校友会、任闻玉三个奖项。 （1）14级可申请奖项 1、竹风思源 设奖人（单位）：蒋泽荫学长、宣明智学长 奖项介绍： 竹风思源奖助学金于2008年在我校设立，评选品学兼优而家庭经济困难的理工科、医学本科一年级新生，以高考分数高低决定；老生续评，要求学业成绩前一年平均学积点不低于75分。 奖项特点： 曾获浙江省新华爱心教育基金会之捡回珍珠奖学金受益学生则优先依入学分数或学积点之高低录取。 获助学生义务： 接受此奖学金学生，在学期中每周为学校做三个小时的工作，在暑假中，选取连续两周，每周需为学校做四十个小时工作，工作内容由学校决定，出勤时数与工作内容均由校方详加记录，并与每三个月交捐赠方备查。 设奖人（单位）简介： 蒋泽荫学长，1969级台湾交通大学电子工程学系毕业，现任台湾上丽股份有限公司总经理。 宣明智学长，1973年交通大学电子工程系毕业，1977年加入工研院，1982年加入联电，现任联华电子名誉副董事长。 申请填写表格说明： 家庭人均月收入低于400元人民币； 学业成绩前一年平均成绩不低于75分，大一学生以入学分数高低决定； 品德优良（未曾记过）； 曾获浙江省新华爱心教育基金会之捡回珍珠奖优先。 2：任闻玉 设奖人（单位）：任九皋先生 奖项介绍： 连续性资助，本科生一年级新评30人，每人每年5000元；研究生一年级新评40人，每人每年5000元。  设奖人（单位）简介： 任闻玉女士系香港知名人士任九皋先生的母亲，为纪念并传承母亲的教诲，支持上海交通大学的教育事业，特别是帮助来自边远地区的低收入家庭的学生完成学业，以利将来为社会作贡献，并以此唤起社会各界对贫困学生的更多关爱，任九皋先生捐资在上海交通大学设立“上海交通大学任闻玉助学金”。 申请填写表格说明： 符合上海交通大学贫困生的标准。 着重考虑孤残学生、烈士子女，以及家庭成员长期患重病、家庭遭遇自然灾害或突发事件等特殊情况。 学期末呈交一份个人总结以及感谢信。 参加公益活动。 （2）13级可申请奖项 北京校友会 1.家庭经济收入在当地最低生活保障线一下或接近保障线的（须提供原住地有关部门出具的“贫困证明”）； 2.思想作风正派、遵守法纪校规、积极参加公益活动； 3.无不及格科目或专业排名在前50%或以内。 获助学生需每年撰写一份个','2015-03-24 00:00','2015-03-24 00:00','','','学生办','2015-03-24 00:00:00.0');
