insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1961077391,'洪志勇做客电院第四期学者讲坛','        12月19日下午，电子信息与电气工程学院第四期学者讲坛“让毕业成为你创业的起点”在电信群楼3-200拉开帷幕。本期学者讲坛由电院博士生党建联席会主办，电气系博士生第一、第二党支部承办，电气工程系洪志勇老师作为主讲嘉宾，与同学们探讨如何充满激情的对待自己的科研，如何将现在的科研与自己未来的人生规划相结合，让毕业成为创业的起点。洪智勇是2010年度上海市东方学者，入选2011年度上海市浦江人才计划，目前在我校主持建设国家能源智能电网（上海）研发中心应用超导实验室。电院学生党总支书记胡艳丽出席讲坛。       “思维的碰撞是创新的催化剂，但这只是表面，不易被我们看到的是能够孕育创新果实的深厚的土壤，这个土壤是靠扎实的学习能力建造的。”在提及自己为学心得时，洪智勇讲到自己在大学时期时并没有立马就热衷于科研，而是顺着出国热的浪潮踏上了海外留学之路。正是在国外多年的求学经验磨练了洪教授坚毅的性格，他所在的剑桥大学有着浓厚的创业氛围，这也使他清楚地认识到了创业的重要性。剑桥博士毕业归国后，他毅然决然地走上了自主创业之路。         洪智勇指出，作为一个创业者，真正的财富掌握在我们自己手中，就是我们读博时期的科研成果。他还提到研发团队靠“获得项目”而创造财富的可能性很小，但成功将科研成果转化为产业，无论是自主开发打入市场，还是形成知识产权被大公司收购，为团队带来的利益都是巨大的。         在访谈互动阶段，现场气氛十分活跃，同学们踊跃提问，洪老师精彩的回答也赢得了大家的阵阵掌声。         本次学者讲坛不仅为同学们的学术科研提供了帮助，也开拓了大家的视野，鼓励有创业想法的同学坚持自己的梦想，对同学们未来的就业以及人生都有积极的指引作用。 访问数量：','2014-12-23 00:00','2014-12-23 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=11781','学生办','2014-12-23 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1302337418,'电气系博士生对话校友探讨“毕业去哪儿”','        12月19日下午，电子信息与电气工程学院电气系博士生“毕业去哪儿”活动在电信群楼1-318A顺利召开，此次活动作为电气系博士生第一党支部申报的优秀组织生活案例，结合专业特点，聚焦博士需求，引领职业规划，吸引了许多电气系的博士生前来参加。         本次组织生活邀请到已在上海电力设计院有限公司、上海市电力公司电力科学研究院、华东电力设计院有限公司就业的学长学姐们。他们向同学们讲述各自在求学、求职与工作道路上的顺逆波折，将这一路走来的所遇、所为、所思、所悟分享给我们年轻的研究生特别是博士生同学；他们用自身的科研和工作经历，向同学们讲述了在新形势下学生党员应该如何增强社会责任感、提升综合素质，让读博生涯成为为党和国家创造社会价值的起点。 经验交流会上，同学们畅所欲言。针对同学们对就业和工作的疑惑，学长学姐们耐心地一一解答，更将他们在面试以及工作中的宝贵经验分享给同学们，为同学们日后求职就业提供了有效的指导。         通过这次组织生活，一方面加强了学校与毕业校友的联系，更重要的是为在校的同学们提供了一个了解日后就业信息以及工作的途径，达到了经验交流的目的，取得了良好效果。   访问数量：','2014-12-23 00:00','2014-12-23 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=11782','学生办','2014-12-23 00:00:00.0');
