insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2058096000,'研究生勤俭助学金、万业企业助学金、香港道德会助学金、张明为助学金、润励学金续评通知','各位在2013年获评勤俭助学金、万业企业助学金、润励学金、香港道德会助学金以及张明为助学金的同学， 根据协议，以上助学金是跟踪续评的，2014年仍然会赞助这些同学，请同学们提交如下续评材料。 （1）除润励学金外的其他助学金获得者提交上海交通大学研究生助学金续评申请表，该表格电脑填写，其中“本人申请”栏目需填满空格的三分之二，“申请人”处请手写签名，“导师推荐意见”处请导师填写。“院系评审意见”及“学生处复审意见”留空不填。 润励学金获得者提交专用表格润励学金申请表，表格中“申请人”处手写签名。 （2）所有同学都必须撰写“年度总结”，电脑打印，标题为“”XXX年度总结，不同助学金的第一行顶格称谓见附件9、2014年奖学金、助学金顶格称谓汇总 正文总结一年来自己的学习生活等方面情况，篇幅在一页纸以上，两页纸以内。 落款写，汇报人：XXXX 日期：XXX （3）成绩单由学院统一打印，学生不需提供。 请于11月3日周一下午5点前将上述两项材料提交至电院3-104张小丽老师处。有任何问题可咨询34204764张老师。 电院学生办 2014年10月28日   访问数量：','2014-10-30 00:00','2014-10-30 00:00','','','学生办','2014-10-30 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1235769661,'2015届本科预毕业生拍照通知','上海交通大学关于2015届本科预毕业生图片采集的通知 各位同学：      大家好！关于2015届本科预毕业生拍照的通知如下，请同学们按照时间、地点安排准时前往拍照。你也可以登录校教务处网站首页查看该通知消息：http://www.jwc.sjtu.edu.cn/web/sjtu/198392-1980000004841.htm。 请同学们相互转告！   教务处[65]号 新华社上海图像信息采集中心对我校2015届预毕业本科生图像采集工作即将开始,具体安排如下: 一．时间和地点 校区 日期 时间 地点 闵行校区 (东川路800号) 11月7日（周五） 9：00—17：00 上院-500号 闵行校区 (东川路800号) 11月14日（周五） 9：00—17：00 上院-500号 徐汇法华校区 (法华镇路535号) 11月14日（周五） 13：00—16：00 法华北楼俱乐部 卢湾校区 (重庆南路227号) 11月14日（周五） 12：30—16：00 卢湾校区体育馆二楼 二．  说明 1．费用 20元/人，现场收费，请准备好零钱； 2．通过验证身份证读取信息,请带好二代身份证；若发现身份证号有误，请联系教务处注册与学生中心（电话：34206405-159或160）； 3．港澳台及留学生的证件号以教育部学信网录取库的护照号为准，不予变更,请带好校园卡； 4．不能参加学校统一组织拍摄的同学，可以在2014年11月15日至2015年2月底前自行前往新华社上海图像信息采集中心补拍。 地址: 上海市虹口区四川北路1457号虹口商城21楼2108室。 工作时间：周一至周六上午9点到下午4点30分，周日和国家法定节假日不上班。费用25元（含5元快递费）。 电话：   36521352     公司网址：http://shxhs.com 附近交通：轨道交通10号线四川北路站下2号或4号出口，沿四川北路向北150米；或轨道交通3、4号线宝山路1号出口，沿虬江路向东500米至四川北路口即到。   三．  安排表   院 系 名 称 时间和地点 船舶海洋与建筑工程学院 11月7日上午       闵行上院500号 机械与动力工程学院 11月7日上午       闵行上院500号 航空航天学院 11月7日上午       闵行上院500号 致远学院 11月7日上午       闵行上院500号 电子信息与电气工程学院 11月7日下午  ','2014-10-29 00:00','2014-10-29 00:00','','','学生办','2014-10-29 00:00:00.0');
