insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1331216950,'上海交通大学2014-2015学年度助学金续评通知（本科生）','现开展2014-2015学年度助学金续评工作，此次续评助学金包括爱菊、曾宪梓、林陈孝文、勤俭、润励学金、万业企业、香港思源、甬协、张明为、张乃新。 请续评同学按照以下附件中的要求提交材料（其中续评曾宪梓和香港思源需填写专用申请表，具体见下文） 电子版材料请以“某某+某某助学金续评申请材料”命名，以文件夹的形式（不需要打包）于10月26日（本周日）中午12点前上传至 ftp://swb:shiwuban@202.120.38.250相应文件夹中，逾期上传将可能影响个人的续评。 纸质版材料请于10月27日（下周一）9点至15点交至电信群楼3号楼104室   上海交通大学2014-2015学年度本科生助学金续评项目说明 上海交通大学本科生助学金续评申请表（通用表格） 奖助学金称谓 说明：1、每项助学金需要提交材料及时间节点要求在”上海交通大学2014-2015学年度本科生助学金续评项目说明“均已写明，请大家仔细阅读按照要求提交。纸质版、电子版材料均需提交。其中纸质版材料需要班主任（思政老师）填写推荐意见之处需填写完整并需相关老师签名方可，电子版材料推荐意见一项可不填写。成绩单无需同学自己打印，将统一打印。           2、助学金申请表，除曾宪梓、香港思源助学金需用专用表格外，其余奖项均用“上海交通大学本科生助学金续评申请表（通用表格）”同学在填写通用表格时请根据自己实际续评的奖项在通用表格中三处位置填写好奖项名称，可以参考上海交通大学本科生助学金续评申请表 - 样表  填写奖项名称时请统一使用 “黑体，小三字体”。           续评曾宪梓的同学，请填写以下2个表格，无需填写通用表格，其中附件1中续评同学只需根据表格要求填写个人信息即可。           附件1：曾宪梓教育基金会第五期“优秀大学生奖励计划”2014年度推荐获奖学生汇总表          附件2：曾宪梓教育基金会第五期“优秀大学生奖励计划”获奖学生情况跟踪表          续评香港思源的同学，填写以下专用申请表，无需填写通用表格。           香港思源奖助学金申请表（2014年版）    3、所有需提交材料的称谓见奖学金称谓表格中要求。    如有不清楚之处也可邮件至lawsjtu2002@sjtu.edu.cn ~发送邮件时请告知个人姓名及联系方式，便于老师反馈。                     访问数量：','2014-10-24 00:00','2014-10-24 00:00','','','学生办','2014-10-24 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(647864741,'本科生专项奖学金评选结果通知','各位同学，经过自主申报，评选出2013-2014学年专项奖学金同学如下（部分同学先前公示学号和班级信息有误，已进行更改）： 2013级专项奖学金公示 2012级专项奖学金公示 2011级专项奖学金公示 如有何问题，请同学们于10月26日(本周日）下午3:00前向 lawsjtu2002@sjtu.edu.cn 发送邮件反映问题，发送邮件时请告知个人姓名及联系方式，便于老师反馈。 同时请获评同学于10月27日（下周一）上午9点至下午4点前将先前申请时提交的各专项奖学金的电子版材料（具体可见先前各专项奖学金的通知）打印出纸质版交至电信群楼3-102室助管处。 特别提醒：所有纸质版材料，签名处均需手写签名。需要班主任（或思政老师）填写推荐意见之处需填写完整并需相关老师签名方可。成绩单无需同学自己打印，将统一打印。如有不清楚之处也可邮件至lawsjtu2002@sjtu.edu.cn ~发送邮件时请告知个人姓名及联系方式，便于老师反馈。 访问数量：','2014-10-23 00:00','2014-10-23 00:00','','','学生办','2014-10-23 00:00:00.0');
