insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-96769394,'关于2015届硕士、2011级博士毕业照安排及毕业生纪念册素材收集的通知','各位2015届硕士，2011级博士研究生同学：   金秋季节，我们即将迎来毕业流程中非常重要的集体照拍摄环节，具体安排请认真查看以下内容，特别是集合时间地点和拍摄注意事项。 另外，具有电院特色的毕业生纪念册也开始收集素材，请大家积极关注。 请各位班长注意提醒班级同学（特别是在外实习或返家的同学）安排好时间参加毕业集体照的拍摄，不要留下永久的遗憾。   毕业照拍摄安排 【1】 拍摄对象：2015届所有毕业硕士、2011级全体博士 【2】 拍摄地点：电信群楼东侧广场 【3】时间安排： 2014.11.4，周二 电子系硕士、博士合影 信安学院硕士、博士合影   2014.11.5，周三 自动化系硕士、博士合影 微纳电子学系硕士、博士合影   2014.11.6，周四 软件学院硕士、博士合影 计算机系硕士、博士合影   2014.11.7，周五 电气系硕士、博士合影 仪器系硕士、博士合影   【4】拍摄流程： 1) 上午8:30开始拍摄班级照，每个班级分别穿便服和学位服拍两张，各班班长在本班人员到齐后现场向摄影师登记拍摄顺序，先到齐的班级先拍； 2) 9:00开始拍摄全系大合照，班级照暂停，同系/学院硕博同时与院系领导合影，穿便服； 3) 系大合照拍摄完毕后，继续按照流程拍摄班级照； 【5】注意事项： 1) 毕业生集体照是属于校方归档的材料，请毕业生务必按时参加，不可邀请非本班毕业生参加； 2) 请所有同学上午8:30准时到场，先到齐的班级先拍； 3) 请大家务必在9:00以前到场，拍摄全系大合照，过时不候，不补拍； 4) 请同学们现场服从摄影师安排，积极配合，有序拍摄； 5) 拍摄用学位服由摄影师统一提供，数量有限，拍摄完毕后请主动归还，循环使用，不可私自带走； 6) 同学们可以自行邀请导师参加拍摄； 7) 若遇下雨或其他恶劣天气无法继续拍摄则延后另行通知。 8) 本次拍摄的班级照和系大合照均会以《毕业纪念册》的形式提供给同学（收取一定工本费），不提供单张的照片纸质版（电子版在拍照后一个月内会放到ftp供同学们下载，纪念册电子版在审核完毕最终版出炉后通知同学们到ftp下载）。     2015届研究生（硕士、博士）毕业纪念册制作通知   学院不为毕业生提供单张的毕业照，而是以《毕业纪念册》的形式提供给学生。   毕业纪念册包含以下内容： *校院领导寄语 *电院各个系介绍 *学院领导，院士介绍 *思政教师、教务老师介绍','2014-10-20 00:00','2014-10-20 00:00','','','学生办','2014-10-20 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1973856912,'电院举办海外高校申请辅导宣讲会','10月16日下午2点，电院邀请到上海交通大学继续教育学院国际教育ICEC中心主任闫高明先生，U.S.A International Academic Alliance（下简称IAA）的Director Ms. Lynn，于电信群楼3-100召开了海外高校申请辅导宣讲会。 首先，由闫高明主任上台宣讲。在对上海交通大学继续教育学院进行了简要介绍之后，闫主任分几方面对留学申请进行了辅导宣讲。在留学趋势方面，他以图表的形式说明了当前申请人数逐年递增，而录取率不断降低的现状；在留学选择方面，他罗列了地域，学校与专业排名，就业等因素来进行分析；在申请准备方面，他分别以哥伦比亚大学，纽约大学，石溪大学为例，对美国大学的入学要求进行了说明。之后闫主任还为我们详细介绍了交大定向美国石溪大学的GOM项目及其优势，并展示了项目的成果。 随后，来自IAA的Director Ms. Lynn侧重于签证准备以及实习就业进行了介绍。针对许多同学在毕业之后，想在美国工作一段时间的想法，Ms. Lynn介绍了CPT(Curricular Practical Training)以及OPT (Optional Practical Training)两种方式以及各自申请流程。宣讲会的最后，Ms. Lynn以她在美国对留学生多年的指导经验，为同学们解答了海外高校申请的各方面问题。 本次海外高校申请辅导宣讲会对同学们明确自己的留学申请方向和路线大有裨益，受到了同学们的一致肯定。   新闻稿 电院就业宣传中心 刘恬礽 访问数量：','2014-10-18 00:00','2014-10-18 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=10699','学生办','2014-10-18 00:00:00.0');
