insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-562730890,'研究生航天科工奖学金评审通知','各位同学： CASIC航天科工奖学金确认评审，主要面向工科的硕士生。该项目与去年首次操作办法一样，由学生自主申请、自主提交材料，现将有关情况通知如下： 一、设奖专业     “航天科工奖学金”设立专业以航天科工集团公司各单位核心、紧缺工科专业为主，其它专业为辅（可申请专业目录详见附件一）。 二、奖励范围及评选条件 1、奖励范围：在校的全日制硕士生（含硕士一年级）。 2、评选条件： （1）学习成绩优异（权重：15%），原则上要求专业排名前10名，硕士一年级学生可提供入学成绩证明，直升学生由院系提供证明。 （2）综合素质高（权重30%），即在德、智、体等多方面全面发展，学习、生活态度积极向上，在各类评比中成绩优异。 （3）科技创新能力强（权重55%），即在校（院）级以上科技创新大赛中取得优异成绩，或在科技项目理论研究、实践工作中担任主要工作。（科技创新能力强，并不仅限于论文发表，还可以是参加科创竞赛等方面的情况。各位老师可结合自己学院的情况，重点推荐在科创竞赛中成绩突出、在国家重大项目中参与科研，潜力突出，但尚未有明显成果的同学。）   三、奖励名额及标准    以“2013-2014学年成绩及综合表现”为评审标准，评选一等奖2*10000，二等奖5*8000；三等奖8*5000；   四、申请方式及时间节点 1、有意向申请该奖学金的同学请填写附件申请表（附件二）和复审评分表（附件三），由院系推荐盖章后，于截止时间之前，自行递交学生服务中心200号助学部，需递交的纸质版材料除申请表、复审评分表（课题负责老师对课题参与情况需确认签名；学院团委书记对科创竞赛部分需审核签名）之外，还需附上申请书、成绩大表及相关证明材料。 2、需递交的电子版材料为复审评分表（附件三），请申请人本人于截止时间之前发送邮件至助学部邮箱：zhuxuebu@sjtu.edu.cn。 3、截至时间为：2014年10月15日（周三）下午15：00时整，过时将不予接收材料。 4、10月17日——10月21日就申报情况（含科研成果）进行公示 5、评审时间预计在10月22日，具体评审方式根据申报人数情况另行通知。   五、注意事项 该奖学金与学院发布的奖学金冲突，二者只能申请其中一项。 联系咨询电话：54746014   附件一、设立奖学金主要专业目录 附件三、CASIC航天科工奖学金申 请学生复审评分表（2013-2014学年） 附件二、上海交通大学研究','2014-10-10 00:00','2014-10-10 00:00','','','学生办','2014-10-10 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1809526181,'关于SCSK株式会社奖学金的评选通知（本科生）','各位同学；         根据学校通知，现开展SCSK株式会社奖学金的评选，此项奖学金申请要求如下：        1、电子系（包括电科和信工专业）、计算机系大四学生。       2、获评此奖学金后不得再获评2013-2014年度其他各类专项奖学金、国家奖学金、上海市奖学金。         此次评选名额共计名额6名，其中一等奖1名，金额5000元/人；二等奖5名，金额3000元/人。根据各专业总人数及各类奖项的设奖情况，具体名额分配如下：         信息工程专业   一等奖 1名，二等奖2名。         电子科学与技术专业  二等奖1名。         计算机科学与技术     二等奖2名。         各位符合要求的同学如有意向申请，请按照如下要求提交材料：         1、申请书。正文标题为”SCSK株式会社奖学金申请书",申请书称呼写“尊敬的SCSK株式会社”，申请书电子版请以“某某+SCSK株式会社奖学金申请书”命名。         2、SCSK株式会社奖学金个人信息汇总表（点击下载）。SCSK株式会社奖学金个人信息汇总表   请准确填写表中的相关信息，便于相关通知的发送。         3、SCSK株式会社奖学金 申请表。（点击下载）。2014年度“SCSK株式会社奖学金”申请表          请申请的同学将3份申请材料的电子版 以 “某某+SCSK株式会社奖学金申请材料”命名，以文件夹的形式（不需要打包）于10月12日（本周日）下午2点前上传至ftp://swb:shiwuban@202.120.38.250相应文件夹中，逾期上传的材料将不被进行评选。          请各位同学仔细阅读通知，按照要求提交材料，对于不按要求提交材料者，视为申请不符合要求，将不予参加评审。谢谢同学们的配合。 访问数量：','2014-10-09 00:00','2014-10-09 00:00','','','学生办','2014-10-09 00:00:00.0');
