insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1970172902,'2014年秋季学期助管招募','各位研究生同学 学生办已持续几年为电院研究生友情推荐助管岗位，获得了机关部处和电院研究生的好评，现在开始招募2014年秋季（2014年9月份上岗）学期助管，请有意向担任助管的同学填写附件的申请表格，正确命名后上传至ftp://swb:shiwuban@202.120.38.250相应文件夹中。以下几点说明： （1）申请表请认真填写，务必填写能上岗的时间。 （2）计算机或者外语方面有特别技能的同学请务必填写，有助于快速推荐到合适的岗位。 （3）一旦上岗，至少工作一个学期，做到责任心强，工作主动性强，树立电院学生的良好形象。 （4）从2014年9月1日起，电院学生办会根据申请表情况，直接将学生申请表发送给有需求的用人部门，并即时在上述ftp公布推荐情况，请申请的同学关注该ftp以便了解你获得推荐的情况。用人部门会直接联系学生。 （5）申请表上传截止日期为2014年9月1日。 系别-姓名-学历-助管申请表   电院学生办 2014年7月1日 访问数量：','2014-07-01 00:00','2014-07-01 00:00','','','学生办','2014-07-01 00:00:00.0');
