insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-990645506,'2014年研究生暑假临时住宿申请和2014级研究生住宿需求登记的通知','各位2014级电院研究生： 关于2014年研究生暑假临时住宿及2014级研究生住宿需求登记相关安排如下： 一、2014年研究生（包括直博生）暑假临时住宿 今年学校能够提供本校直研或者考研录取学生总数的30%的床位，床位非常紧张，原则上不提供临时住宿，对于确实因科研需要的研究生，可以由导师提出申请。 1、申请对象：2014级电院研究生（包括电气系、自动化系、计算机系、电子系、仪器系、信安学院、软件学院、微纳科学与微电子系2014级研究生）确因导师科研任务需要申请临时寝室住宿的，可以提出申请 2、申请方法：导师手写书面申请，申请内容包括但不限于学生基本信息、科研项目简介及学生需要承担的任务等。 3、申请要求：要求导师手写（打印或者导师只签名无效），每份申请只能为一个学生申请（一份申请里面为两个或者两个以上学生申请临时宿舍无效） 4、递交申请：2014年6月18日（周三）、19日（周四）上午8:30-11:00将申请递交到电信楼3号楼104李劲湘老师处。 5、床位数：目前宿管办还没有提供，待宿管办提供后发布。 6、说明：如果申请人数少于床位数，则直接安排；如果申请人数多于床位数，则采取电脑随机抽签的方式，抽签时间和地点另行在学生办网站上通知。本校F10级学生优先。   二、研究生期间住宿需求登记的通知 对于研究生住宿期间想要住在一个宿舍或者一个套间的同学，学院将在6月23日（周一）、24日（周二）上午8:30-11:00在电信楼3-104开放在需求登记，学院在安排宿舍时会尽量按照同学们的需求来安排，同一个系的同学如登记住一个宿舍或者一个套间，基本上都能够满足，不同系同学原则上不安排在同一个宿舍或者套间。 登记时，必须凭所有需求者的有效证件来登记，即可以替别人登记，但必须有需求者的有效证件。 因我校所有宿舍都可以租赁空调，请大家在登记需求时考虑此因素。 特别提醒：所有同学都必须登陆家园网（ourhome.sjtu.edu.cn）申请研究生宿舍，否则不予安排宿舍（就是说有没有宿舍是在家园网申请决定，怎么安排是学院安排，学院可以根据需求登记尽量安排）                                     电院学生办                                    2014-6-10 访问数量：','2014-06-10 00:00','2014-06-10 00:00','','','学生办','2014-06-10 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(536656059,'电院“跟我一起做科研”系列活动之本科生课业辅导开班','       5月27日和6月4日晚，电院电子系博士生第三党支部与电院2011级、2012级本科生党建联席会合作，在东中院教室分别开展了《通信基本电路》辅导班和《信号与系统》辅导班各两场共12小时。此次学业辅导也拉开了电院博士生“跟我一起做科研”本科生科研指导系列活动的序幕。在此次活动中，充分发挥博士生党员的学业优势，通过课业辅导、科普讲座、科研项目指导等一系列活动，提升本科生同学对科学研究的兴趣，帮助他们更早、更快、更好地参与到科研项目中来。        面向2011级本科生的《通信基本电路》讨论班在东中院1-104教室进行。主讲的博士生党员来自感知与导航研究所。他通过事先调研，了解了大家普遍感到困难的知识点，然后从老师布置的课后习题中选取了有针对性的习题，透彻地讲解了典型电路的分析方法。在讨论中，大家对遇到的新问题和新想法，纷纷发言表达自己的观点，讨论的气氛专注而热烈。          面向2012级本科生的《信号与系统》讨论班在东中院2-101教室进行。讨论班分为第一场知识点专场和第二场习题专场。在第一场讨论中，主讲的博士生党员来自无线通信技术研究所，他凭借自己多年科研的经验和广泛阅读的视野，整理出对课程独树一帜的见解，以有条不紊而又幽默风趣的方式系统地传授给学弟学妹，使大家对课程的整体有了清晰的认识。讨论结束后，还有大批同学围住主讲人进行交流。在第二场讨论中，邀请到大四本科生党员刘东豪主讲，他在复习考研期间深入钻研了数百道习题，他从中选取了十道典型习题予以详细的讲解，缜密的逻辑和纯熟的技巧给大家做出了良好的示范。        参加讨论班的本科生对活动表达了肯定和感谢。他们说，讨论班是个很有帮助的存在，在梳理理论框架的同时，还针对课后习题做细致的分析，让大家对抽象的知识点有了具体的理解，一些原来不太明白的知识点因此豁然开朗，对攻克这门“神课”的信心足了很多。主讲人也对活动予以正面评价。主讲人博士生张苏说，自己在初学《信号与系统》时走了很多弯路，直到读了大量的书以后才体会到课程的奥义，现在能够通过讨论班分享学习心得，帮助学弟学妹少走弯路，自己感到高兴和欣慰。主讲人大四学生刘东豪说，自己大三时曾作为听众参加一年的讨论班，对自己帮助很大，如今站上讲台主讲，是对讨论班活动的反哺和回馈。        据悉，电院学生办在全学院范围内开展了丰富多彩的学业辅导活动。活动形式既有传统的“答疑模式”，又有新颖的“讨论','2014-06-06 00:00','2014-06-06 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=9610','学生办','2014-06-06 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1225323703,'电院举办第十一期党员论坛','       2014年6月4日晚19时30分，电子信息与电气工程学院第十一期党员论坛在电信群楼3-200报告厅举行。本次论坛以学习从交大毕业后积极投身国防建设，积劳成疾，病逝于工作岗位上的黄亮学长的先进事迹，邀请到了黄亮的同期战友裴澍炜，及两位同样就读于交大，而后毅然选择投笔从戎的学长范琛琛、薛承荣，一同分享他们的军营生涯，一同体味“大爱无疆，青春无悔”的人生追求。电院党委副书记杨一帆、学生党总支书胡艳丽、以及电院学生办部分思政老师出席活动。          主持人先向黄亮的战友裴澍炜询问了黄亮生前在部队中的种种表现，小到衣食住行，大到是非抉择，人生规划。裴军官认为黄亮在各方面都称得上是一个道德楷模，他不仅脚踏实地，乐于奉献，还关心战友，体恤亲人，大爱无疆。          之后，主持人请上了范琛琛、薛承荣两位曾就读于交大，后又投笔从戎开始军旅生活的学长，主持人与台上三位嘉宾一同畅谈了军旅生活的感受，对他们的影响以及当下大学生该怎样选择人生方向等问题。          其间两位学长多次谈及了“梦想”一词，他们认为参军入伍，投身国防就是他们从儿时以来的梦想，而实现梦想可以不计得失，只要追随本心。进而他们认为大学生活应该过得充实有意义，应该努力追求自我价值，完善各方面的能力，把握住交大提供的海量资源。        在随后的题词环节中，在三位嘉宾给交大学子留下了赠言。嘉宾们勉励在场学子不坠青云志，不负韶华时，早日成为国家的栋梁之才，践行“大爱无疆，青春无悔”的宏图远志。         最后，胡艳丽代表在场全体师生为三位军人赠送纪念品，表达了对军人的崇高敬意。        作为有思想有觉悟的青年，同学们应该像几位学长一样，积极响应国家征兵的号召，学习黄亮精神，续写精忠报国的篇章！   访问数量：','2014-06-06 00:00','2014-06-06 00:00','','','学生办','2014-06-06 00:00:00.0');
