insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2060752056,'本科生张明为、晨曦、金清扬、万业企业助学金材料提交说明','各位同学：       大家好！       请按照短信通知的获助项目提交相应的材料，所有材料的签名和日期需手写，申请表填写请先阅读表格下方的填表说明。请于1月2日上午11点前将纸质材料交到电群3-104，谢谢大家的配合！        张明为、晨曦、金清扬、万业企业助学金材料提交要求如下：        张明为助学金：附件中的申请表、称呼为“尊敬的张明为先生、张明为慈善基金会”的助学金申请书（两页纸左右，需手写签名和日期）、大一同学提交高考成绩证明（可从网上打印或自己写明各科成绩及总分）；        晨曦助学金：附件中的申请表、称呼为“尊敬的黎洋学长”的助学金申请书（两页纸左右，需手写签名和日期）、参与公益活动汇报材料（写总结并提交相应的证明）、非大一同学成绩单由学院统一打印；        金清扬助学金：附件中的申请表、称呼为“尊敬的金清扬先生”的助学金申请书（两页纸左右，需手写签名和日期）、大一同学提交高考成绩证明（可从网上打印或自己写明各科成绩及总分），非大一同学成绩单由学院统一打印；        万业企业助学金：附件中的申请表、称呼为“尊敬的上海万业企业股份有限公司”的助学金申请书（两页纸左右，需手写签名和日期）、大一同学提交高考成绩证明（可从网上打印或自己写明各科成绩及总分）；        今年的助学金还在评审当中，还没收到通知的同学请耐心等候。如有问题，可发送邮件到molina@sjtu.edu.cn咨询。 张明为助学金申请表   晨曦助学金申请表   金清扬助学金申请表   万业企业助学金申请表                                                                                                                                   电院学生办                                                                                                                                   2013.12.31 访问数量：','2013-12-31 00:00','2013-12-31 00:00','','','学生办','2013-12-31 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-59035751,'电院推行全信息化的在线党员年终考核体系','       为了全面推进党员考核“双评、双议、双公开”，电院党建办公室以本次党员年终考核为抓手、拟开创一套在党员自评的基础上能够全方位综合群众考察意见、党建工作考评、党内互评、党建指导老师指导建议的考核系统。为了能够在本次的考核中对科学、全面、高效评价做出一次行之有效的尝试，电院学生党总支重新制定考核方案并设计考评体系如下： 建立360°全方位评价体系       “党员年终考评条例的制定不仅需要做到尽可能全面，更应该具有指导意义。我们要做到让党员们看到这份考核通知就能清楚地明白作为一名党员应该做些什么！”        在电院学生党总支的统筹指导下，电院学生党建联席会考核部对本次的党员年终考核方案进行了重新规划、重新评估，最终确定个人年终考核成绩构成为：党员日常工作*30％＋支部考察意见*20％＋群众满意度调查*20％＋党员理论考核*15％＋学习成绩*15％。        党员日常工作和支部考察意见在整个的考评占50％，重点考核党员在党支部中的表现，旨在突出参与支部党组织生活及各项常规党务工作的重要性；群众满意度考查占20%，旨在强调作为党员在群众中应起到的先锋模范作用及服务表率；党员理论考核旨在以考促学，促进党员加强党的基础理论知识及时政要点学习；学习成绩考核旨在鼓励优秀、警示成绩不佳同学，督促党员不忘学习本职。 开创24小时在线考核系统       结合电院自身学科特点、发挥学科优势，电院学生党建办公室组建在线党员考核系统开发团队，经电院党建办公室田怡萌老师及两位电院辅导员仲一昕、娄亚南设计开发，在本次年终考核期间推出了两个在线考核网站——党员考核系统及群众满意度调查系统。党员可通过JAccount账号登录“党员考核系统”完成在线党内互评、在线理论考核、个人年终总结提交；群众可通过JAcount登录群众满意度调查系统对所有党员进行评价打分及“优秀党员”推荐，评价及打分全匿名，党员也可通过自己的JAcount登录该系统查看群众打分情况和评价。       全在线考核系统的推出旨在推进全自动化、无纸化办公的同时为同学们提供一种更加便捷、灵活的考核平台。对于系统首次推行中出现的各类问题，电院学生党建办公室也相应推出电院党建微博“红色正能量SEIEE”答疑回复平台及时为同学们解决各类问题。        “所有的事情都不可能一蹴而就，遇到问题我们就想办法解决。第一次推行不论对老师还是同学、对系统的设计、运行和','2013-12-30 00:00','2013-12-30 00:00','','','学生办','2013-12-30 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1382588696,'汪小帆与电院博士生畅谈读博情怀[图]','       12月23日下午，电子信息与电气工程学院第二期学者讲坛举行。致远学院常务副院长汪小帆教授作为本期学者讲坛的特邀嘉宾，为大家带来一场题为“让读博成为你一生的荣耀”的精彩演讲。院学生党总支书记胡艳丽以及电院学生办老师参加活动。        汪小帆讲述了自己的人生与科研的历程，分享了自己治学道路上的经验。        面对多种多样的读博动机问题，汪小帆认为，对科研的执着和热爱至关重要，要想取得成功，必须制定一个明确的目标，确立自己的人生定位，并要为之不懈奋斗。        对于如何攻读博士学位，汪小帆将自身感悟与大家分享。他建议要保持良好心态，还要养成独立思考的习惯，同时必须具备科学精神和人文情怀。        汪小帆认为，要想成为一个优秀的博士生，必须对自己的课题有深入的了解，力求使所研究的课题处于国内外领先水平。他勉励同学们要让“读博成为他们一生的荣耀”。        学者讲坛由电院学生党总支主办，由电院博士生党建联席会组织，以构建高起点、多领域的博士生科研方法交流平台为己任，旨在拓宽同学们学术视野和启迪大家的创新思维。 [作者]: 电院博士生党建联席会 杨官学 [摄影]: 电院博士生党建联席会 夏轩   访问数量：','2013-12-30 00:00','2013-12-30 00:00','','http://news.sjtu.edu.cn/_mediafile/newsnet/2013/12/25/2asc87t5vc.jpg','学生办','2013-12-30 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1124613529,'苏跃增做客电院第十期党员论坛与学生党员面对面交流[图]','      12月18日下午，电子信息与电气工程学院第十期党员论坛在电信群楼3-200报告厅举行。院党委书记苏跃增为学生党员带来一场题为“变中求进新展望，珠联璧合共前行”的精彩访谈。院党委副书记杨一帆，学生党总支书记胡艳丽以及电院学生办人员出席活动。        苏跃增分享了自己的人生经历，讲述了自己从教学岗位到行政岗位的角色转换和心路历程。苏跃增先后在上海交大化学化工学院、人事处（现人力资源处）、航空航天学院任职，现在又来到电院挑起改革大梁，他感到，每一次角色的转变都是挑战，更是机遇。       谈到自己的专业时，苏跃增自述本科时研究化学，后学习计算机化学，而后进入航空航天学院，如今又来到电院，从未停止研究。对此，他表示：“（接触到一个新的行业时）我不能做这个行业的专家，但不能做外行。”        对于高校建设以及学生发展，苏跃增认为，电类学科群的合并“利大于弊”，创建一个优势学科群有利于统一指挥和资源集中。对于同学们，他鼓励大家去尝试学科交叉，他形象地比喻：“专注于单一研究方向犹如攀登一座山峰，登顶是学者的最高追求；而学科交叉犹如两座山峰之间的山谷，此处更易在思维碰撞中迸出火花。”      在访谈的互动环节，苏跃增与在座党员坦诚交流，针对同学们对“高校去行政化利弊”等疑问与大家进行探讨，现场气氛十分热烈。      一个多小时的访谈，让在座的老师和同学们看到了一段执着追求的人生，也对改革后大电院无限憧憬。       访谈最后，苏跃增为本期论坛题词，寄语电院学生党员：“探讨人生，坚守信念。”学生党员代表宋睿为他送上一幅人物肖像画作为纪念。       电院党员论坛是电院学生党建联席会的品牌活动之一，旨在加强对学生党员和入党积极分子的爱国教育，培养眼界开阔，追求卓越的交大青年学子。 [作者]: 电院党建联席会 黄棣 [摄影]: 电院党建联席会 吴晓洁 访问数量：','2013-12-30 00:00','2013-12-30 00:00','','http://news.sjtu.edu.cn/_mediafile/newsnet/2013/12/19/26pcpczml0.jpg','学生办','2013-12-30 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(302982689,'2013年上海交通大学立天唐人奖学金评审通知','各位同学       现有立天唐人奖学金需要进行申报评审，该奖学金由“上海立天唐人投资集团有限公司”捐资设立，用于奖励学习勤奋、成绩优良，且在各类创新活动中成绩突出的学生，无专业限制。 该奖学金采取学生自由申报、院系汇总推荐的方式，   具体要求如下：   一、奖学金申请条件     1）2013年度秋季学年仍在校正式注册的本科生、硕士生、博士； 2）学习勤奋、成绩优良，且在各类创新活动中成绩突出的学生（成绩底线要求为无课程不及格；创新活动需提供佐证材料）； 3）积极参加品牌类相关课程学习与实践工作的学生优先评定（品牌类相关课程包括品牌管理、广告、营销等，需提供成绩单；实践活动包括参加品牌类相关课题研究、各类策划、营销、创意类比赛等，需提供佐证材料。）    4）同等条件下，农村及边远地区家庭经济困难的学生优先评定； （经济困难同学需提供家庭经济情况调查表）    5）诚实守信，无违纪违规行为   二、奖学金发放标准      一等奖学金，全校共10名，每人获奖学金人民币10000元；      二等奖学金，全校共20名，每人获奖学金人民币5000元   三、申请材料及截止时间      1、《2013年上海交通大学立天唐人奖学金申请表》（如附件）      2、立天唐人奖学金申请书。抬头统一为“尊敬的上海立天唐人投资集团有限公司”，请打印后亲笔签名      3、其他佐证材料        纸质版全套材料请于1月2日12：00之前交给电院3-104张小丽老师处      同时将汇总表（详见附件）发送至fashte@sjtu.edu.cn 学院对材料不进行筛选，所有申请学生的材料全部报学校评审。   四、其他说明      由于该奖学金确认较晚，且今年的奖学金评审工作已基本完成。允许已获评专项奖学金额度不超过5000元的学生申报。               2013立天唐人奖学金申请表 上海交通大学立天唐人奖学金汇总表 访问数量：','2013-12-30 00:00','2013-12-30 00:00','','','学生办','2013-12-30 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-701670137,'关于2013年家庭困难同学寒假回家路费补贴的通知','各位同学：        大家好，寒假即将来临，为使我校家庭经济困难学生能够顺利回家过年，与亲人团聚，学生事务中心今年采取了新的路费补贴发放形式，由以往学生被动等待名额分配变为需要主动申请，由以往缺乏感恩精神教育转变为重视服务他人和回报社会，由以往不同距离同等补贴变为不同距离不同补贴，由以往学校单一补贴变为引入社会资源共同补贴，请各位同学珍惜这个机会！        详细的通知和具体的学生操作指南在附件中，请大家自行下载查看！ 上海交通大学2013年困难学生寒假回家路费补贴（通知版本）                                                                                                                                                           电院学生办                                                                                                                                                            2013.12.17 访问数量：','2013-12-17 00:00','2013-12-17 00:00','','','学生办','2013-12-17 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1307748925,'“如影随形·映像交大”创意摄影大赛圆满结束[图]','    对于党员，党支部有着特殊的意义，从积极分子到预备党员再到成为正式党员，党员的每一步成长都离不开党支部。无论是严肃正式的党支部大会，或者是轻松活泼的支部组织生活，我们在党支部这个集体有着共同的记忆，时间流转，有些记忆将沉淀，将成为回忆当初最美的一幕。     本次创意摄影大赛历时一个多月，通过为期一周的网络评选以及评委打分，最终角逐出【最佳合照】【最美风采】【最美交大】【最美回忆】【最佳创意】等多个奖项，现将获奖党支部公布如下并对部分获奖作品进行展示。   最佳合照 最美风采 最美交大 最美回忆 最佳创意 一等奖 B1303194党支部 B1303392党支部 B1303392党支部 B1203591-2党支部 B1303291党支部 二等奖 B1303191党支部 B1203491党支部 B1303191党支部 B1303291党支部 B1203591-2党支部 B1303494党支部 B1203191党支部 B1303291党支部 B1303392党支部 B1303191党支部 三等奖 B1303392党支部 电子系博士生第四党支部 B1203194党支部 B1203291党支部 B1203194党支部 电子系博士生第一党支部 B1303194党支部 B1303591-2党支部 B1303191党支部 B1203191党支部   自动化系博士生第一党支部 B1203191党支部 B1103591-2党支部   部分获奖作品展示： 【最佳合照一等奖B1303194党支部】 【最佳合照二等奖B1303191党支部】 【最佳合照二等奖B1203491党支部】 【最美风采一等奖B1303392党支部】 【最美交大一等奖B1303392党支部】 【最美交大二等奖B1203591-2党支部】 【最美交大二等奖B1303494党支部】 【最美回忆一等奖B1203591-2党支部】 【最佳创意一等奖B1303291党支部】       访问数量：','2013-12-16 00:00','2013-12-16 00:00','','http://xsb.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=7782','学生办','2013-12-16 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-477450564,'关于研究生国奖、专项奖、优秀奖发放情况通报','各位同学，研究生奖学金发放事宜说明如下：         1、2013年度研究生国家奖学金已于昨日通过财务系统发放至获评学生的卡中，但奖状的颁发要等教育部的通知。         2、国防科技奖学金之前年度获评学生的奖金已发放，2013年度申报的学生目前正在与单位联系确认过程中，具体奖金的发放要到2014年。         3、研究生优秀奖学金（硕士4000/人，博士5000/人）将于近两周内完成发放。         4、其他学校部分专项奖助学金的发放时间不一，具体操作是由基金会执行，且涉及设奖方，所以还请同学们耐心等待。         5、学院部分奖学金， E+H奖学金已经发放， intel奖学金、阿尔斯通奖学金将于12月底颁奖仪式结束后发放， 周维幹奖学金、李福寿思源电气奖学金将于2014年校庆期间发放，请获奖的毕业班同学毕业时不要注销银行卡。 福伊特企业管理奖学金发放时间待定。   请同学们相互转告。 电院学生办 2013年12月16日   访问数量：','2013-12-16 00:00','2013-12-16 00:00','','','学生办','2013-12-16 00:00:00.0');
