insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2063136117,'关于中华人民共和国劳动合同法！','中华人民共和国劳动合同法 （2007年6月29日第十届全国人民代表大会常务委员会第二十八次会议通过） 目  录 第一章  总  则 第二章  劳动合同的订立 第三章  劳动合同的履行和变更 第四章  劳动合同的解除和终止 第五章  特别规定 第一节  集体合同 第二节  劳务派遣 第三节  非全日制用工 第六章  监督检查 第七章  法律责任 第八章  附  则 第一章  总  则 第一条  为了完善劳动合同制度，明确劳动合同双方当事人的权利和义务，保护劳动者的合法权益，构建和发展和谐稳定的劳动关系，制定本法。 第二条  中华人民共和国境内的企业、个体经济组织、民办非企业单位等组织（以下称用人单位）与劳动者建立劳动关系，订立、履行、变更、解除或者终止劳动合同，适用本法。 国家机关、事业单位、社会团体和与其建立劳动关系的劳动者，订立、履行、变更、解除或者终止劳动合同，依照本法执行。 第三条  订立劳动合同，应当遵循合法、公平、平等自愿、协商一致、诚实信用的原则。 依法订立的劳动合同具有约束力，用人单位与劳动者应当履行劳动合同约定的义务。 第四条  用人单位应当依法建立和完善劳动规章制度，保障劳动者享有劳动权利、履行劳动义务。 用人单位在制定、修改或者决定有关劳动报酬、工作时间、休息休假、劳动安全卫生、保险福利、职工培训、劳动纪律以及劳动定额管理等直接涉及劳动者切身利益的规章制度或者重大事项时，应当经职工代表大会或者全体职工讨论，提出方案和意见，与工会或者职工代表平等协商确定。 在规章制度和重大事项决定实施过程中，工会或者职工认为不适当的，有权向用人单位提出，通过协商予以修改完善。 用人单位应当将直接涉及劳动者切身利益的规章制度和重大事项决定公示，或者告知劳动者。 第五条  县级以上人民政府劳动行政部门会同工会和企业方面代表，建立健全协调劳动关系三方机制，共同研究解决有关劳动关系的重大问题。 第六条  工会应当帮助、指导劳动者与用人单位依法订立和履行劳动合同，并与用人单位建立集体协商机制，维护劳动者的合法权益。 第二章  劳动合同的订立 第七条  用人单位自用工之日起即与劳动者建立劳动关系。用人单位应当建立职工名册备查。 第八条  用人单位招用劳动者时，应当如实告知劳动者工作内容、工作条件、工作地点、职业危害、安全生产状况、劳动报酬，以及劳动者要求了解的其他情况；用人单位有权了解劳动者与劳动合同直接相关的基本情况，','2012-12-17 00:00','2012-12-17 00:00','','','学生办','2012-12-17 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1552845555,'电院党建联席会“名企之约”系列活动百姓网专场举行','   12月12日下午，由电院党建联席会组织的“名企之约”系列活动“百姓网专场”拉开序幕。活动中，电院同学不仅被百姓网强烈的企业文化吸引，更被创始人之一的首席产品官李佳的精彩讲解所折服。     经过近一个小时的车程，电院的同学们来到了坐落于交大徐汇校区的百姓网公司总部。在百姓网做人事经理的交大学长给同学们介绍了公司的布局。     在这里，并不是传统的一群程序员各自在自己的小隔间里埋头写代码。取而代之的是舒适清新的工作环境，温馨有爱的工作氛围以及自由开放的工作方式。百姓网虽然员工不多，不过60多人。但是分工明确，部门齐全，工作起来高效严谨。虽然各部门有各自的位置，但沟通起来毫无障碍。在百姓网，各位员工无论职位高低，全部在公共的办公区域内，并且各工作位之间没有任何隔离物。就连会议室都是透明的，全公司的人都可以随时加入会议，互相交流。这样清新简单的工作环境让同学们耳目一新。     参观完工作区域后，首席产品官李佳将大家带入会议室，和同学们亲切交谈，并答疑解惑。他介绍了百姓网作为分类信息的网站服务方向，析解了区区60多人就能在互联网行业中做得风生水起的重要原因——百姓网的专注。在各大竞争对手纷纷被巨大的用户量诱惑而转线之时，百姓网始终专注于所做的分类信息方向。终于在不乏腾讯，新浪，天涯这类大公司的3000多家竞争公司中占得一席之地，并成为该方向的领头企业。李佳还向同学们介绍了百姓网的企业文化与招聘原则。原来，百姓网除了工作环境简洁，工作理念也简单专一，正是这种大道至简的公司理念让百姓网在缤纷复杂的互联网行业中异军突起，稳步向前。   活动最后，公司找来了各个方向的公司员工与同学们分小组展开讨论，零距离交流让同学们对百姓网有了更深的了解。至此，名企之约百姓网专场告一段落，但同学们与百姓网的接触才刚刚开始。这也正是名企之约活动的目的所在——帮助更多同学能够在名企之约活动中找到心仪的企业，发现未来的方向。 访问数量：','2012-12-13 00:00','2012-12-13 00:00','','http://www.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=5672','学生办','2012-12-13 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(621375706,'电院教工第二党支部参与腾讯公司企业开放日活动','2012年12月10日，在电院职业发展中心的联系下，电院教工第二党支部的二十余名思政及辅导员参与了在腾讯上海分公司为期半天的企业开放日活动。电院职业发展中心主任林玲，电院学生综合事务办主任李劲湘，电院国际化交流办公室主任史瑞，电院团委书记王培丞参与了此次活动。 下午2时许，经过约40分钟的车程，电院教师一行到达位于漕河泾开发区腾讯大厦的腾讯上海分公司，一走进腾讯大厦，便受到了腾讯公司HR部门工作人员的热情欢迎接待，腾讯大厦的液晶显示屏上也打出了欢迎交大电院教师一行人的字样。 在合影后，一行人参观了位于腾讯大厦一楼的展示大厅，大厅中成列了腾讯公司成立至今的成绩与现在主要的运营项目，而最让人感兴趣的是显示QQ实时在线人数分布的液晶屏，它直观地反馈了当下全国各省QQ的在线人数，并以亮点形式标注起上线地区。在参观完展示大厅后，电院教师一行参观了腾讯大厦的食堂及健身房设施，HR部门的工作人员也主动向各位教师介绍了腾讯员工的业余生活。 结束一楼的参观，校企双方进入了位于腾讯大厦七楼的工作区培训室，开展座谈交流。座谈会由腾讯上海招聘平台经理于晨曦主持，腾讯上海分公司人力资源总监张显，交大校友、腾讯TEG搜索产品部整合搜索中心无线网页组组长陈戈，交大校友、无线网页组的员工吴利兵作为交流嘉宾出席了此次座谈会。 腾讯上分人资总监张显首先对腾讯公司的发展历程进行了介绍，自1998年至今，腾讯从一个近百人的公司，已发展成为了一个拥有1.8万不同国籍员工的大型互联网企业，其目前市值已排名世界第二位。张总监还介绍了腾讯公司各项人性化的薪酬福利体系和其校园招聘理念，这些都给在场的参观教师留下了深刻印象。两位交大校友分别从技术面试官和被面试者角度谈了自己对校园招聘的理解，使教师们了解到企业对人才的要求。 应腾讯人力资源部的邀请，电院职业发展中心主任林玲就交大的校园招聘政策进行了解读，并当场回答了腾讯人资部门的一些校方政策问题。简短的休息后，座谈会进入了自由互动提问环节，校企双方坦诚交流，了解对方的需求，解答己方的疑惑，整个座谈会持续了两个小时。 参观活动进入尾声，电院教工第二党支部一行与腾讯的各位交流员工亲切道别之后上车返程。笔者在车上采访了部分教工对此次参观的感想，他们表示，这样的交流参观活动非常好，不仅拓宽了视野，了解了企业的所想，而且对于未来针对学生开展生涯导航有非常积极地意义，希望能有更多的机会参与此类交流活动。       访问数量','2012-12-11 00:00','2012-12-11 00:00','','http://www.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=5670','学生办','2012-12-11 00:00:00.0');
