insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1379650419,'电院学生党支部学业辅导活动深入进行[图]','       12月6日晚，电院电子系博士生第三党支部和电子系2010级本科生党支部共建的“通信原理讨论班”的第六场活动在中院106教室顺利进行。讨论班由2010级本科生电科党支部书记周思雨同学主持。       本场讨论的主题是“数字信号带通传输（上）”。主讲的博士生党员来自空间信息技术研究中心，他针对BPSK、相干2FSK、非相干2FSK等基础调制方式，详细推算功率谱、相关积分解调、匹配滤波器解调、误码率等重要知识点，每一个公式都不留疑问；同时，在推导过程中，强调了高斯白噪声的特性、最佳接收机的由来、信噪比概念的地位、基函数的正交性等基础概念的意义；最后，对推算的结果作了总结，展示了数字通信的宏伟思想。清晰的概念和扎实的计算为大家解决了疑惑，鼓舞了信心，树立了榜样。讨论班在21点准时结束，大家满载收获地离开，并期盼下一次讨论的到来。        据悉，由电院学生党总支指导、由博士生党支部和本科生党支部共建的“本科生课程讨论班”已接近第三学期的尾声。讨论班贴近本科生需求，站在初学者的角度对可能遇到的疑难问题作出解答，尤其注重对基础知识和基本方法的透彻理解，较好地帮助了本科同学成长；同时，讨论班也增强了党员的先进性意识和荣誉感，激发了党员自我教育、自我提高的内在动力，树立了党支部的良好形象。活动得到学校985三期“示范党支部”创建基金的立项支持。 [作者]: 周思雨  [摄影]: 刘东豪 访问数量：','2012-12-10 00:00','2012-12-10 00:00','','http://news.sjtu.edu.cn/_mediafile/newsnet/2012/12/07/2vqw3a9i58.jpg','学生办','2012-12-10 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2068173392,'电院研究生会举办“工科转金融”求职导航讲座','12月7日晚七时，电子信息与电气工程学院研究生会在电信群楼3-100报告厅举办了一场求职导航讲座。本次讲座题为“学长？学姐？帮你忙”之揭秘“工科转咨询、工科转金融”之路，为打算跳出理工科职业选择，或者徘徊在技术岗与非技术岗之间的交大学子们请来了电院毕业，在咨询、金融业领军企业就职的学姐学长。他们现场讲解咨询、金融业职场的准入门槛、欲转之前所需做的准备以及作为理工科学生，转与不转的利弊，给学弟学妹带来了有益的参考和启示。   就职于埃森哲咨询公司的徐轶人毕业于交大电院自动化系，目前在该公司从事IT咨询工作。徐学长就如何踏入IT咨询的大门这一话题，从何为IT咨询、能力需求及准备和应聘面试Tips三方面进行了详细的介绍。通过徐学长的介绍，大家对IT咨询这块有了更加深入全面的了解。 接下来就职于交通银行的邹学姐和大家分享了她这些年在交行研发部的一些见闻体会。邹学姐的幽默诙谐令现场气氛瞬间欢快不少，言谈笑语中大家了解了所谓的金融行业，了解了金融行业里的IT。在谈及求职以及转行方面时，邹学姐用身边的实例告诉大家，求职靠的是70%能力和硬件，30%缘分，转行需要先做好本职工作，有一颗强大的内心，要有端正的人品。 最后一位学长是就职于上海黄金交易所的高伟学长。高学长的在校传奇经历让大家折服，曾经是“饿了么”团队的主要开发人员、曾经拒过摩根斯坦利的offer、曾经做过猎头（江湖上依旧流传着关于他的传说）。高学长朴实无华，用最实在的话语道出了他这一路走来的艰辛，曾经的他也被拒过，失落过，但是他没有被打倒，用自己的努力和坚持换回了如今的幸福，高伟学长字字珠玑，道尽了作为一名工科学生在职场上的几经浮沉。 理工科学生要不要转金融、咨询等行业，这个问题需要深入认真去思考。制造业和工程行业并没有没落，国家的兴旺需要各界人才的协调合作，作为新一代的大学生、研究生需要的不仅是知识的积累和创新，同样需要不同的视角，去剖析好自己，然后选择真正想走的路，不论技术还是非技术。正如邹学姐所言，都需要做好本职工作，需要有一颗强大的内心，去面对自己的选择。 访问数量：','2012-12-08 00:00','2012-12-08 00:00','','http://www.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=5667','学生办','2012-12-08 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-288760564,'助学金申请书、感谢信书写规范','助学金申请书书写格式：    标题：助学金申请书    首行：尊敬的XXX基金会（如获评张乃新助学金，则写为“尊敬的张乃新基金会”）    正文：从各方面叙述自己及家庭的基本情况，说明申请助学金的原因。    最后：申请人、申请时间    字数：至少1000字以上，建议用交大双线稿纸手写。     助学金感谢信书写格式：    标题：感谢信    首行：尊敬的XXX基金会（如获评张乃新助学金，则写为“尊敬的张乃新基金会”）    正文：可从生活、学习等各方面汇报自己的近况，能表达自己对资助方的谢意即可    最后：受助同学、时间等    字数：至少1000字以上，签名必须手写。        对于需要提交申请书和感谢信的同学，我们会有短信告知！如有疑问，可至电信楼3-102咨询，电话34204764。                                                                                                                                                           电院学生办                                                                                                                                                      2012年12月7日 访问数量：','2012-12-07 00:00','2012-12-07 00:00','','','学生办','2012-12-07 00:00:00.0');
