insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1212101357,'如何申请办理校园卡','如何申请办理校园卡 新生办理学生证、研究生证，一般集中办理。其个人资料及数码照片由教务处、研究生院在指定时间内准确、完整地提供给校园卡运行管理中心，统一制证。 新教职工办理工作证，凭人事处进校手续单（注明工号）；因故未能集中办证的新生，可持本人有效身份证件（居民身份证、护照、港澳回乡证、台胞证、军官证、士兵证、户口薄，下同），到校园卡运行管理中心拍照、制证，一般可当场领取。 办理校园卡须由本人申请，校内相关部门批准。办理校园卡的人员范围是：留学生中的非学位生、成人教育学院学生、网络教育学院学生、技术学院的计划外学生，教务处管理的各类短期交换生和旁听生、研究生院管理的各类访问学者和旁听生，组织部管理的本校离休干部，人事处管理的本校退休人员、柔性引进高级人才和项目代理人员，各院、系、部、处、室、中心聘请的校外教师、外籍教师、临时聘用职工及各类短期来校讲学、公务、培训、访问、交流人员。 各类学生办理校园卡，由批准办理校园卡的管理部门（包括国际教育学院、成人教育学院、网络教育学院、技术学院、教务处、研究生院）填写“申领校园卡申请表（集体）”，统一收集个人资料和数码照片，提供给校园卡运行管理中心，统一制卡。其他需要集体办理校园卡的部门和人员，参照此办法办理。由于是身份证件，各类非本校全日制职工办校园卡，须经本校相关院处级部门党委（总支）批准。 符合校园卡办理范围的个人申请办理校园卡，须填写“申领校园卡申请表”，申请表经申请人所在院、系、部、处级党委（总支）领导批准签字并加盖单位公章，持本人有效身份证件和聘用合同，到校园卡运行管理中心拍照、制卡，一般可当场领取。没有聘用合同的按短期来校人员办理。 消费卡的办理对象是：在本校校园内工作学习生活的各类非本校人员。包括，本校职工家属，与本校相关的社会力量办学师生，外单位在本校的施工人员、物业管理人员，借用本校房屋的外单位人员等。 符合消费卡办理范围的个人申请办理消费卡，须填写“申领消费卡申请表”，经相应单位批准后，持本人有效身份证件到校园卡运行管理中心办理，一般可当场领取；本校职工家属可凭本校教职工的工作证、本人有效身份证件和亲属关系证明文件（户口本、结婚证等）办理。单位集体办理消费卡的，由批准办理消费卡的单位填写“申领消费卡申请表（集体）”，统一收集个人资料，提供给校园卡运行管理中心，统一办理。、 每位持卡人只能持有一张一卡通用户卡。持卡人身份变更时，应重新办理与新身份相对应的','2012-05-22 00:00','2012-05-22 00:00','','','学生办','2012-05-22 00:00:00.0');
