insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-533694817,'关于宿舍搬迁跟宿管办沟通的情况说明','各位B11级同学： 今天跟宿管办沟通了一下大家搬宿舍的事情，宿管办对于此次大家搬家安排的非常周到细致，也比较以人为本，说明如下，如有好的建议，请留言或者发送email至jx_li@sjtu.edu.cn李劲湘老师。 一、搬迁日程安排 1、4月17日-18日，登记宿舍，包括先本系登记，然后跨系登记 2、4月18日晚，未登记学生随机分配 3、4月19日，学院安排名单公布（bbs的seiee版和学生办网站），有问题及时与李劲湘老师沟通。 4、4月19日晚，学院上报宿管办 5、4月20日—25日，搬迁宿舍同学可以到研究生宿舍楼阿姨处领取钥匙。从领到钥匙开始，即可进行自行搬迁。 6、4月24日7:30-17:30宿管办安排两辆货车协助搬迁。 7、4月25日16:00前，全部搬迁完毕，归还原本科生宿舍钥匙。 二、集中搬迁当天（4月24日）安排 1、  时间：7:30-17:30 2、  X26和D13各一辆卡车，X26的同学的行李分两个区域，分别是运往X38和X43的，D13的同学行李分两个区域，分别是X48和X62的。 3、  装车后，留2-3个同学在车上，其他同学骑车跟着卡车一起走（卡车开得不快），到研究生宿舍再卸下货物。 4、  行李装车、卸车、搬运均由学生自己负责。 5、  贵重物品请随身携带，电脑等物品不要离开自己视线。 三、其他注意事项 1、  本周五（4月20日）之前，阿姨会给每个同学发放两个编织袋，便于大家打包。 2、  本周五前，阿姨会准备好贴纸，大家可以贴在自己打包的纸箱或者袋子上，至少写上班级和姓名，以防混淆。 3、  搬运过程中请服从现场工作人员安排，保管好自己的钱物。 四、温馨提示 1、因搬迁撤空的宿舍经清理后将立即另做它用，为保证宿舍顺利交接，涉及搬迁的宿舍内所有的物品请在当日全部搬离，避免被他人视认为无主物品处理。 2、  学生搬离后的寝室、单元、楼道内的一切公物须完好无损，凡有损 坏、遗失的，按“谁使用谁负责”的原则照价赔偿；责任难以区分的室内以寝室为单位、室外公用部分以楼层或单元为单位，各人平均分摊赔偿费用。 3、 空调退租：凡原有宿舍租赁空调的同学需于4月23日17：00之前以宿舍为单位派出一名代表凭租赁合同及押金单至商业街124办理空调退租、遥控器押金退还业务。 4、 空调租赁：凡新宿舍需要租赁空调的同学请以宿舍为单位派出一名代表凭学生证至商业街124办理空调租赁手续。 5、原宿舍空调','2012-04-17 00:00','2012-04-17 00:00','','','学生办','2012-04-17 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-546984163,'电院B11级调换宿舍学生名单及宿舍明细','各位同学：      附件是此次B11级同学调换宿舍的同学名单以及宿舍明细，里面有三个表格，请注意，有的宿舍已经有空调，有的宿舍还没有空调。       请各位同学来登记时，如果不想安装空调，请不要选择有空调的宿舍。如果想要空调，选择没空调的宿舍是没问题的，可以向空调租赁方租空调。       本科生那边已经安装了空调的，要根据宿管办和空调出租方的要求做好相关工作，具体请咨询宿管办和出租方。                                电院学生办                                 2012-4-16 访问数量：','2012-04-17 00:00','2012-04-17 00:00','','','学生办','2012-04-17 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2007952186,'电院关于B11级研究生调换宿舍的通知','各位B11级研究生同学： 接学校通知，大家将于2012年4月24日和25日两天从本科生宿舍楼搬迁到研究生宿舍楼，宿舍楼都是8人一个套间，2人一个房间，现将相关宿舍分配工作安排如下： 一、           根据宿管办提供的学生名单和可安排的宿舍分为5个区域，每个专业占一个区域。同一个专业的同学在本专业的区域可以自由申请组合，可以是2个人一起申请一个房间，也可以8个人一起申请一个套间（当然前提是有空余的整房间和整套间）。各系区域具体信息将于2012年4月16日公布（因还有部分宿舍没有腾出来，因此需要16日才能公布，请大家关注学生办网站xsb.seiee.sjtu.edu.cn以及饮水思源BBS的SEIEE版）目前已经确定的是女生住X38和X43，男生住X48和X62. 二、           各系同学在学院安排的时间和地点到工作人员（一般为负责该系的思政老师）处，按次序选择套间和房间，先到先登记。来登记时，必须携带校园卡或者学生证等有效证件，如果要替别人登记，前来进行登记的同学必须同时携带被替代人的有效证件，否则不予登记。具体时间地点为： 系名 时间 地点 自动化系 4月17日9:00-11:00 电信楼3-101B 计算机系 4月18日9:00-11:00 电信楼3-102旁边 电子系 4月17日14:00-16:30 电信楼3-101B 电气系和仪器系 4月17日14:00-16:30 电信楼3-102旁边       三、           原则上不允许跨系组合宿舍，如确实想要跨系组合，必须在18日上午14:00-16:00到电信楼3-102找李劲湘老师登记（前提是还有合适的房间剩余）。18日上午16:00前不登记的同学，将由学院统一安排。                                      电院学生办                                   2012-4-12 访问数量：','2012-04-12 00:00','2012-04-12 00:00','','','学生办','2012-04-12 00:00:00.0');
