insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(573165205,'关于申请2012年度本科生google女性奖学金的通知','     2012年度google女性奖学金评审工作已经展开，google女性奖学金是为了纪念安妮塔？博格博士，谷歌在中国顶尖高校启动了“Anita Borg计算机学科女性奖学金”，奖励计算机科学及相关技术学科的女性学生。本科生获奖者每人将获得人民币8000元的奖金，研究生获奖者每人将获得人民币10000元的奖金。所有获奖学生还将在2012年暑假被邀请访问谷歌北京或上海办公室，与谷歌工程师面对面交谈，并与来自国内其它院校的获奖者分享交流。     此次google女性奖学金针对的本科生对象是09级计算机专业的女生，学院上报名额为5人，最终google公司评审出获奖者3人，每人奖金8000元。     凡有意向申请的同学，请填写附件的申请表，于2012年3月20日下午17：00前交到电信楼3号楼101C，学院将根据上一学期的综合测评成绩排名，确定通过初审的同学。     凡获得此次google女性奖学金的同学，不能再申请2013年1月之前的其他专项奖学金。                                                               电院学生办                                                            2012年3月16日 本科生专项奖学金申请表  访问数量：','2012-03-16 00:00','2012-03-16 00:00','','','学生办','2012-03-16 00:00:00.0');
