insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-950652002,'研究生专项奖学金排名查询和志愿申报通知','请提交了专项奖学金申请材料的同学，携带学生证或校园卡，于10月8日12:00~17：00，10月9日10:00~17:00，到电信楼3-101C查看自己的材料在本系排名（博士不分系别），并填写《研究生专项奖学金志愿申报单》，过期不提交申报单，视作放弃。 告知：（1）根据统计，本次全院申请者总数略大于奖学金总数，某些系申请者数量小于奖学金名额数，而有些系申请者数量大于奖学金名额数，因此，后期我们会根据申报情况将国优、黑莓、光华等奖学金名额在各系间进行微调，希望同学们积极申报上述奖项。 （2）请带好校园卡或学生证前往查看自己的排名，不可查看其它同学的名次和志愿。如果代同学查看名次、申报志愿，必须同时出具双方学生证以及对方的委托书。  请收到通知的同学相互转告 访问数量：','2011-10-08 00:00','2011-10-08 00:00','','','学生办','2011-10-08 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2133575751,'给电子信息类专业毕业生的求职建议','给电子信息类专业毕业生的求职建议   随着经济形势逐渐回暖，制造业和服务业中涉及到电子信息的大部分企业及单位对应届毕业生的需求也在持续扩大，目前仍然是招募和录用应届大学生数量最多的行业。面对纷繁复杂的就业市场，学生难免会因为缺乏足够的信息而造成目标错位，影响就业质量和自身未来的发展路径。因此有必要对相关行业进行大致的分类说明，以帮助毕业生有针对性地做出选择、做好准备。 按照单位性质及规模分类，大致包括国有大中型企业及科研院所、民营企业及外资单位、创业团队及其他等三大类。其中，国有大中型企业及科研院所仍然占据就业市场的主要份额，特别是针对名牌大学的传统优势专业。这类单位人员规模较为庞大，下属众多分支部门及机构，会有比较稳定的工作环境，职务晋升相对较为缓慢，对员工的个性发展关注较少。民营企业及外资单位是当前最为活跃的中坚力量，因为没有第一类单位的先天优势，他们更关注竞争和效率，因而对员工的工作热情要求较高，福利待遇弹性差距较大，外资单位还特别关注员工的个性发展，机遇和挑战较多。创业团队及其他周边企业大多没有很高的知名度和市场份额，他们依靠做小而精的产品或服务来经历目前的成长期，单位规模普遍很小甚至有些员工不足十人，但是每一名员工对企业都非常重要，所需要处理的事务并不局限于各自的岗位，大家的共同愿景超越了收入和福利。单位性质及规模很大程度上决定了企业的文化和地位，这对走出校门的毕业生而言，无论进入哪一类单位都是需要一定时间来适应和磨合的。 按照岗位和工作内容分类，大致包含产品研发及装备制造、信息维护及技术支持、咨询服务及教学培训等三大类。其中，产品研发及装备制造对毕业生的学历和能力水平要求较严格，无论是硬件还是软件，其研发时间和产品质量等要求都决定了该工作的强度和压力较大，这一类岗位的招聘规模一般不大。信息维护及技术支持是目前市场上招聘规模较大的一类电子信息相关专业岗位，特别是近年来银行金融等产业的快速发展激增了这类岗位的需求，并且薪资福利平均水平较高，需要注意的是规模化容易导致均一化，如何保持和提高自身的核心竞争力以应对将来的变化在这类工作中显得更加重要。咨询服务及教学培训相比前两类而言是新兴的岗位类别，以整合和提供现有行业信息、操作技术、一体化解决方案为主要内容，是当前信息服务业中较为高端的一类。 需要特别说明的是，每一家企事业单位都不可能只有一个部门或者只招聘一种岗位的员工。对电子信息类专业的毕业生来说，单位和','2011-10-01 00:00','2011-10-01 00:00','','','学生办','2011-10-01 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1909200102,'110928 学生党建联席会破冰大会圆满落幕','      为欢迎2011级新成员的加入，学院学生党建联席会破冰大会于2011年9月28日星期三中午13:00隆重举行。本次活动旨在提供一个展现自我及部门风采的平台，加强成员间交流，培养团队意识，增强组织凝聚力，形成特有集体文化。       蔚蓝的天空中摇摆的风筝和电院大草坪上社员们灿烂的笑颜交相辉映，四个部门精彩节目的轮番上演，欢声笑语中新老成员在游戏和娱乐中相识相知，每位成员积极融入其中，切身体会党建联席会这个温馨的大家庭的生机与活力，将破冰进行到底。同时，大会得到了指导老师的充分肯定，并应各成员之邀，我们请到了指导老师巢睿祺以及党建刘毓闻老师等参加活动，与同学们一同体验破冰的乐趣。       大会在活跃的暖场游戏中悄然拉开帷幕，两两成组体验着套中人的新奇刺激与步履维艰，那些或是失误或是故意将报纸扯破的小举动，无不博得大家哈哈一笑，而败者一个个滑稽的“献草”仪式更是赢得满堂喝彩。率先冲锋上前为大家奉献节目的是考核部的同学们，他们以一段搞笑舞蹈引爆全场，引导会场气氛瞬间HIGH起来。接下来，宣传部原创节目由一只扭动的贪吃蛇隆重推出，真人版贪吃蛇在吃饱喝足后将一曲由《换大米》改编的《宣传部之歌》倾情献上，引得阵阵掌声。稍事休息，地雷阵又横空出世，一个个看似不起眼的充水气球让选手如履薄冰，终点处的小树则更是一次次将唾手可及的胜利阻挡在外，而观众们也不甘示弱，纷纷当起了“恶人”，搅乱选手的视听，看得大家是心跳连连。而发展部则是另辟蹊径，结合当下热门节目《非诚勿扰》自编自导自演同名小品，各种搞笑桥段层出不穷，令人忍俊不禁。组织部也不甘为人后，以一首原创歌曲尽现其才艺之精髓，博得众人欢呼，将大会推向高潮。       持续一个半小时的“欢乐破冰 共同成长”活动在大家的欢歌笑语中圆满落幕。毫无疑问，电院学生党建联席会必将带领全体成员走好大学之路，引领大家共同发展，共同成长！ 访问数量：','2011-09-30 00:00','2011-09-30 00:00','','','学生办','2011-09-30 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1465099658,'110928 电院召开2011级学生基层党支部成立大会','  9月26日晚，在雄壮的国歌声中，电院2011级基层党组织成立大会拉开帷幕。会议由11级本科生党建老师王牧之主持，学院党总支书记洪梅、学院团委书记胡艳丽以及各年级党建老师出席会议。来自电院31个班级的67名学生党员共同参加了此次大会。 会议首先由党总支书记洪梅老师宣读电院党委“关于同意成立基层党组织的批复”，随后，洪老师亲切寄语新生党员要努力学习：努力学习科学文化知识，用知识武装自己，用文化充实自己；努力学习如何做人，有坚定的信仰，宽广的胸怀；努力学习如何工作，包括学生工作，党建工作以及以后到社会参加的工作，不断实践，积累经验，把自己培养成又红又专的新时代大学生。 随后，王牧之老师对新生党支部进行了基本情况的介绍，并对党支部工作进行了部署，包括参与积极分子的入党材料制作，积极参加组织生活以及协助完成支部各种材料。随后，王老师又对新生党员提出了一些具体要求，首先要坚定政治立场，永远围绕在党的周围；要刻苦学习党的理论知识和大学课程；要认真完成对积极分子的考察工作；要定期向班主任老师递交思想汇报；要积极参与到组织生活中去。 最后，大会在国际歌中圆满结束。相信电院11级学生党支部将在今后的日子中在广大新生中树立榜样，发挥其在学生工作中的正面引领作用。 访问数量：','2011-09-28 00:00','2011-09-28 00:00','','','学生办','2011-09-28 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1888176723,'研究生申请助学金通知','各位研究生新生同学，研究生助学金申请工作已经开始了，请认真阅读附件一“上海交通大学助学金评审管理办法.doc”   家庭经济困难申请助学金的同学可提交以下材料： （1）申请书，书写格式见附件二 （2）上海交通大学家庭经济困难学生认定申请表，见附件三 至各自思政教师处。 截止时间：2011年10月9日 已经向思政教师提交过上述材料的，不需再交。 如果成功评审，10月20日前，我们会通知进行进一步的材料提交。 本次没有评审成功的同学，如果学校下发后续的名额，我们会直接根据本次提交的材料进行评审，不再广发通知，因此请家庭经济困难的同学积极申报，以免错过机会。 另外，悟宿奖助学金采用学生自由申报、学校事务中心评审的方式，具体通知http://affairs.sjtu.edu.cn/News/News_Detail.asp?News_ID=313。 截止日期2011年10月20日下午16:00，学生可向铁生馆100号学生事务中心申请。悟宿奖助学金不能与其他专项奖学金、专项助学金兼得（政府之资助或津贴不在此列）。 在助学金评审工作中，若发现学生弄虚作假、欺骗等行为，将取消参选资格，情节严重者将给予处分。 附件一：上海交通大学助学金评审管理办法 附件二：申请书格式与内容说明 附件三：上海交通大学家庭经济困难学生认定申请表 访问数量：','2011-09-27 00:00','2011-09-27 00:00','','','学生办','2011-09-27 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-2011429750,'关于增加黑莓奖学金的通知和几件事项的补说明','一、各系和博士均增加黑莓奖学金，具体名额见excel“20110919本批次奖学金清单——按系别排列”中红色字体标出。欢迎同学们积极申报。 二、最近同学集中问的几个问题： （1）硕博连读的博一学生、硕士提前毕业攻博的博一学生，以及直博生如何参评？ 前两类学生以硕士身份参评，在填写各项表格时，填写现在博士班号和学号，但后面需括号附上硕士班号和学号。参评成果和工作均按照上一学年时间，不包括读博之后的工作。平均积点和成绩单如果无法显示，在申请表“平均积点”一栏填写“直播生或提前攻博生”等信息，我们会统一查询硕士期间平均积点。 后一类学生按照博士生参评 （2）奖学金申请表中的“平均积点”是网上显示的平均积点，申请表第二页成绩的自评分也采用此“平均积点”进行计算。 （3）奖学金申请表中社会工作一栏，如果在学校担任职务，需请相应指导教师签字确认，并打分（可将学院对社会工作的打分细则告知指导教师）。如果在学院担任干部职务，则自己填写职务名称和自评分即可。填写职务名称时注意详细，比如B0903393班生活委员等等。 （4）交材料时间节点是9月28日下午5点钟，过期不再接受任何材料。 访问数量：','2011-09-27 00:00','2011-09-27 00:00','','','学生办','2011-09-27 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1739547837,'2011年电院研究生专项奖学金、国家优秀奖学金评选通知','具体内容见附件， 说明：专项奖学金与学业奖学金不冲突，可以兼得。欢迎同学们积极申报。 后期可能还会有少量奖学金发布，但数量不会太多。请同学们慎重选择。 2011年电院研究生专项奖学金国家优秀奖学金评选通知 20110919本批次奖学金清单——按系别排列 电院研究生专项奖学金申请表 问题与解答汇总 110920 访问数量：','2011-09-22 00:00','2011-09-22 00:00','','','学生办','2011-09-22 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-835799659,'电院学生党支部工作职责','为了确保学生党支部工作的开展有章可循，充分发挥其在大学生思想政治教育方面的作用，体现电院学生党员的精神风貌，依据《中共上海交通大学学生支部委员会工作条例》等文件精神，结合我院实际，特制定本规定。 一、党支部工作职责 1、学习、宣传和执行党的路线方针政策和上级党组织的决议，把支部各项工作落到实处，带领和团结广大普通同学，共同进步。 2、加强对党员的日常教育、管理和监督，定期召开组织生活会和民主生活会，开展批评和自我批评。 3、严格按照《电院党员发展细则》要求，教育和培养入党积极分子，做好经常性的党员发展和转正工作。 4、关心和支持院学生会和研究生会、班委会等组织开展各种健康有益活动，充分发挥党员的先锋模范作用。 5、积极筹划学校党建创新奖的申报和答辩，做好支部评优工作，对违纪、违规的党员提出初步处理意见。 6、抓好支委会自身建设，积极探索支部思想政治教育工作新模式，大力开展主题社会实践活动，致力于支部凝聚力建设，打造属于本支部的特色文化。 7、积极完成学院党委和学生党总支布置的其他任务，加强与其他支部的交流与合作。 二、支委会工作职能 支委会成员由支部党员大会选举产生，包括支部书记、组织委员、宣传委员等，需报上一级党组织批准和组织部备案。本支部工作由支部书记牵头，协同所有支委成员共同组织实施，做到分工明确、密切配合。 （一）支部书记职责 在学生党总支的指导下，全面负责本支部开展各项具体工作： 1、召集和主持支部委员会、支部党员大会、支部组织生活会、民主生活会等，认真贯彻落实上级党组织的决议和指示。 2、结合本支部实际，每学期初，制定学期工作计划，并定期向支委会、支部大会和学生党总支汇报工作。学期末，进行工作总结，并上交书面材料。 3、按照《细则》要求，认真做好党员的教育和管理，积极分子的培养和发展，预备党员的转正等常规性工作。 4、以党章学习小组为载体，协助小组长开展特色学习活动，挖掘优秀分子，并充实到党员队伍中来。 5、做好青马党校各类培训班的报名等工作。 6、其他工作 （二）组织委员职责 1、拟定党员发展计划，组织落实支部主题生活。 2、了解本支部组织结构，探索支部建设新方案，做好党员基本信息的更新与维护，各类材料的管理与归档。 3、负责积极分子和预备党员的培养、教育和考察，及时了解和掌握党员的思想发展状况。 4、完善支部成员的考核制度，协助支部书记做好评优工作。 5、督促支部成员按时交纳党费，收齐后上缴党建','2011-09-21 00:00','2011-09-21 00:00','','','学生办','2011-09-21 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-584634102,'中国共产党章程','中国共产党章程 （中国共产党第十七次全国代表大会部分修改， 2007年10月21日通过） 总 纲 中国共产党是中国工人阶级的先锋队，同时是中国人民和中华民族的先锋队，是中国特色社会主义事业的领导核心，代表中国先进生产力的发展要求，代表中国先进文化的前进方向，代表中国最广大人民的根本利益。党的最高理想和最终目标是实现共产主义。 中国共产党以马克思列宁主义、毛泽东思想、邓小平理论和“三个代表”重要思想作为自己的行动指南。 马克思列宁主义揭示了人类社会历史发展的规律，它的基本原理是正确的，具有强大的生命力。中国共产党人追求的共产主义最高理想，只有在社会主义社会充分发展和高度发达的基础上才能实现。社会主义制度的发展和完善是一个长期的历史过程。坚持马克思列宁主义的基本原理，走中国人民自愿选择的适合中国国情的道路，中国的社会主义事业必将取得最终的胜利。 以毛泽东同志为主要代表的中国共产党人，把马克思列宁主义的基本原理同中国革命的具体实践结合起来，创立了毛泽东思想。毛泽东思想是马克思列宁主义在中国的运用和发展，是被实践证明了的关于中国革命和建设的正确的理论原则和经验总结，是中国共产党集体智慧的结晶。在毛泽东思想指引下，中国共产党领导全国各族人民，经过长期的反对帝国主义、封建主义、官僚资本主义的革命斗争，取得了新民主主义革命的胜利，建立了人民民主专政的中华人民共和国；建国以后，顺利地进行了社会主义改造，完成了从新民主主义到社会主义的过渡，确立了社会主义基本制度，发展了社会主义的经济、政治和文化。 十一届三中全会以来，以邓小平同志为主要代表的中国共产党人，总结建国以来正反两方面的经验，解放思想，实事求是，实现全党工作中心向经济建设的转移，实行改革开放，开辟了社会主义事业发展的新时期，逐步形成了建设中国特色社会主义的路线、方针、政策，阐明了在中国建设社会主义、巩固和发展社会主义的基本问题，创立了邓小平理论。邓小平理论是马克思列宁主义的基本原理同当代中国实践和时代特征相结合的产物，是毛泽东思想在新的历史条件下的继承和发展，是马克思主义在中国发展的新阶段，是当代中国的马克思主义，是中国共产党集体智慧的结晶，引导着我国社会主义现代化事业不断前进。 十三届四中全会以来，以江泽民同志为主要代表的中国共产党人，在建设中国特色社会主义的实践中，加深了对什么是社会主义、怎样建设社会主义和建设什么样的党、怎样建设党的认识，积累了治党治国新的宝贵经验','2011-09-21 00:00','2011-09-21 00:00','','','学生办','2011-09-21 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(1856016677,'110908 电院学生党建联席会招新宣讲会隆重举行','       9月8日，电院学生党建联席会招新宣讲会在电信群楼3-100隆重举行。参加此次宣讲会的嘉宾有电院学生党总支书记洪梅老师、电院学生党建联席会指导老师巢睿祺老师。        主持人徐鑫开场言语幽默诙谐，既结合了党建联席会的特点，也引起了全场的阵阵欢笑声。一段精心打造的“穿越”版的招新视频让新生们一饱眼福之后，主持人介绍了党建联席会的宗旨、特点、机构设置和品牌项目，新生们开始对党建联席会有了一个更深入的了解。接着院学生党总支书记洪梅老师上台为新生讲话，她语重心长地说，“一个人将来能否做大事、成大业，关键是看与你交往的人。党建联席会有很多优秀的人才，是一个值得你们加入的优秀的组织。”        在洪梅老师精彩的讲话之后，各部门分别开始了部门展示活动。组织部别出心裁的《功夫》版斧头帮舞蹈、考核部精心准备的改编歌曲、宣传部吸引眼球的广告串烧和发展部的精彩话剧博得了新生们的热烈掌声，也体现出联席会的创意十足。就在部门展示环节快要结束的时候，一幕感人的画面出现了：一位来自湖南衡阳的11级新生在两位同学的搀扶下来到了宣讲会现场，之前她的脚不慎骨折，还裹着厚厚的绷带。在主持人的邀请下，这位女生上台分享了她立志早日入党、想加入党建联席会的热忱的心情，台下顿时响起一片掌声。        最后进入了提问环节，一位位急于加入联席会的同学纷纷举手，联席会的主席、副主席们耐心地回答了他们的问题。宣讲会结束之后，新生们踊跃地提交了报名表。在新生们挤着提交报名表的时候，考虑周全的联席会特意安排了去年superE最佳台风奖得主薛午阳为大家奉上两首精彩的歌曲，新生们一边欣赏歌声一边排队提交报名表。        宣讲会在热烈的掌声中圆满结束，联席会成员们在台下辛勤准备所付出的汗水也得到了回报。在新的一个学期里，党建联席会将继续打造品牌项目、继续为广大电院学生服务。                                                                                                                                                           电院学生党建联席会宣传部 访问数量：','2011-09-21 00:00','2011-09-21 00:00','','','学生办','2011-09-21 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(-1944356661,'入党申请书的要求及写法','       按照党章规定，要求入党的人必须向党组织提交入党申请。入党申请一般采取书面形式，应由本人撰写，亲自递交给所在单位党组织。入党申请书是入党申请人向党组织表达自己入党愿望和决心的书面材料。   一、入党申请书的要求： 1．写入党申请书必须自愿，并且经过慎重的考虑，不能靠一时的感情冲动，不能有半点勉强。因为申请入党，就意味着要做好承担一个中共党员政治责任的准备，在党和人民利益需要的时候，能够义无反顾地牺牲自己的一切； 2．写入党申请书之前，要认真学习党章和有关党的基本知识，了解党的性质、宗旨、任务和党员的权利、义务等，增进对党的认识和感情，树立正确的入党动机，要紧密联系自己的思想实际谈自己对党的认识，不能空洞地抄写报纸和书刊上的内容，不谈自己的真实思想； 3．入党申请书既要结合新党章的特点，体现与时俱进，又要结合自己的思想实际，写得朴实庄重，不要追求华丽的词藻，夸夸其谈； 4．对党要忠诚老实，如实地向党组织反映自己的政治、历史、本人经历等有关情况，包括受过什么处分，或曾受过哪些奖励和表扬，不得有任何隐瞒和伪造； 5．入党申请书一般写一次即可，如果由于人事变动，造成材料遗失，需再次提交。 6．入党申请书要用较正规的横线纸或方格纸书写，而且要用黑色或蓝黑色的钢笔或毛笔，字迹要清晰、工整，以便存档，字数一般在5000字左右，不宜太少也不宜太多。   二、入党申请书的格式： 1．标题。“入党申请书”这几个字写在第一行的正中间，字体可以稍大，也可以和正文一样。 2．称呼。写在入党申请书的开始，一般写“敬爱的党支部（党组织）”，顶格书写在标题的下一行，后面加冒号。第一句话一般是“我申请加入中国共产党”。 3．正文。这是申请书的主要部分。 正文部分的主要内容包括： （1）入党的原因，主要写对党的认识，入党动机以及对待入党的态度。这部分应该表明自己的入党愿望。 （2）个人的基本情况，主要写自己的政治信念、成长经历，有无政治历史问题，受过何种奖励和处分，自己思想、工作、学习、作风等方面的情况（包括优缺点），必须如实写清楚。 （3）今后努力方向以及如何以实际行动争取早日加入党组织。 （4）家庭主要成员和主要社会关系情况。家庭主要成员一般写本人的父母（或抚养者）、配偶和子女，以及和本人长期在一起生活的人；主要社会关系主要写同本人联系密切或对本人影响较深的亲戚、朋友等。如果自己家庭成员和主要社会关系中，有人政治历史情况比较复杂，','2011-09-20 00:00','2011-09-20 00:00','','','学生办','2011-09-20 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(581320337,'上海交通大学本科生奖学金评选管理办法','第一章  总则 为了全面推进素质教育,调动广大学生刻苦学习、奋发向上的积极性，促进学生全面发展，根据《中华人民共和国教育法》和《中华人民共和国高等教育法》的有关规定，结合我校情况，特制定本管理办法。 本管理办法管理对象为学校所有在校本科生（留学生另行规定）。 第二章  奖学金类别 1、国家奖学金 国家奖学金是由国务院设立的（见《国务院关于建立健全普通本科高校、高等职业学校和中等职业学校家庭经济困难学生资助政策体系的意见》（国发<2007>13号）），用于激励普通本科高校、高等职业学校勤奋学习、努力进取，在德、智、体、美等方面得到全面发展的在校学生。国家奖学金是目前我校奖学金中奖金额度和规格最高的奖学金。 2、上海市奖学金 上海市奖学金是由上海市政府设立的（见《上海市人民政府关于建立健全普通本科高校、高等职业学校和中等职业学校家庭经济困难学生资助政策体系的实施意见》（沪府发〔2007〕35号）），为激励本市所有普通本科高校、高等职业学校勤奋学习、努力进取，在德、智、体、美等方面得到全面发展的在校学生。目前上海市奖学金的奖金额度与国家奖学金等同。 3、专项奖学金 专项奖学金是由企事业单位、个人及社会团体等与上海交通大学签订相关协议，捐助设立的。专项奖学金的奖励对象由捐助方指定，获奖要求也由捐助方规定，但一般要求学生学习勤奋、努力进取，在德、智、体、美等方面得到全面发展。专项奖学金的各个奖项奖金额度不等。 4、优秀奖学金 优秀奖学金是由上海交通大学设立的，旨在激励学习勤奋、努力进取，在德、智、体、美等方面得到全面发展的在校学生。共分A、B、C三个等级，优秀奖学金是目前我校奖学金中覆盖面最广、总奖金金额最高的奖学金。 5、新生奖学金 1、凡德智体美全面发展，高考总分（不含各类加分）名列该省市自治区第一名且被我校录取者，可获得人民币2万元新生优秀奖学金；名列第2-10名且被我校录取者，可获得人民币1万元新生优秀奖学金；名列第10-20名且被我校录取者，可获得人民币5000元新生优秀奖学金； 2、保送生符合下列条件者可以申请新生奖学金：A、凡在全国中学生学科奥林匹克数学、物理、化学、信息学、生物学五科竞赛中获得全国决赛一等奖且免试保送我校者，可获得人民币20000元奖学金；B、凡在全国中学生学科奥林匹克数学、物理学科竞赛中获得全国决赛二等奖且免试保送我校者，可获得人民币10000元奖学金。 第三章  奖学金经费来源及管理 ','2011-09-20 00:00','2011-09-20 00:00','','','学生办','2011-09-20 00:00:00.0');
insert into notification(notification_id, title, description, start_time, end_time, address, imageUrl, website,create_time) values(2120144118,'百年IBM上海交通大学校园周','百年IBM上海交通大学校园周 主办：上海交通大学电子信息与电气工程学院、软件学院、图书馆、IBM中国有限公司 协办：上海交通大学IBM学生俱乐部、学生国际交流协会、电子信息与电气工程学院研究生会 IBM是世界第一家百年信息科技企业，在今天，世界各地约有400，,000名IBM员工不断在提供硬件、软件好服务的创新与整合，帮助那些具有远见的企业、组织和个人，共建智慧的地球。 2011年9月19~23日，IBM走进中国著名学府——上海交通大学并举办校园周活动，旨在与中国下一代的领袖精英们分享IBM的百年遗产，立足全球、思考未来，共同开启下一个百年的智慧之旅。       以上讲座中带星号的需至电信群楼3号楼101C领取素拓票，领取时间从9月16日至9月23日 10:30~17:30，双休日休息。  0919-05-百年IBM校园周 访问数量：','2011-09-19 00:00','2011-09-19 00:00','','http://www.seiee.sjtu.edu.cn/content/fileUpload.action?method=downFileById&fileId=1151','学生办','2011-09-19 00:00:00.0');
